import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from 'recharts';

// NOTE: Removed 'import ./index.css' to resolve the compilation error. 
// Any necessary global styles will be placed in a style block or managed by Tailwind CSS utility classes.

// --- Assets and Helper Functions ---

// Dedicated high-resolution image for the full-screen background (A colorful, textured food scene)
const BACKGROUND_FOOD_IMAGE = 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=2670&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'; // Food background image (Salad bowl)

// Collection of diverse food image placeholders to make the UI look more creative and realistic.
const FOOD_IMAGE_URLS = [
    'https://placehold.co/400x300/228B22/ffffff?text=Grilled+Salmon',
    'https://placehold.co/400x300/F5DEB3/000000?text=Avocado+Toast',
    'https://placehold.co/400x300/800080/ffffff?text=Vegetable+Stir+Fry',
    'https://placehold.co/400x300/A0522D/ffffff?text=Chicken+Tikka',
    'https://placehold.co/400x300/ADD8E6/000000?text=Smoothie+Bowl',
    'https://placehold.co/400x300/D2B48C/000000?text=Lentil+Soup',
    'https://placehold.co/400x300/5F9EA0/ffffff?text=Taco+Salad',
    'https://placehold.co/400x300/8B4513/ffffff?text=Quinoa+Bowl',
    'https://placehold.co/400x300/F4A460/000000?text=Oatmeal+and+Berries',
    'https://placehold.co/400x300/9ACD32/000000?text=Healthy+Pizza',
];

// Simple function to get a random image URL for card placeholders
const getRandomFoodImage = () => {
    const randomIndex = Math.floor(Math.random() * FOOD_IMAGE_URLS.length);
    return FOOD_IMAGE_URLS[randomIndex];
};

// Component to render the nutritional donut chart
const NutritionalChart = ({ data, colors, isDarkMode }) => {
    const getTextColor = () => isDarkMode ? 'white' : 'black';

    const formattedData = Object.keys(data).map(key => ({
        name: key,
        value: data[key],
    }));

    if (!formattedData || formattedData.length === 0) {
        return <p className={`text-center ${getTextColor()}`}>No nutritional data available.</p>;
    }

    const renderCustomizedLabel = ({ cx, cy }) => {
        return (
            <text x={cx} y={cy} dy={-5} textAnchor="middle" fill={getTextColor()} className="text-xl font-bold">
                Calories
            </text>
        );
    };

    return (
        <div className="flex flex-col items-center">
            <h3 className={`text-xl font-semibold mb-4 ${isDarkMode ? 'text-purple-300' : 'text-indigo-500'}`}>
                Nutritional Values:
            </h3>
            <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                    <Pie
                        data={formattedData}
                        cx="50%"
                        cy="50%"
                        innerRadius={60}
                        outerRadius={100}
                        fill="#8884d8"
                        paddingAngle={5}
                        dataKey="value"
                        labelLine={false}
                        label={renderCustomizedLabel}
                    >
                        {formattedData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                        ))}
                    </Pie>
                    <Tooltip />
                </PieChart>
            </ResponsiveContainer>
        </div>
    );
};

// Main App component for the diet recommender application.
const App = () => {
