    const [age, setAge] = useState(2);
    const [height, setHeight] = useState(50);
    const [weight, setWeight] = useState(10);
    const [gender, setGender] = useState('male');
    const [activity, setActivity] = useState(1);
    const [mealsPerDay, setMealsPerDay] = useState(3);
    const [healthMetrics, setHealthMetrics] = useState(null);
    const [bmi, setBmi] = useState(null);

    // Existing user profile state
    const [userProfile, setUserProfile] = useState({
        name: 'Paula',
        email: 'paula@zylker.com',
        userId: '630083685',
        dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.',
        weeklyGoal: 'N/A'
    });

    const API_URL = 'http://localhost:5000';

    // Hardcoded nutritional data for the chart, based on the user's image.
    const nutritionalData = {
        'Calories': 1238,
        'FatContent': 20,
        'SaturatedFatContent': 5,
        'CholesterolContent': 100,
        'SodiumContent': 500,
        'CarbohydrateContent': 150,
        'FiberContent': 25,
        'SugarContent': 50,
        'ProteinContent': 80,
    };
    const chartColors = [
        '#667eea', 
        '#4FD1C5', 
        '#F6AD55', 
        '#E53E3E', 
        '#63B3ED', 
        '#81E6D9', 
        '#D53F8C', 
        '#ECC94B', 
        '#9F7AEA' 
    ];

    // Helper function to get the activity level label
    const getActivityLabel = (level) => {
      switch (level) {
        case 1:
          return 'Little/no exercise';
        case 2:
          return 'Lightly active';
        case 3:
          return 'Moderately active';
        case 4:
          return 'Very active';
        case 5:
          return 'Extra active';
        default:
          return 'Unknown';
      }
    };

    // Sync activePage with URL hash
    useEffect(() => {
        const hash = window.location.hash.replace('#', '').toLowerCase() || 'home';
        setActivePage(hash);
    }, []);

    // Handle navigation click and update URL hash
    const handleNavClick = (page) => {
        const formattedPage = page.toLowerCase().replace(' ', '-');
        window.location.hash = formattedPage;
        setActivePage(formattedPage);
    };

    // Handles the file input change event
    const handleImageChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setImage(event.target.files[0]);
            setText('');
            setRecipes([]);
            setSelectedRecipe(null);
            setErrorMessage('');
            setStatus('Image selected');
            setImageLoading({});
        }
    };

    // Main function to process the image and find recipes
    const processImage = async () => {
        setErrorMessage('');
        if (!image) {
            setErrorMessage('Please select an image first.');
            return;
        }

        setStatus('Uploading and processing...');
        setText('');
        setRecipes([]);
        setSelectedRecipe(null);
        setImageLoading({});

        try {
            // Step 1: Upload image for OCR (Simulated to avoid actual backend dependency for image URL filling)
            // In a real scenario, this would trigger the OCR on your server.
            console.log("Simulating OCR request...");

            const extractedText = `Ingredients:
                3 large tomatoes
                1 cucumber
                50g feta cheese
                1 red onion
                Olive oil
                Oregano
            `;
            setText(extractedText);

            // Step 2: Format text into a list of ingredients and simulate API call
            const ingredients = extractedText
                .toLowerCase()
                .split(/\s*[\r\n-]\s*/)
                .map(item => item.replace(/^\d+(\.\d+)?\s*(cups?|tbsp|tsp|oz|g|kg|lb)?\s*/i, '')) 
                .map(item => item.replace(/\b(and|or|with|to|taste)\b/g, '')) 
                .filter(item => item.trim() !== '' && item.length > 2);

            if (ingredients.length === 0) {
                setStatus('No valid ingredients found in the image.');
                return;
            }
            
            // Step 3: Simulate Recipe Lookup
            // Using dummy recipe data that includes our random image URLs
            setStatus('Searching local database for recipes...');

            const dummyRecipes = [
                { id: 101, title: 'Classic Greek Salad', image: getRandomFoodImage() },
                { id: 102, title: 'Tomato and Feta Pasta', image: getRandomFoodImage() },
                { id: 103, title: 'Mediterranean Cucumber Bites', image: getRandomFoodImage() },
                { id: 104, title: 'Simple Red Onion Tart', image: getRandomFoodImage() },
            ];
            
            // Simulate a short network delay
            await new Promise(resolve => setTimeout(resolve, 1500)); 

            setRecipes(dummyRecipes);
            setImageLoading(dummyRecipes.reduce((acc, recipe) => ({
                ...acc,
                [recipe.id]: true // Set initial loading state for all images
            }), {}));
            setStatus('Complete! Recipes Found.');

        } catch (error) {
            console.error('API Error:', error);
            setErrorMessage(error.message.includes('Failed to fetch')
                ? `Cannot connect to the backend server. Please ensure it is running at ${API_URL}.`
                : `Error: ${error.message}`);
            setStatus('An error occurred.');
            setRecipes([]);
            setImageLoading({});
        }
    };

    // Fetch detailed information for a specific recipe (Simulated)
    const fetchRecipeDetails = async (id, title) => {
        setStatus('Fetching recipe details...');
        setErrorMessage('');
        setImageLoading(prev => ({ ...prev, [id]: true }));
        try {
            // Simulate API fetching delay
            await new Promise(resolve => setTimeout(resolve, 500)); 

            const details = {
                id, 
                title,
                image: recipes.find(r => r.id === id)?.image || getRandomFoodImage(),
                nutrients: {
                    calories: '350 kcal',
                    protein: '15g',
                    carbohydrates: '40g',
                    fat: '12g',
                },
                ingredients: [
                    '2 large tomatoes, diced',
                    '1 cup chopped cucumber',
                    '1/4 cup crumbled feta',
                    '2 tbsp olive oil',
                    '1 tsp dried oregano',
                    'Salt and pepper to taste',
                ],
                instructions: `**Step 1:** Dice tomatoes, cucumber, and thinly slice red onion. **Step 2:** Combine all vegetables and feta cheese in a large bowl. **Step 3:** Whisk olive oil and oregano together in a small cup. **Step 4:** Pour dressing over the salad and toss gently. Serve immediately.`,
            };

            setSelectedRecipe(details);
            setStatus('Details loaded!');
        } catch (error) {
            console.error('Error fetching details:', error);
            setErrorMessage('Failed to load details. Please try again.');
            setStatus('Failed to load details.');
        } finally {
            setImageLoading(prev => ({ ...prev, [id]: false }));
        }
    };
    
    // Go back to the recipe list from the detailed view
    const goBackToList = () => {
        setSelectedRecipe(null);
        setStatus('Complete! Recipes Found.');
        setImageLoading({});
    };

    // Toggle dark/light mode
    const toggleDarkMode = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    const themeClasses = isDarkMode
        ? {
            // Removed bg-gray-950 here as it will be applied to the overlay div
            bg: 'bg-transparent', 
            text: 'text-white', 
