                                <p className={`text-sm ${themeClasses.listText}`}>{userProfile.email}</p>
                                <p className={`text-sm ${themeClasses.listText}`}>User ID: {userProfile.userId}</p>
                            </div>
                        </div>

                        {/* Health Profile Form Section */}
                        <div className={`w-full mx-auto rounded-3xl shadow-xl overflow-hidden p-8 space-y-6 ${themeClasses.infoBox}`}>
                            <div className="text-center">
                                <h2 className={`text-xl font-semibold text-gray-800 dark:text-white ${themeClasses.text}`}>Modify your health values</h2>
                            </div>

                            {/* Input Sections (Simplified for brevity) */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="age" className={`block font-medium ${themeClasses.text}`}>Age</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="age" value={age} onChange={(e) => setAge(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setAge(prevAge => prevAge > 0 ? prevAge - 1 : 0)}>-</button>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setAge(prevAge => prevAge + 1)}>+</button>
                                </div>
                            </div>
                            {/* ... (Other inputs like Height, Weight, Gender, Activity, etc. remain the same) */}

                            <div className="flex items-center justify-between">
                                <label htmlFor="height" className={`block font-medium ${themeClasses.text}`}>Height(cm)</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="height" value={height} onChange={(e) => setHeight(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setHeight(prevHeight => prevHeight > 0 ? prevHeight - 1 : 0)}>-</button>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setHeight(prevHeight => prevHeight + 1)}>+</button>
                                </div>
                            </div>

                            <div className="flex items-center justify-between">
                                <label htmlFor="weight" className={`block font-medium ${themeClasses.text}`}>Weight(kg)</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="weight" value={weight} onChange={(e) => setWeight(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setWeight(prevWeight => prevWeight > 0 ? prevWeight - 1 : 0)}>-</button>
                                    <button className={`rounded-full w-8 h-8 flex items-center justify-center ${themeClasses.button}`} onClick={() => setWeight(prevWeight => prevWeight + 1)}>+</button>
                                </div>
                            </div>

                            <div className="space-y-4 pt-4">
                                <h3 className={`block font-medium ${themeClasses.text}`}>Gender</h3>
                                <div className="flex items-center space-x-6">
                                    <label className="flex items-center space-x-2 cursor-pointer">
                                        <input type="radio" name="gender" value="male" checked={gender === 'male'} onChange={() => setGender('male')} className="form-radio text-blue-600" />
                                        <span className={`${themeClasses.text}`}>Male</span>
                                    </label>
                                    <label className="flex items-center space-x-2 cursor-pointer">
                                        <input type="radio" name="gender" value="female" checked={gender === 'female'} onChange={() => setGender('female')} className="form-radio text-blue-600"/>
                                        <span className={`${themeClasses.text}`}>Female</span>
                                    </label>
                                </div>
                            </div>

                            <div className="pt-4">
                                <h3 className={`block font-medium ${themeClasses.text}`}>Activity</h3>
                                <div className="relative mt-2">
                                    <input type="range" id="activity" min="1" max="5" value={activity} onChange={(e) => setActivity(parseInt(e.target.value))} className="w-full"/>
                                    <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-2">
                                        <span>Little/no exercise</span>
                                        <span>Extra active (very active & physical job)</span>
                                    </div>
                                    <p className={`text-center mt-2 text-sm ${themeClasses.text}`}>Current: {getActivityLabel(activity)}</p>
                                </div>
                            </div>

                            <div className="pt-4">
                                <label htmlFor="weight-plan" className={`block font-medium ${themeClasses.text}`}>Choose your weight loss plan:</label>
                                <select id="weight-plan" className={`w-full mt-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm ${themeClasses.text}`}>
                                    <option>Maintain weight</option>
                                    <option>Mild weight loss</option>
                                    <option>Regular weight loss</option>
                                    <option>Aggressive weight loss</option>
                                </select>
                            </div>

                            <div className="pt-4">
                                <label htmlFor="meals" className={`block font-medium ${themeClasses.text}`}>Meals per day</label>
                                <div className="relative mt-2">
                                    <input type="range" id="meals" min="1" max="6" value={mealsPerDay} onChange={(e) => setMealsPerDay(parseInt(e.target.value))} className="w-full"/>
                                    <p className={`text-center mt-2 text-sm ${themeClasses.text}`}>Current: {mealsPerDay} meals</p>
                                </div>
                            </div>

                            {/* Generate button section */}
                            <div className="flex justify-center pt-6">
                                <button onClick={calculateHealthMetrics} className={`bg-blue-600 text-white font-semibold py-3 px-8 rounded-full shadow-md hover:bg-blue-700 transition-colors duration-300`}>
                                    Generate
                                </button>
                            </div>

                            {/* Results display area (BMI and Calories) */}
                            {healthMetrics && (
                                <div className="pt-6 text-center text-gray-800 dark:text-white space-y-6">
                                    {healthMetrics.error ? (
                                        <p className="text-red-500 font-medium">{healthMetrics.error}</p>
                                    ) : (
                                        <>
                                            {/* BMI Section */}
                                            <div className="bg-gray-200 dark:bg-gray-600 rounded-lg p-4 shadow-inner">
                                                <h3 className="text-lg font-bold">BMI CALCULATOR</h3>
                                                <p className="text-2xl font-bold mt-2">{bmi} <span className="text-sm font-normal">kg/m²</span></p>
                                                <p className={`font-bold mt-1 ${bmi >= 30 ? 'text-red-500' : bmi >= 25 ? 'text-yellow-500' : bmi >= 18.5 ? 'text-green-500' : 'text-orange-500'}`}>
                                                    {bmi >= 30 ? 'Obesity' : bmi >= 25 ? 'Overweight' : bmi >= 18.5 ? 'Normal' : 'Underweight'}
                                                </p>
                                                <p className="text-xs text-gray-500 dark:text-gray-300 mt-1">Healthy BMI range: 18.5 kg/m² - 25 kg/m²</p>
                                            </div>

                                            {/* Calorie Calculator Section */}
                                            <div className="bg-gray-200 dark:bg-gray-600 rounded-lg p-4 shadow-inner">
                                                <h3 className="text-lg font-bold">CALORIES CALCULATOR</h3>
                                                <div className="grid grid-cols-2 gap-4 mt-4 text-center">
                                                    <div><p className="font-semibold text-sm">Maintain</p><p className="text-3xl font-bold mt-1 text-blue-400">{healthMetrics.maintainWeight}</p><p className="text-xs">kcal/day</p></div>
                                                    <div><p className="font-semibold text-sm">Mild Loss</p><p className="text-3xl font-bold mt-1 text-green-400">{healthMetrics.mildWeightLoss}</p><p className="text-xs">kcal/day</p></div>
                                                    <div><p className="font-semibold text-sm">Loss (0.5 kg/wk)</p><p className="text-3xl font-bold mt-1 text-green-500">{healthMetrics.weightLoss}</p><p className="text-xs">kcal/day</p></div>
                                                    <div><p className="font-semibold text-sm">Extreme Loss</p><p className="text-3xl font-bold mt-1 text-red-400">{healthMetrics.extremeWeightLoss}</p><p className="text-xs">kcal/day</p></div>
                                                </div>
                                            </div>
                                        </>
                                    )}
                                </div>
                            )}

                            {/* Chart Section */}
                            {healthMetrics && (
                                <div className={`w-full p-6 rounded-3xl shadow-lg ${themeClasses.infoBox}`}>
                                    <NutritionalChart data={nutritionalData} colors={chartColors} isDarkMode={isDarkMode} />
                                </div>
                            )}

                            {/* Existing user profile details (kept for context) */}
                            <div className={`w-full p-6 rounded-3xl shadow-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>My Weekly Goal</h3>
                                <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                                    {userProfile.weeklyGoal}
                                </p>
                            </div>
                        </div>
                    </div>
                );
            case 'weekend-summary':
                return <div className="text-center">Weekend Summary content goes here.</div>;
            default:
                return <div className="text-center">Page not found.</div>;
        }
    };

    return (
        <div
            // This is the main container for the entire app
            className={`min-h-screen font-sans flex flex-col items-center transition-colors duration-500 relative`}
            style={{
                backgroundImage: isDarkMode 
                    ? `linear-gradient(rgba(0, 0, 0, 0.85), rgba(0, 0, 0, 0.85)), url(${BACKGROUND_FOOD_IMAGE})` 
                    : `linear-gradient(rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.8)), url(${BACKGROUND_FOOD_IMAGE})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundAttachment: 'fixed', // Keeps image fixed during scroll
            }}
        >
            <div className={`w-full max-w-7xl flex flex-col items-center z-10 ${themeClasses.text} p-4 sm:p-8`}>
                <header className="w-full flex justify-between items-center mb-8">
                    <div className="flex items-center">
                        <svg
                            className="w-8 h-8 mr-2 text-purple-400" 
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5"
                            />
                        </svg>
                        <span className="text-2xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">PlanMyPlate</span>
                    </div>
                    <nav className="hidden sm:flex space-x-6"> {/* Hidden on mobile, shown on SM breakpoint */}
                        {['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => {
                            const formattedPage = page.toLowerCase().replace(' ', '-');
                            return (
                                <button
                                    key={page}
                                    onClick={() => handleNavClick(page)}
                                    className={`px-2 py-1 transition-all duration-300 font-semibold ${themeClasses.navText} ${
                                        activePage === formattedPage ? themeClasses.navActive : ''
                                    }`}
                                    aria-current={activePage === formattedPage ? 'page' : undefined}
                                    aria-label={`Maps to ${page}`}
                                >
                                    {page}
                                </button>
                            );
                        })}
                    </nav>
                    <div className="flex space-x-2 sm:space-x-4">
                        <button 
                            className={`p-2 rounded-full transition-colors duration-200 ${themeClasses.buttonPrimary} text-white`} 
                            aria-label="Search"
                        >
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"/></svg>
                        </button>
                        <button 
                            className={`p-2 rounded-full transition-colors duration-200 ${themeClasses.buttonPrimary} text-white`} 
                            aria-label="Profile"
                            onClick={() => handleNavClick('Profile')}
                        >
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"/></svg>
                        </button>
                        <button className={`p-2 rounded-full transition-colors duration-200 ${themeClasses.button} sm:hidden`} aria-label="Menu">
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"/></svg>
                        </button>
                    </div>
                </header>
                <main
                    // This container ensures the content stays centered and readable over the background.
                    className={`w-full p-4 sm:p-8 rounded-3xl shadow-2xl border transition-colors duration-500 mb-12 ${themeClasses.container}`}
                >
                    {renderPageContent()}
                </main>
            </div>
            <button
                onClick={toggleDarkMode}
                className={`fixed bottom-4 right-4 p-3 rounded-full font-semibold ${themeClasses.button} shadow-lg transition-colors duration-200 z-50`}
                aria-label={isDarkMode ? 'Switch to light mode' : '🌙 Dark Mode'}
            >
                {isDarkMode ? '🌞 Light Mode' : '🌙 Dark Mode'}
            </button>
        </div>
    );
};

export default App;
