import React, { useState, useEffect, useCallback } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from 'recharts';

// --- NutritionalChart Component (Omitted for brevity) ---
const NutritionalChart = ({ data, colors, isDarkMode }) => {
    const getTextColor = () => isDarkMode ? 'white' : 'black';
    const formattedData = Object.keys(data).filter(key => data[key] > 0).map(key => ({
        name: key.replace('Content', '').replace(/([A-Z])/g, ' $1').trim(),
        value: data[key],
    }));
    const totalCalories = data['Calories'] || 0;
    if (totalCalories === 0) return <p className={`text-center ${getTextColor()} p-4`}>No nutritional data logged today. Log a meal below!</p>;
    const renderCustomizedLabel = ({ cx, cy }) => (
        <text x={cx} y={cy} dy={5} textAnchor="middle" fill={getTextColor()} className="text-sm">
            <tspan x={cx} dy="-0.6em" className="text-xl font-bold">{totalCalories}</tspan>
            <tspan x={cx} dy="1.5em" className="text-sm font-light">LOGGED CALORIES</tspan>
        </text>
    );
    return (
        <div className="flex flex-col items-center">
            <h3 className={`text-xl font-semibold mb-4 ${isDarkMode ? 'text-purple-300' : 'text-indigo-500'} transition-colors duration-500`}>
                Daily Nutritional Summary:
            </h3>
            <div className="flex justify-center items-center w-full h-80">
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={formattedData} cx="50%" cy="50%" innerRadius={70} outerRadius={110} fill="#8884d8" paddingAngle={2} dataKey="value" labelLine={false} label={renderCustomizedLabel} cornerRadius={5}
                        >
                            {formattedData.map((entry, index) => (<Cell key={`cell-${index}`} fill={colors[index % colors.length]} />))}
                        </Pie>
                        <Tooltip
                             contentStyle={{ backgroundColor: isDarkMode ? '#1f2937' : 'white', borderColor: isDarkMode ? '#4b5563' : '#d1d5db', borderRadius: '8px', fontSize: '14px', color: isDarkMode ? 'white' : 'black', padding: '10px' }}
                             formatter={(value, name) => [`${value} ${name.toLowerCase().includes('fat') || name.toLowerCase().includes('protein') || name.toLowerCase().includes('carbohydr') ? 'g' : ''}`, name.replace('Content', '')]}
                        />
                    </PieChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

// --- Main App Component (Only relevant portions shown) ---

const App = () => {
    // ... (State variables, API_URL, chartColors, and helper functions omitted for brevity)

    const API_URL = 'http://localhost:5000'; // Define API_URL here

    // ... (All other state and logic definitions remain here)
    const [image, setImage] = useState(null);
    const [ingredientText, setIngredientText] = useState('');
    const [extractedText, setExtractedText] = useState('');
    const [status, setStatus] = useState('Idle');
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipe, setSelectedRecipe] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(true);
    const [activePage, setActivePage] = useState('Home');
    const [imageLoading, setImageLoading] = useState({});
    const [todayMeal, setTodayMeal] = useState(null);
    const [dailyMealLog, setDailyMealLog] = useState([]);
    const [dailyNutritionSummary, setDailyNutritionSummary] = useState({
        'Calories': 0, 'FatContent': 0, 'SaturatedFatContent': 0, 'CholesterolContent': 0,
        'SodiumContent': 0, 'CarbohydrateContent': 0, 'FiberContent': 0, 'SugarContent': 0, 'ProteinContent': 0,
    });
    const [age, setAge] = useState(30);
    const [height, setHeight] = useState(175);
    const [weight, setWeight] = useState(70);
    const [gender, setGender] = useState('male');
    const [activity, setActivity] = useState(3);
    const [mealsPerDay, setMealsPerDay] = useState(3);
    const [healthMetrics, setHealthMetrics] = useState(null);
    const [bmi, setBmi] = useState(null);
    const [userProfile, setUserProfile] = useState({
        name: 'Paula', email: 'paula@zylker.com', userId: '630083685',
        dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.', weeklyGoal: 'N/A'
    });
    const chartColors = [
        '#667eea', '#4FD1C5', '#F6AD55', '#E53E3E', '#63B3ED', '#81E6D9', '#D53F8C', '#ECC94B', '#9F7AEA'
    ];
    // ... (All other state and logic definitions remain here)

    // Log meal function (omitted for brevity)
    const logMealToDailyGoal = (mealData) => {
        // Determine if it's a recipe (from search, has a full nutrient object) or a dataset lookup
        const isRecipe = mealData.nutrients && mealData.nutrients.calories !== undefined;
        
        // Use full nutrient profile if it's a recipe or lookup, or minimal info if manual
        const mealNutrients = {
            'Calories': parseInt(mealData.nutrients?.calories || mealData.calories) || 0,
            'FatContent': parseFloat(mealData.nutrients?.fat || 0) || 0,
            'SaturatedFatContent': parseFloat(mealData.nutrients?.fat || 0) / 4 || 0, // Placeholder calculation
            'CholesterolContent': 0, 
            'SodiumContent': 0, 
            'CarbohydrateContent': parseFloat(mealData.nutrients?.carbohydrates || 0) || 0,
            'FiberContent': 0,
            'SugarContent': 0,
            'ProteinContent': parseFloat(mealData.nutrients?.protein || 0) || 0,
        };

        // Log the meal to the daily log
        setDailyMealLog(prevLog => [
            ...prevLog, 
            {
                name: mealData.title || mealData.name,
                calories: mealNutrients.Calories,
                type: isRecipe ? 'Recipe' : 'Dataset Lookup',
                timestamp: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
            }
        ]);

        // Update the cumulative nutritional summary
        setDailyNutritionSummary(prevSummary => ({
            'Calories': prevSummary.Calories + mealNutrients.Calories,
            'FatContent': prevSummary.FatContent + mealNutrients.FatContent,
            'SaturatedFatContent': prevSummary.SaturatedFatContent + mealNutrients.SaturatedFatContent,
            'CholesterolContent': prevSummary.CholesterolContent + mealNutrients.CholesterolContent,
            'SodiumContent': prevSummary.SodiumContent + mealNutrients.SodiumContent,
            'CarbohydrateContent': prevSummary.CarbohydrateContent + mealNutrients.CarbohydrateContent,
            'FiberContent': prevSummary.FiberContent + mealNutrients.FiberContent,
            'SugarContent': prevSummary.SugarContent + mealNutrients.SugarContent,
            'ProteinContent': prevSummary.ProteinContent + mealNutrients.ProteinContent,
        }));

        // setTodayMeal(null); // Keep todayMeal in case user wants to log another serving
        handleNavClick('Daily Goal'); // Navigate to the Daily Goal page
    };

    // Placeholder for handleLogSuggestedMeal (omitted for brevity)
    const handleLogSuggestedMeal = () => {
        if (todayMeal) {
            logMealToDailyGoal(todayMeal); 
        }
    };
    
    // Placeholder for other functions
    const toggleDarkMode = () => setIsDarkMode(prev => !prev);
    const calculateHealthMetrics = () => { /* existing logic */ };
    const goBackToList = () => { /* existing logic */ };
    const fetchRecipeDetails = async (id, title) => { /* existing logic */ };
    const handleImageChange = (event) => { /* existing logic */ };
    const handleTextChange = (event) => { /* existing logic */ };
    const processImage = async () => { /* existing logic */ };

    const themeClasses = {
        bg: 'bg-gray-900', text: 'text-white', container: 'bg-gray-800 border-gray-700 shadow-2xl',
        card: 'bg-gray-700', cardHover: 'hover:bg-gray-600', button: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
        buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700',
        subheading: 'text-purple-300', infoBox: 'bg-gray-700 border-gray-700',
        input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100', nutrients: 'bg-gray-600 text-gray-100',
        nutrientsSubtext: 'text-gray-400', listText: 'text-gray-300', navText: 'text-gray-300 hover:text-white',
        navActive: 'border-b-4 border-purple-400 font-extrabold', bgFilter: 'bg-black/80 backdrop-blur-sm'
    };

    // --- Daily Goal Manual Input Form Component (Now uses Lookup) ---
    const ManualMealLogger = ({ logMeal, themeClasses }) => {
        const [mealName, setMealName] = useState('');
        // Initialize fields as empty strings for form control
        const [calories, setCalories] = useState('');
        const [protein, setProtein] = useState('');
        const [carbohydrates, setCarbohydrates] = useState('');
        const [fat, setFat] = useState('');
        
        const [logError, setLogError] = useState('');
        const [isLookupLoading, setIsLookupLoading] = useState(false);
        const [lastLookupName, setLastLookupName] = useState('');

        const handleLookup = useCallback(async (name) => {
            if (!name) return;
            setLogError('');
            setIsLookupLoading(true);
            setLastLookupName(name);

            try {
                const response = await fetch(`${API_URL}/lookup_nutrition`, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ food_name: name }),
                });

                if (!response.ok) {
                    const errorJson = await response.json();
                    const errorMsg = errorJson.error || `Food item "${name}" not found in dataset.`;
                    setLogError(errorMsg);
                    // Clear fields if lookup fails to force manual correction
                    setCalories('');
                    setProtein('');
                    setCarbohydrates('');
                    setFat('');
                    return;
                }

                const data = await response.json();
                
                // Set all retrieved fields automatically
                setCalories(data.calories ? data.calories.toString() : '');
                setProtein(data.protein ? data.protein.toString() : '');
                setCarbohydrates(data.carbohydrates ? data.carbohydrates.toString() : '');
                setFat(data.fat ? data.fat.toString() : '');
                setLogError(''); 

            } catch (e) {
                setLogError('Network error or server issue during lookup.');
            } finally {
                setIsLookupLoading(false);
            }
        }, [API_URL]);

        // Effect to trigger lookup when mealName changes (debounced)
        useEffect(() => {
            if (mealName.trim() && mealName !== lastLookupName) {
                const timer = setTimeout(() => handleLookup(mealName.trim()), 500); // Debounce by 0.5s
                return () => clearTimeout(timer);
            }
        }, [mealName, handleLookup, lastLookupName]);


        const handleSubmit = (e) => {
            e.preventDefault();
            setLogError('');

            if (!mealName.trim()) {
                setLogError('Please enter a meal name.');
                return;
            }
            // MODIFIED VALIDATION: Check if at least Calories is present
            if (!calories) { 
                 setLogError('Calories are missing. Please ensure the food item is valid or enter the value.');
                 return;
            }

            // Log the meal using all retrieved/entered macro data
            logMeal({ 
                name: mealName.trim(), 
                // Pass the data in the same structure as a recipe
                nutrients: {
                    calories: parseFloat(calories || 0), // Use 0 if not entered manually/retrieved
                    protein: parseFloat(protein || 0),
                    carbohydrates: parseFloat(carbohydrates || 0),
                    fat: parseFloat(fat || 0),
                }
            });
            
            // Clear fields after successful log
            setMealName('');
            setCalories('');
            setProtein('');
            setCarbohydrates('');
            setFat('');
            setLastLookupName('');
            setStatus('Manual meal logged!');
        };

        return (
            <div className={`p-6 rounded-2xl shadow-xl ${themeClasses.infoBox} transition-all duration-500`}>
                <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Log Meal from Dataset</h3>
                <form onSubmit={handleSubmit} className="space-y-4">
                    
                    {/* Meal Name Input (Triggers Lookup) */}
                    <div className="relative">
                        <input
                            type="text"
                            value={mealName}
                            onChange={(e) => {
                                setMealName(e.target.value);
                                // Clear nutrient fields when name changes, forcing re-lookup/re-entry
                                setCalories(''); setProtein(''); setCarbohydrates(''); setFat('');
                            }}
                            placeholder="Type Food Item Name (e.g., Eggs, Banana, Oats)"
                            className={`w-full p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} focus:ring-purple-500 focus:border-purple-500 transition-colors duration-500`}
                        />
                         {isLookupLoading && (
                            <span className="absolute right-3 top-3">
                                <svg className="animate-spin h-5 w-5 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg>
                            </span>
                        )}
                    </div>

                    {/* Auto-filled/Manually Editable Nutrient Fields */}
                    {/* NOTE: Removed the 'required' attribute to allow submission even if macros are zero */}
                    <div className="grid grid-cols-2 gap-4">
                        <input type="number" value={calories} onChange={(e) => setCalories(e.target.value)} placeholder="Calories (kcal)" min="0" className={`p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} transition-colors duration-500`} />
                        <input type="number" value={protein} onChange={(e) => setProtein(e.target.value)} placeholder="Protein (g)" min="0" className={`p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} transition-colors duration-500`} />
                        <input type="number" value={carbohydrates} onChange={(e) => setCarbohydrates(e.target.value)} placeholder="Carbs (g)" min="0" className={`p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} transition-colors duration-500`} />
                        <input type="number" value={fat} onChange={(e) => setFat(e.target.value)} placeholder="Fat (g)" min="0" className={`p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} transition-colors duration-500`} />
                    </div>
                    
                    <button
                        type="submit"
                        disabled={isLookupLoading || !mealName.trim()} // Disable only if loading or no name is typed
                        className={`w-full px-4 py-2 rounded-full text-white font-bold bg-green-600 hover:bg-green-700 transition-transform duration-200 shadow-md disabled:opacity-50`}
                    >
                        Log Meal
                    </button>
                    {logError && <p className="text-sm text-red-400 text-center">{logError}</p>}
                    <p className={`text-xs text-center ${themeClasses.nutrientsSubtext}`}>*Values are automatically retrieved from the dataset when a matching food item is typed. You can correct them if needed.</p>
                </form>
            </div>
        );
    };

    // ... (The rest of the App component remains the same)
    
    const renderHomePageContent = () => { /* ... existing home content ... */ };
    const renderPageContent = () => {
        switch (activePage) {
            case 'Home':
                return renderHomePageContent();
            case 'Daily Goal':
                return (
                    <div className="w-full max-w-4xl mx-auto space-y-8">
                        <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox} transition-colors duration-500`}>
                            <NutritionalChart data={dailyNutritionSummary} colors={chartColors} isDarkMode={isDarkMode} />
                        </div>
                        <ManualMealLogger logMeal={logMealToDailyGoal} themeClasses={themeClasses} />
                        <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox} transition-colors duration-500`}>
                            <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Meals Logged Today ({dailyMealLog.length})</h3>
                            {dailyMealLog.length === 0 ? (
                                <p className={`text-center ${themeClasses.listText}`}>No meals tracked yet. Use the form above or log a recipe from the Home page!</p>
                            ) : (
                                <ul className="space-y-3">
                                    {dailyMealLog.map((meal, index) => (
                                        <li key={index} className={`flex justify-between items-center p-3 rounded-lg ${themeClasses.card}`}>
                                            <div className="flex items-center space-x-3">
                                                <span className={`px-2 py-1 text-xs font-bold rounded ${meal.type === 'Recipe' ? 'bg-purple-500 text-white' : 'bg-gray-400 text-gray-900'}`}>{meal.type}</span>
                                                <span className={`font-semibold ${themeClasses.text}`}>{meal.name}</span>
                                            </div>
                                            <div className="text-right">
                                                <span className={`text-lg font-extrabold ${themeClasses.subheading}`}>{meal.calories} kcal</span>
                                                <p className={`text-xs ${themeClasses.nutrientsSubtext}`}>{meal.timestamp}</p>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                );
            case 'Profile': 
                return (
                    <div className="text-center p-6">Profile Content</div>
                );
            case 'Weekend Summary': 
                return (
                    <div className="text-center p-6">Weekend Summary Content</div>
                );
            default:
                return <div className="text-center">Page not found.</div>;
        }
    };
    // ... (rest of App component code)
    return (
        <div className={`min-h-screen font-sans flex flex-col items-center p-4 sm:p-8 transition-all duration-500 ease-in-out`} style={{ backgroundImage: 'url("https://images.unsplash.com/photo-1543353071-10c84333b207?q=80&w=2670&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D")', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
            <div className={`absolute inset-0 z-0 ${themeClasses.bgFilter} transition-colors duration-500`}></div>
            <header className={`w-full max-w-7xl flex justify-between items-center mb-10 z-10 transition-colors duration-500 ${themeClasses.text}`}>
                {/* Header content */}
            </header>
            <main className={`w-full max-w-7xl min-h-[70vh] p-8 rounded-3xl z-10 border-t-4 border-b-4 border-purple-500 ${themeClasses.container} transition-all duration-500 ease-in-out`}>
                {renderPageContent()}
            </main>
            {/* Mobile Nav */}
        </div>
    );
};

export default App;
