import React, { useState } from "react";

const API_URL = "http://localhost:5000";

function App() {
  const [ingredients, setIngredients] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [status, setStatus] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [theme, setTheme] = useState("light");
  const [ocrText, setOcrText] = useState("");

  const themeClasses = {
    light: {
      background: "bg-gray-100 text-gray-800",
      card: "bg-white shadow-lg",
      nutrients: "bg-gray-200",
      nutrientsSubtext: "text-gray-600",
    },
    dark: {
      background: "bg-gray-900 text-white",
      card: "bg-gray-800 shadow-lg",
      nutrients: "bg-gray-700",
      nutrientsSubtext: "text-gray-300",
    },
  };

  const toggleTheme = () => setTheme(theme === "light" ? "dark" : "light");

  // OCR Upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setStatus("Processing image with OCR...");
    setErrorMessage("");
    setOcrText("");

    const formData = new FormData();
    formData.append("image", file);

    try {
      const response = await fetch(`${API_URL}/ocr`, {
        method: "POST",
        body: formData,
      });
      const result = await response.json();

      if (result.error) throw new Error(result.error);

      setOcrText(result.text);
      setIngredients(result.text);
      setStatus("OCR completed. Ingredients detected.");
    } catch (error) {
      setErrorMessage(`OCR failed: ${error.message}`);
      setStatus("OCR failed.");
    }
  };

  // Get recipes from Spoonacular
  const fetchRecipes = async () => {
    if (!ingredients) {
      setErrorMessage("Please enter ingredients first.");
      return;
    }

    setStatus("Searching recipes...");
    setErrorMessage("");
    setRecipes([]);
    setSelectedRecipe(null);

    try {
      const response = await fetch(`${API_URL}/get_recipes`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ingredients }),
      });
      const result = await response.json();

      if (result.error) throw new Error(result.error);

      setRecipes(result.recipes);
      setStatus("Recipes loaded.");
    } catch (error) {
      setErrorMessage(`Error fetching recipes: ${error.message}`);
      setStatus("Failed to load recipes.");
    }
  };

  // Get recipe details with full nutrients
  const fetchRecipeDetails = async (id) => {
    setStatus("Fetching recipe details...");
    setErrorMessage("");
    setSelectedRecipe(null);

    try {
      const response = await fetch(`${API_URL}/get_recipe_details/${id}`);
      const result = await response.json();

      if (result.error) throw new Error(result.error);

      setSelectedRecipe(result);
      setStatus(`Viewing: ${result.title}`);
    } catch (error) {
      setErrorMessage(`Error fetching details: ${error.message}`);
      setStatus("Failed to load recipe details.");
    }
  };

  return (
    <div
      className={`min-h-screen p-6 transition ${themeClasses[theme].background}`}
    >
      <div className="max-w-5xl mx-auto space-y-6">
        {/* Header */}
        <header className="flex justify-between items-center">
          <h1 className="text-2xl font-bold">🍴 Smart Recipe Finder</h1>
          <button
            onClick={toggleTheme}
            className="px-4 py-2 rounded-lg bg-indigo-500 text-white hover:bg-indigo-600"
          >
            {theme === "light" ? "🌙 Dark" : "☀️ Light"}
          </button>
        </header>

        {/* OCR Input */}
        <div className={`${themeClasses[theme].card} p-4 rounded-xl`}>
          <label className="block mb-2 font-semibold">
            Upload an image with ingredients:
          </label>
          <input type="file" onChange={handleImageUpload} />
          {ocrText && (
            <p className="mt-2 text-sm text-green-600">
              OCR detected: {ocrText}
            </p>
          )}
        </div>

        {/* Ingredients Text Input */}
        <div className={`${themeClasses[theme].card} p-4 rounded-xl`}>
          <label className="block mb-2 font-semibold">Enter Ingredients:</label>
          <input
            type="text"
            className="w-full border px-3 py-2 rounded-lg"
            value={ingredients}
            onChange={(e) => setIngredients(e.target.value)}
            placeholder="e.g., rice, tomato, milk"
          />
          <button
            onClick={fetchRecipes}
            className="mt-3 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
          >
            Find Recipes
          </button>
        </div>

        {/* Status & Error */}
        {status && <p className="text-blue-500">{status}</p>}
        {errorMessage && <p className="text-red-500">{errorMessage}</p>}

        {/* Recipes List */}
        <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
          {recipes.map((recipe) => (
            <div
              key={recipe.id}
              className={`${themeClasses[theme].card} rounded-xl overflow-hidden`}
            >
              <img
                src={recipe.image}
                alt={recipe.title}
                className="w-full h-40 object-cover"
              />
              <div className="p-4">
                <h3 className="font-bold text-lg">{recipe.title}</h3>
                <button
                  onClick={() => fetchRecipeDetails(recipe.id)}
                  className="mt-2 px-3 py-1 bg-indigo-500 text-white rounded-lg"
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Selected Recipe */}
        {selectedRecipe && (
          <div className={`${themeClasses[theme].card} p-6 rounded-xl`}>
            <h2 className="text-xl font-bold mb-2">{selectedRecipe.title}</h2>
            <img
              src={selectedRecipe.image}
              alt={selectedRecipe.title}
              className="w-full h-64 object-cover rounded-xl mb-4"
            />
            <h3 className="font-semibold mb-2">Ingredients:</h3>
            <ul className="list-disc list-inside mb-4">
              {selectedRecipe.ingredients.map((ing, idx) => (
                <li key={idx}>{ing}</li>
              ))}
            </ul>
            <h3 className="font-semibold mb-2">Instructions:</h3>
            <p
              className="mb-4"
              dangerouslySetInnerHTML={{
                __html: selectedRecipe.instructions,
              }}
            />
            <h3 className="font-semibold mb-2">Nutrition:</h3>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
              {Object.entries(selectedRecipe.nutrients).map(([name, val], idx) => (
                <div
                  key={idx}
                  className={`p-3 rounded-xl shadow-inner ${themeClasses[theme].nutrients}`}
                >
                  <span className="block text-xl font-bold">
                    {val.amount} {val.unit}
                  </span>
                  <span
                    className={`block text-xs ${themeClasses[theme].nutrientsSubtext}`}
                  >
                    {name}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
