import React from 'react';

const HomePage = () => { 
    return (
        <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center">
            <h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. üçΩÔ∏è</h2>
            <p className="text-lg text-gray-700 leading-relaxed mb-8">Unlock culinary possibilities with ingredients you already have.</p>
            <div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8">
                <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3><p className="text-gray-600">Upload an image of your ingredients and get instant recipe ideas.</p></div>
                <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3><p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p></div>
                <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3><p className="text-gray-600">Recipes categorized by meal type for easy browsing.</p></div>
            </div>
            <p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to the "Recipe Scanner"!</p>
        </div>
    ); 
};

export default HomePage;
