import React, { useState } from 'react';
import axios from 'axios';


// --- Navbar Component ---
const Navbar = ({ setCurrentPage }) => {
  return (
    <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-white text-3xl font-bold tracking-wide">
          <span className="text-green-200">Plan</span>MyPlate
        </h1>
        <ul className="flex space-x-6">
          <li>
            <button
              onClick={() => setCurrentPage('home')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Home
            </button>
          </li>
          <li>
            <button
              onClick={() => setCurrentPage('scanner')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Grocipes
            </button>
          </li>
          <li>
            <button
              onClick={() => setCurrentPage('profile')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Your Profile
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

// --- HomePage Component ---
const HomePage = () => {
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center">
      <h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. üçΩÔ∏è</h2>
      <p className="text-lg text-gray-700 leading-relaxed mb-8">
        Unlock culinary possibilities with ingredients you already have.
        Simply scan your ingredients, and let us suggest Healthy recipes tailored just for you.
      </p>
      <div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8">
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3>
          <p className="text-gray-600">Upload an image of your ingredients and get instant recipe ideas.</p>
        </div>
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3>
          <p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p>
        </div>
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3>
          <p className="text-gray-600">Recipes categorized by meal type for easy browsing.</p>
        </div>
      </div>
      <p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to the "Recipe Scanner"!</p>
    </div>
  );
};

// --- ProfilePage Component ---
const ProfilePage = () => {
  // You can use useState to hold the data from the constant above
  const [profileData] = useState(demoData);

  // This is a good way to check if the data loaded correctly
  if (!profileData) {
    return <div className="text-center p-8">Error: Profile data could not be loaded.</div>;
  }

  // Destructure the data for easier access in the JSX
  const { userProfile, dailyGoals, weightLog } = profileData;

  return (
    <div className="container mx-auto p-8">
      <div className="bg-white shadow-xl rounded-lg p-8">
        <div className="flex items-center space-x-6 mb-8">
          {/* A simple avatar using the user's first initial */}
          <div className="bg-green-500 text-white w-24 h-24 rounded-full flex items-center justify-center text-4xl font-bold">
            {userProfile.username.charAt(0).toUpperCase()}
          </div>
          <div>
            <h2 className="text-4xl font-extrabold text-green-700">{userProfile.username}</h2>
            <p className="text-gray-600">{userProfile.email}</p>
          </div>
        </div>

        {/* --- User Stats Section --- */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 text-center">
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Current Weight</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.currentWeight.value} {userProfile.currentWeight.unit}</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Goal Weight</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.goalWeight.value} {userProfile.goalWeight.unit}</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Height</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.height.value} {userProfile.height.unit}</p>
          </div>
        </div>

        {/* --- Daily Goals Section --- */}
        <div className="mb-8">
            <h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Daily Goals</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-gray-800">
                <div className="p-2"><strong>Calories:</strong> {dailyGoals.calories} kcal</div>
                <div className="p-2"><strong>Protein:</strong> {dailyGoals.protein}g</div>
                <div className="p-2"><strong>Carbs:</strong> {dailyGoals.carbohydrates}g</div>
                <div className="p-2"><strong>Fats:</strong> {dailyGoals.fats}g</div>
            </div>
        </div>

        {/* --- Weight Log Section --- */}
        <div>
            <h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Weight History</h3>
            <ul className="space-y-2">
                {weightLog.map((entry, index) => (
                    <li key={index} className="flex justify-between bg-gray-50 p-3 rounded-md">
                        <span className="text-gray-600">{new Date(entry.date).toLocaleDateString()}</span>
                        <span className="font-bold text-gray-800">{entry.weight} kg</span>
                    </li>
                ))}
            </ul>
        </div>
      </div>
    </div>
  );
};

// --- RecipeScanner Component (Contains all previous App logic) ---
const RecipeScanner = () => {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isFullRecipeLoading, setIsFullRecipeLoading] = useState(false);
  const [selectedRecipeDetails, setSelectedRecipeDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    console.log("File selected:", file ? file.name : "No file");
  };

  const handleUpload = async () => {
    if (!image) {
      alert('Please select an image first.');
      console.log("Upload cancelled: No image selected.");
      return;
    }

    setIsLoading(true);
    setText('');
    setRecipes([]);
    setSelectedRecipeDetails(null);
    setShowModal(false);

    const formData = new FormData();
    formData.append('image', image);
    console.log("FormData created, appending image:", image.name);

    try {
      console.log("Attempting to post to /extract...");
      const ocrResponse = await axios.post('http://localhost:5000/extract', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      console.log("Response from /extract:", ocrResponse.data);

      const extractedText = ocrResponse.data.text;
      setText(extractedText);
      await suggestRecipes(extractedText);

    } catch (error) {
      console.error('Error during OCR or recipe suggestion:', error);
      alert('Failed to process image or suggest recipes. Please check console for details.');
      setRecipes([]);
    } finally {
      setIsLoading(false);
    }
  };

  const suggestRecipes = async (extractedText) => {
    const ingredientsQuery = extractedText.toLowerCase()
      .split(/[\s,.\n-]+/)
      .filter(Boolean)
      .join(',');

    if (!ingredientsQuery) {
      setRecipes([]);
      return;
    }

    try {
      const apiResponse = await axios.get(`http://localhost:5000/api/recipes?ingredients=${ingredientsQuery}`);
      setRecipes(apiResponse.data);
      console.log("Recipes fetched from backend (Spoonacular):", apiResponse.data);

    } catch (error) {
      console.error("Error fetching recipes from backend:", error);
      alert(error.response?.data?.message || 'Could not fetch recipes. Check your backend server and API key.');
      setRecipes([]);
    }
  };

  const fetchFullRecipeDetails = async (recipeName) => {
    setIsFullRecipeLoading(true);
    setSelectedRecipeDetails(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/full-recipe-details?name=${encodeURIComponent(recipeName)}`);
      setSelectedRecipeDetails(response.data);
      setShowModal(true);
    } catch (error) {
      console.error("Error fetching full recipe details:", error);
      alert(error.response?.data?.message || 'Failed to fetch full recipe details. Please try again.');
    } finally {
      setIsFullRecipeLoading(false);
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedRecipeDetails(null);
  };

  const groupRecipesByMealType = (recipesList) => {
    const grouped = {
      breakfast: [],
      lunch: [],
      dinner: [],
      dessert: [],
      other: [],
    };

    recipesList.forEach(recipe => {
      const meal = recipe.meal ? recipe.meal.toLowerCase() : 'unknown';
      if (grouped[meal]) {
        grouped[meal].push(recipe);
      } else {
        grouped.other.push(recipe);
      }
    });
    return grouped;
  };

  const categorizedRecipes = groupRecipesByMealType(recipes);

  const renderRecipeList = (categoryName, recipesInCategory) => {
    if (recipesInCategory.length === 0) {
      return null;
    }
    return (
      <div key={categoryName} className="mb-8 p-4 bg-gray-50 rounded-lg shadow-md flex-grow-0 flex-shrink-0 w-full md:w-[calc(50%-1rem)] lg:w-[calc(33.33%-1rem)]"> {/* Responsive width */}
        <h4 className="text-xl font-semibold text-green-600 border-b-2 border-green-400 pb-2 mb-4">
          {categoryName.charAt(0).toUpperCase() + categoryName.slice(1)} Recipes
        </h4>
        <ul className="list-none p-0">
          {recipesInCategory.map((recipe, index) => (
            <li
              key={recipe.id || index}
              className="mb-3 bg-white p-4 rounded-lg shadow-sm border-l-4 border-green-500 flex items-center gap-4 cursor-pointer hover:shadow-md transition duration-200 ease-in-out"
              onClick={() => fetchFullRecipeDetails(recipe.name)}
            >
              {recipe.image && (
                <img
                  src={recipe.image}
                  alt={recipe.name}
                  className="w-20 h-20 rounded-lg object-cover flex-shrink-0"
                  onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/80x80/cccccc/000000?text=No+Img"; }}
                />
              )}
              <div className="flex-grow flex flex-col">
                <div className="flex justify-between items-center w-full">
                  <strong className="text-green-700 text-lg">{recipe.name}</strong>
                  {recipe.calories && recipe.calories !== 'N/A' && (
                    <span className="text-gray-600 text-sm font-medium">{recipe.calories}</span>
                  )}
                </div>
                <div className="flex flex-wrap gap-x-2 text-gray-500 text-sm mt-1">
                  {recipe.meal && recipe.meal !== 'Unknown' && (
                    <span>{recipe.meal}</span>
                  )}
                  {recipe.cuisine && recipe.cuisine !== 'Unknown' && (
                    <span>({recipe.cuisine})</span>
                  )}
                </div>
                {recipe.ingredients && recipe.ingredients.length > 0 && (
                  <span className="text-gray-500 text-xs mt-1">
                    (Used: {recipe.ingredients.join(', ')})
                  </span>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="container mx-auto p-6 bg-white shadow-xl rounded-lg mt-8">
      <h2 className="text-3xl font-bold text-center text-green-800 mb-6">Recipe Scanner üì∏</h2>
      
      <div className="flex flex-col md:flex-row items-center justify-center gap-4 mb-8 p-4 bg-green-50 rounded-lg shadow-inner">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-green-100 file:text-green-700 hover:file:bg-green-200 cursor-pointer"
        />
        <button
          onClick={handleUpload}
          disabled={isLoading || !image}
          className={`py-2 px-6 rounded-full text-white font-semibold transition duration-300 ease-in-out ${
            isLoading ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300'
          }`}
        >
          {isLoading ? 'Processing...' : 'Upload & Get Recipes'}
        </button>
      </div>

      {text && (
        <div className="mb-8">
          <h3 className="text-2xl font-semibold text-gray-700 mb-3">Extracted Ingredients:</h3>
          <pre className="bg-gray-100 p-6 rounded-lg border border-gray-200 whitespace-pre-wrap break-words text-gray-800 text-lg">
            {text}
          </pre>
        </div>
      )}

      {isLoading && <p className="text-center text-green-600 text-lg font-medium">Loading recipes...</p>}

      {recipes.length > 0 && (
        <div className="mt-8">
          <h3 className="text-2xl font-semibold text-gray-700 mb-6">Suggested Recipes ‚ú®</h3>
          
          <div className="flex flex-wrap justify-center gap-4">
            {renderRecipeList('breakfast', categorizedRecipes.breakfast)}
            {renderRecipeList('lunch', categorizedRecipes.lunch)}
            {renderRecipeList('dinner', categorizedRecipes.dinner)}
            {renderRecipeList('dessert', categorizedRecipes.dessert)}
            {renderRecipeList('other', categorizedRecipes.other)}
          </div>
        </div>
      )}

      {text && recipes.length === 0 && !isLoading && (
        <p className="mt-8 text-center text-gray-500 text-lg">No recipes found with the given ingredients. Try adding more or adjust your search.</p>
      )}

      {/* --- Full Recipe Details Modal --- */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 backdrop-blur-sm p-4">
          <div className="bg-white p-8 rounded-xl shadow-2xl max-w-xl w-full max-h-[90vh] overflow-y-auto relative">
            {isFullRecipeLoading ? (
              <p className="text-center text-green-600 text-lg">Loading full recipe details...</p>
            ) : selectedRecipeDetails ? (
              <>
                <h3 className="text-3xl font-bold text-green-800 mb-4">{selectedRecipeDetails.title}</h3>
                {selectedRecipeDetails.image && (
                  <img
                    src={selectedRecipeDetails.image}
                    alt={selectedRecipeDetails.title}
                    className="w-full h-auto rounded-lg mb-4 object-cover"
                    onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/400x300/cccccc/000000?text=No+Image"; }}
                  />
                )}
                <p className="text-gray-700 mb-2"><strong>Servings:</strong> {selectedRecipeDetails.servings || 'N/A'}</p>
                <p className="text-gray-700 mb-4"><strong>Ready in:</strong> {selectedRecipeDetails.readyInMinutes ? `${selectedRecipeDetails.readyInMinutes} minutes` : 'N/A'}</p>
                
                {selectedRecipeDetails.nutrition && selectedRecipeDetails.nutrition.nutrients && (
                  <div className="mt-4 pt-4 border-t border-gray-200">
                    <h4 className="text-xl font-semibold text-gray-700 mb-3">Nutritional Information (per serving):</h4>
                    <ul className="list-none p-0 grid grid-cols-1 sm:grid-cols-2 gap-2 text-gray-600">
                      {selectedRecipeDetails.nutrition.nutrients.map((nutrient, i) => {
                        if (['Calories', 'Carbohydrates', 'Sugar', 'Protein', 'Fat'].includes(nutrient.name)) {
                          return (
                            <li key={i} className="mb-1">
                              <strong>{nutrient.name}:</strong> {nutrient.amount.toFixed(1)}{nutrient.unit}
                            </li>
                          );
                        }
                        return null;
                      })}
                    </ul>
                  </div>
                )}

                <div className="flex flex-wrap gap-x-4 text-gray-600 text-md mt-4">
                    {selectedRecipeDetails.dishTypes && selectedRecipeDetails.dishTypes.length > 0 && (
                        <span><strong>Meal Type:</strong> {selectedRecipeDetails.dishTypes.map(type => type.charAt(0).toUpperCase() + type.slice(1)).join(', ')}</span>
                    )}
                    {selectedRecipeDetails.cuisines && selectedRecipeDetails.cuisines.length > 0 && (
                        <span><strong>Cuisine:</strong> {selectedRecipeDetails.cuisines.map(cuisine => cuisine.charAt(0).toUpperCase() + cuisine.slice(1)).join(', ')}</span>
                    )}
                </div>

                <h4 className="text-xl font-semibold text-gray-700 mt-6 mb-3">Ingredients:</h4>
                <ul className="list-disc pl-6 text-gray-700">
                  {selectedRecipeDetails.extendedIngredients && selectedRecipeDetails.extendedIngredients.map((ing, i) => (
                    <li key={i} className="mb-1">{ing.original}</li>
                  ))}
                  {(!selectedRecipeDetails.extendedIngredients || selectedRecipeDetails.extendedIngredients.length === 0) && (
                    <li>No detailed ingredients available.</li>
                  )}
                </ul>
                
                <h4 className="text-xl font-semibold text-gray-700 mt-6 mb-3">Instructions:</h4>
                {selectedRecipeDetails.instructions ? (
                  <div className="prose max-w-none text-gray-700" dangerouslySetInnerHTML={{ __html: selectedRecipeDetails.instructions }} />
                ) : (
                  <p className="text-gray-600">No detailed instructions available.</p>
                )}
              </>
            ) : (
              <p className="text-center text-red-500 text-lg">Failed to load recipe details.</p>
            )}
            
            <button
              onClick={closeModal}
              className="mt-6 py-2 px-6 bg-red-600 text-white rounded-full font-semibold hover:bg-red-700 transition duration-300 ease-in-out self-end focus:outline-none focus:ring-2 focus:ring-red-300"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// --- Main App Component ---
function App() {
  const [currentPage, setCurrentPage] = useState('home'); // Default to 'home' page

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'scanner':
        return <RecipeScanner />;
      case 'profile':
        return <ProfilePage />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100"> {/* Set a min-height and background for the whole app */}
      <Navbar setCurrentPage={setCurrentPage} />
      <main className="py-8"> {/* Padding for content below navbar */}
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
