import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ onLoginSuccess, switchToSignup }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
 
    const handleLogin = async () => {
      try {
        const response = await axios.post('http://localhost:5000/api/login', { email, password });
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        alert(response.data.message);
        onLoginSuccess();
      } catch (error) {
        alert(error.response?.data?.message || 'Login failed.');
      }
    };
 
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md">
                <h2 className="text-3xl font-bold mb-6 text-center text-green-700">Login</h2>
                <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-4 p-3 w-full border rounded-lg" />
                <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-6 p-3 w-full border rounded-lg" />
                <button onClick={handleLogin} className="bg-green-600 text-white py-3 px-4 rounded-lg w-full font-semibold hover:bg-green-700 transition">Login</button>
                <p className="mt-6 text-center">
                  Don't have an account? <button onClick={switchToSignup} className="text-blue-500 hover:underline">Sign Up here</button>
                </p>
            </div>
        </div>
    );
};

export default Login;
