import React, { useState } from 'react';
import './App.css'; 

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [status, setStatus] = useState('Idle');
  const [recipes, setRecipes] = useState([]);

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setImage(event.target.files[0]);
      setText('');
      setRecipes([]);
      setStatus('Image selected');
    }
  };

  const processImage = async () => {
    if (!image) {
      alert('Please select an image first.');
      return;
    }

    setStatus('Uploading and processing...');
    setText('');
    setRecipes([]);

    try {
      // Step 1: Upload image for OCR
      const formData = new FormData();
      formData.append('image', image);
      const ocrResponse = await fetch('http://localhost:5000/ocr', {
        method: 'POST',
        body: formData,
      });

      if (!ocrResponse.ok) {
        throw new Error(`OCR failed with status: ${ocrResponse.status}`);
      }
      const ocrData = await ocrResponse.json();
      const extractedText = ocrData.text;
      setText(extractedText);
      
      // Step 2: Format text into a list of ingredients
      const ingredients = extractedText.toLowerCase().split(/\s*[\r\n-]\s*/).filter(item => item.trim() !== '');

      // Step 3: Send ingredients to get recipes from Spoonacular via your backend
      setStatus('Fetching recipes from Spoonacular...');
      const recipesResponse = await fetch('http://localhost:5000/get_spoonacular_recipes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients }),
      });

      if (!recipesResponse.ok) {
        throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}`);
      }
      const recipesData = await recipesResponse.json();
      setRecipes(recipesData.recipes);
      setStatus('Complete!');

    } catch (error) {
      console.error('API Error:', error);
      setStatus('An error occurred.');
      setRecipes([]);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>OCR & Recipe Suggester 📜</h1>
        <div className="input-section">
          <input type="file" onChange={handleImageChange} accept="image/*" />
          <button onClick={processImage} disabled={status.includes('processing')}>
            Upload and Extract Text
          </button>
        </div>
        <p><strong>Status:</strong> {status}</p>
        
        {text && (
          <div className="extracted-ingredients-box">
            <h3>Extracted Ingredients:</h3>
            <pre>{text}</pre>
          </div>
        )}

        {recipes.length > 0 && (
          <div className="recipes-container">
            <h3>Suggested Recipes 👇</h3>
            <div className="recipe-grid">
              {recipes.map((recipe) => (
                <div key={recipe.id} className="recipe-card">
                  <h4>{recipe.title}</h4>
                  <img src={recipe.image} alt={recipe.title} className="recipe-image" />
                  <p>Used Ingredients: {recipe.usedIngredientCount}</p>
                  <p>Missed Ingredients: {recipe.missedIngredientCount}</p>
                </div>
              ))}
            </div>
          </div>
        )}
        {status === 'Complete!' && recipes.length === 0 && (
          <p>No recipes found. Try a different image or more common ingredients!</p>
        )}
      </header>
    </div>
  );
}

export default App;
