import React, { useState } from 'react';
import axios from 'axios';
import CameraCapture from './CameraCapture';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [preview, setPreview] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleImageCapture = (imageBlob) => {
    setSelectedFile(imageBlob);
    setPreview(URL.createObjectURL(imageBlob));
  };

  const handleExtract = async () => {
    if (!selectedFile) return alert('Please select or capture an image first');

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/extract-text', formData);
      setExtractedText(response.data.text);
    } catch (error) {
      alert('Error extracting text');
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>OCR App</h1>

      <h3>üì∏ Capture Image</h3>
      <CameraCapture onCapture={handleImageCapture} />

      <h3>üìÅ Or Upload Image</h3>
      <input type="file" accept="image/*" onChange={handleFileChange} />

      {preview && <img src={preview} alt="Preview" width="250" style={{ margin: '20px auto' }} />}

      <br />
      <button onClick={handleExtract}>Extract Text</button>

      {extractedText && (
        <div>
          <h3>üìÑ Extracted Text:</h3>
          <pre>{extractedText}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
