import React, { useState, useRef, useCallback } from 'react';
import axios from 'axios';
import Webcam from 'react-webcam'; // Import the new library

// --- Navbar Component (No Changes) ---
const Navbar = ({ setCurrentPage }) => {
  return (
    <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-white text-3xl font-bold tracking-wide">
          <span className="text-green-200">Plan</span>MyPlate
        </h1>
        <ul className="flex space-x-6">
          <li>
            <button
              onClick={() => setCurrentPage('home')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Home
            </button>
          </li>
          <li>
            <button
              onClick={() => setCurrentPage('scanner')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Grocipes
            </button>
          </li>
          <li>
            <button
              onClick={() => setCurrentPage('profile')}
              className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300"
            >
              Your Profile
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

// --- HomePage Component (No Changes) ---
const HomePage = () => {
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center">
      <h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. 🍽️</h2>
      <p className="text-lg text-gray-700 leading-relaxed mb-8">
        Take a picture of your ingredients, and let us suggest healthy recipes tailored just for you.
      </p>
      {/* Cards... */}
       <div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8">
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3>
          <p className="text-gray-600">Use your camera or upload an image to get instant recipe ideas.</p>
        </div>
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3>
          <p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p>
        </div>
        <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm">
          <h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3>
          <p className="text-gray-600">Recipes categorized by meal type for easy Browse.</p>
        </div>
      </div>
      <p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to "Grocipes"!</p>
    </div>
  );
};

// --- ProfilePage Component (No Changes) ---
const ProfilePage = ({ demoData }) => {
  const [profileData] = useState(demoData);
  if (!profileData) {
    return <div className="text-center p-8">Error: Profile data could not be loaded.</div>;
  }
  const { userProfile, dailyGoals, weightLog } = profileData;
  return (
    <div className="container mx-auto p-8">
      <div className="bg-white shadow-xl rounded-lg p-8">
        <div className="flex items-center space-x-6 mb-8">
          <div className="bg-green-500 text-white w-24 h-24 rounded-full flex items-center justify-center text-4xl font-bold">
            {userProfile.username.charAt(0).toUpperCase()}
          </div>
          <div>
            <h2 className="text-4xl font-extrabold text-green-700">{userProfile.username}</h2>
            <p className="text-gray-600">{userProfile.email}</p>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 text-center">
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Current Weight</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.currentWeight.value} {userProfile.currentWeight.unit}</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Goal Weight</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.goalWeight.value} {userProfile.goalWeight.unit}</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Height</p>
            <p className="text-2xl font-bold text-green-800">{userProfile.height.value} {userProfile.height.unit}</p>
          </div>
        </div>
        <div className="mb-8">
            <h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Daily Goals</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-gray-800">
                <div className="p-2"><strong>Calories:</strong> {dailyGoals.calories} kcal</div>
                <div className="p-2"><strong>Protein:</strong> {dailyGoals.protein}g</div>
                <div className="p-2"><strong>Carbs:</strong> {dailyGoals.carbohydrates}g</div>
                <div className="p-2"><strong>Fats:</strong> {dailyGoals.fats}g</div>
            </div>
        </div>
        <div>
            <h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Weight History</h3>
            <ul className="space-y-2">
                {weightLog.map((entry, index) => (
                    <li key={index} className="flex justify-between bg-gray-50 p-3 rounded-md">
                        <span className="text-gray-600">{new Date(entry.date).toLocaleDateString()}</span>
                        <span className="font-bold text-gray-800">{entry.weight} kg</span>
                    </li>
                ))}
            </ul>
        </div>
      </div>
    </div>
  );
};


// --- RecipeScanner Component (UPDATED WITH CAMERA) ---
const RecipeScanner = () => {
  const [imageFile, setImageFile] = useState(null); // This will hold the File object
  const [previewUrl, setPreviewUrl] = useState(''); // For displaying the image preview
  const [cameraMode, setCameraMode] = useState(false); // Toggle for camera view
  
  const [text, setText] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedRecipeDetails, setSelectedRecipeDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isFullRecipeLoading, setIsFullRecipeLoading] = useState(false);

  const webcamRef = useRef(null);

  // --- Converts base64 image from webcam to a File object ---
  const base64ToFile = (base64, filename) => {
    const arr = base64.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
  };
  
  // --- Capture photo from webcam ---
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (imageSrc) {
      const file = base64ToFile(imageSrc, 'capture.jpg');
      setImageFile(file);
      setPreviewUrl(imageSrc);
      setCameraMode(false); // Exit camera mode after taking picture
    }
  }, [webcamRef]);

  // --- Handle file selected from input ---
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!imageFile) {
      alert('Please select an image first.');
      return;
    }
    setIsLoading(true);
    setText('');
    setRecipes([]);

    const formData = new FormData();
    formData.append('image', imageFile);

    try {
      // Your existing OCR and recipe suggestion logic
      const ocrResponse = await axios.post('http://localhost:5000/extract', formData);
      const extractedText = ocrResponse.data.text;
      setText(extractedText);
      
      const apiResponse = await axios.get(`http://localhost:5000/api/food-search`, {
        params: { ingredients: extractedText }
      });
      setRecipes(apiResponse.data);

    } catch (error) {
      console.error('Error processing image:', error);
      alert(error.response?.data?.message || 'Failed to process image. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };
  
  // --- Functions for fetching details and managing modal (no changes) ---
  const fetchFullRecipeDetails = async (fdcId) => {
    setIsFullRecipeLoading(true);
    setShowModal(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/food-details/${fdcId}`);
      setSelectedRecipeDetails(response.data);
    } catch (error) {
      console.error("Error fetching full details:", error);
      alert(error.response?.data?.message || 'Failed to fetch details.');
      setShowModal(false);
    } finally {
      setIsFullRecipeLoading(false);
    }
  };
  
  const closeModal = () => {
    setShowModal(false);
    setSelectedRecipeDetails(null);
  };
  
  // --- UI Rendering ---
  return (
    <div className="container mx-auto p-6 bg-white shadow-xl rounded-lg mt-8">
      <h2 className="text-3xl font-bold text-center text-green-800 mb-6">Ingredient Scanner 📸</h2>
      
      {/* --- Camera / File Input Section --- */}
      <div className="p-4 bg-green-50 rounded-lg shadow-inner">
        {cameraMode ? (
          <div className="flex flex-col items-center gap-4">
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              className="w-full max-w-lg rounded-lg"
            />
            <button onClick={capture} className="py-2 px-6 bg-blue-600 text-white rounded-full font-semibold">Take Photo</button>
          </div>
        ) : (
          <div className="flex flex-col items-center gap-4">
            <div className="flex gap-4">
              <button onClick={() => setCameraMode(true)} className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold">Open Camera</button>
              <label className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold cursor-pointer">
                <span>Upload File</span>
                <input type="file" accept="image/*" onChange={handleFileChange} className="hidden" />
              </label>
            </div>
            {previewUrl && (
              <div className="mt-4 text-center">
                <h4 className="font-semibold text-lg">Preview:</h4>
                <img src={previewUrl} alt="Selected preview" className="max-w-xs mx-auto rounded-lg mt-2"/>
              </div>
            )}
          </div>
        )}
      </div>

      {/* --- Upload Button --- */}
      <div className="text-center my-6">
        <button
          onClick={handleUpload}
          disabled={isLoading || !imageFile}
          className={`py-2 px-8 rounded-full text-white font-semibold transition duration-300 ${
            isLoading || !imageFile ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'
          }`}
        >
          {isLoading ? 'Analyzing...' : 'Analyze Ingredients'}
        </button>
      </div>

      {/* --- Results Section --- */}
      {text && (
        <div className="mb-8">
          <h3 className="text-2xl font-semibold text-gray-700 mb-3">Detected Text:</h3>
          <pre className="bg-gray-100 p-4 rounded-lg whitespace-pre-wrap">{text}</pre>
        </div>
      )}

      {recipes.length > 0 && (
        <div className="mt-8">
          <h3 className="text-2xl font-semibold text-gray-700 mb-6 text-center">Nutritional Information ✨</h3>
          <ul className="space-y-3">
            {recipes.map(item => (
              <li key={item.fdcId} onClick={() => fetchFullRecipeDetails(item.fdcId)} className="bg-white p-4 rounded-lg shadow-sm border-l-4 border-green-500 cursor-pointer hover:shadow-md">
                <p className="font-bold text-green-800">{item.name}</p>
                <p className="text-sm text-gray-600">{item.brand}</p>
                <p className="text-sm text-gray-500 mt-1">Calories: {item.calories}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
      
      {/* --- Full Details Modal (for USDA data) --- */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
          <div className="bg-white p-6 rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            {isFullRecipeLoading ? (
              <p>Loading details...</p>
            ) : selectedRecipeDetails ? (
              <>
                <h3 className="text-2xl font-bold text-green-800 mb-2">{selectedRecipeDetails.description}</h3>
                <p className="text-gray-500 mb-4">{selectedRecipeDetails.brandOwner || 'Generic'}</p>
                <div className="border-t pt-4">
                  <h4 className="text-xl font-semibold mb-3">Nutrient Breakdown (per 100g)</h4>
                  <ul className="space-y-1 text-gray-700 max-h-96 overflow-y-auto">
                    {selectedRecipeDetails.foodNutrients.map(n => (
                      <li key={n.nutrient.id} className="flex justify-between">
                        <span>{n.nutrient.name}</span>
                        <span className="font-medium">{n.amount.toFixed(2)} {n.nutrient.unitName.toLowerCase()}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                <button onClick={closeModal} className="mt-6 py-2 px-6 bg-red-600 text-white rounded-full font-semibold">Close</button>
              </>
            ) : <p>Details could not be loaded.</p>}
          </div>
        </div>
      )}
    </div>
  );
};

// --- Demo Data for Profile Page ---
const demoData = {
    userProfile: {
        username: 'AlexDoe',
        email: 'alex.doe@example.com',
        currentWeight: { value: 75, unit: 'kg' },
        goalWeight: { value: 70, unit: 'kg' },
        height: { value: 180, unit: 'cm' },
    },
    dailyGoals: {
        calories: 2200,
        protein: 150,
        carbohydrates: 250,
        fats: 70,
    },
    weightLog: [
        { date: '2025-07-29', weight: 76 },
        { date: '2025-08-05', weight: 75 },
    ],
};

// --- Main App Component ---
function App() {
  const [currentPage, setCurrentPage] = useState('home');

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'scanner':
        return <RecipeScanner />;
      case 'profile':
        return <ProfilePage demoData={demoData} />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar setCurrentPage={setCurrentPage} />
      <main className="py-8">
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
