import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import Webcam from 'react-webcam';

// --- Navbar Component ---
const Navbar = ({ setCurrentPage, isLoggedIn, onLogout }) => {
  return (
    <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-white text-3xl font-bold tracking-wide">
          <span className="text-green-200">Plan</span>MyPlate
        </h1>
        <ul className="flex space-x-6">
          <li><button onClick={() => setCurrentPage('home')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Home</button></li>
          {isLoggedIn && (
            <>
              <li><button onClick={() => setCurrentPage('scanner')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Grocipes</button></li>
              <li><button onClick={() => setCurrentPage('profile')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Your Profile</button></li>
              <li><button onClick={onLogout} className="bg-red-500 text-white hover:bg-red-600 transition duration-300 ease-in-out text-lg font-medium py-2 px-4 rounded-full">Logout</button></li>
            </>
          )}
          {!isLoggedIn && (
            <li><button onClick={() => setCurrentPage('login')} className="bg-green-500 text-white hover:bg-green-600 transition duration-300 ease-in-out text-lg font-medium py-2 px-4 rounded-full">Login</button></li>
          )}
        </ul>
      </div>
    </nav>
  );
};

// --- Login & Signup Forms ---
const AuthForm = ({ onLoginSuccess, switchToSignup, isLoginMode }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleAuth = () => {
    if (isLoginMode) {
      const storedUser = JSON.parse(localStorage.getItem('userData'));
      if (storedUser && storedUser.email === email && storedUser.password === password) {
        onLoginSuccess(email);
        alert('Logged in successfully!');
      } else {
        alert('Invalid credentials. Please sign up first.');
      }
    } else {
      const newUser = { email, password, profile: null, questionnaire: null };
      localStorage.setItem('userData', JSON.stringify(newUser));
      alert('Signed up successfully! You can now log in.');
      switchToSignup(true); // Switch to login mode after successful signup
    }
  };

  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">{isLoginMode ? 'Login' : 'Sign Up'}</h2>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={handleAuth} className="bg-green-600 text-white py-2 px-4 rounded w-full">{isLoginMode ? 'Login' : 'Sign Up'}</button>
      <p className="mt-4 text-center">
        {isLoginMode ? "Don't have an account?" : "Already have an account?"}
        <button onClick={() => switchToSignup(!isLoginMode)} className="text-blue-500 hover:underline">{isLoginMode ? 'Sign Up here' : 'Login here'}</button>
      </p>
    </div>
  );
};

// --- ProfileForm Component ---
const ProfileForm = ({ onSubmit }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Create Profile</h2>
      <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Age" value={age} onChange={e => setAge(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Weight (kg)" value={weight} onChange={e => setWeight(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Height (cm)" value={height} onChange={e => setHeight(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onSubmit({ name, age, weight, height })} className="bg-green-600 text-white py-2 px-4 rounded w-full">Save Profile</button>
    </div>
  );
};

// --- Questionnaire Component ---
const Questionnaire = ({ onSubmit }) => {
  const [problems, setProblems] = useState([]);
  const [needs, setNeeds] = useState([]);
  const problemOptions = ['Diabetes', 'Hypertension', 'Obesity', 'None'];
  const needOptions = ['Weight Loss', 'Healthy Recipes', 'Track Nutrition', 'Other'];
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Tell us about you</h2>
      <div className="mb-4">
        <p className="font-semibold mb-2">What health problems do you have?</p>
        {problemOptions.map(opt => (
          <label key={opt} className="block mb-1">
            <input type="checkbox" checked={problems.includes(opt)} onChange={() => setProblems(problems.includes(opt) ? problems.filter(p => p !== opt) : [...problems, opt])} />
            <span className="ml-2">{opt}</span>
          </label>
        ))}
      </div>
      <div className="mb-4">
        <p className="font-semibold mb-2">What do you want from this app?</p>
        {needOptions.map(opt => (
          <label key={opt} className="block mb-1">
            <input type="checkbox" checked={needs.includes(opt)} onChange={() => setNeeds(needs.includes(opt) ? needs.filter(n => n !== opt) : [...needs, opt])} />
            <span className="ml-2">{opt}</span>
          </label>
        ))}
      </div>
      <button onClick={() => onSubmit({ problems, needs })} className="bg-green-600 text-white py-2 px-4 rounded w-full">Continue</button>
    </div>
  );
};

// --- Homepage Component ---
const HomePage = () => (
  <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center">
    <h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. 🍽️</h2>
    <p className="text-lg text-gray-700 leading-relaxed mb-8">
      Use your camera or upload a picture of your ingredients, and let us suggest delicious recipes for you.
    </p>
    <div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8">
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3><p className="text-gray-600">Use your camera or upload an image to get instant recipe ideas.</p></div>
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3><p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p></div>
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3><p className="text-gray-600">Recipes categorized by meal type for easy Browse.</p></div>
    </div>
    <p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to "Grocipes"!</p>
  </div>
);

// --- RecipeScanner Component ---
const RecipeScanner = () => {
  const [imageFile, setImageFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [cameraMode, setCameraMode] = useState(false);
  const webcamRef = useRef(null);
  const [text, setText] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedRecipeDetails, setSelectedRecipeDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isFullRecipeLoading, setIsFullRecipeLoading] = useState(false);
  
  const base64ToFile = (base64, filename) => {
    const arr = base64.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) { u8arr[n] = bstr.charCodeAt(n); }
    return new File([u8arr], filename, { type: mime });
  };
  
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (imageSrc) {
      const file = base64ToFile(imageSrc, 'capture.jpg');
      setImageFile(file);
      setPreviewUrl(imageSrc);
      setCameraMode(false);
    }
  }, [webcamRef]);
  
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!imageFile) {
      alert('Please take a photo or upload an image first.');
      return;
    }
    setIsLoading(true);
    setText('');
    setRecipes([]);

    const formData = new FormData();
    formData.append('image', imageFile);

    try {
      const ocrResponse = await axios.post('http://localhost:5000/extract', formData);
      const extractedText = ocrResponse.data.text;
      setText(extractedText);
      
      const apiResponse = await axios.get(`http://localhost:5000/api/recipes`, {
        params: { ingredients: extractedText }
      });
      setRecipes(apiResponse.data);

    } catch (error) {
      console.error('Error processing image:', error);
      alert(error.response?.data?.message || 'Failed to get recipes. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };
  
  const fetchFullRecipeDetails = async (recipeName) => {
    setIsFullRecipeLoading(true);
    setShowModal(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/full-recipe-details`, {
        params: { name: recipeName }
      });
      setSelectedRecipeDetails(response.data);
    } catch (error) {
      console.error("Error fetching full recipe details:", error);
      alert(error.response?.data?.message || 'Failed to fetch details.');
      setShowModal(false);
    } finally {
      setIsFullRecipeLoading(false);
    }
  };
  
  const closeModal = () => {
    setShowModal(false);
    setSelectedRecipeDetails(null);
  };
  
  const groupRecipesByMealType = (recipesList) => {
    const grouped = { breakfast: [], lunch: [], dinner: [], dessert: [], other: [] };
    recipesList.forEach(recipe => {
      const meal = recipe.meal ? recipe.meal.toLowerCase() : 'unknown';
      if (meal.includes('breakfast')) grouped.breakfast.push(recipe);
      else if (meal.includes('lunch') || meal.includes('main course')) grouped.lunch.push(recipe);
      else if (meal.includes('dinner')) grouped.dinner.push(recipe);
      else if (meal.includes('dessert')) grouped.dessert.push(recipe);
      else grouped.other.push(recipe);
    });
    return grouped;
  };
  const categorizedRecipes = groupRecipesByMealType(recipes);

  const renderRecipeList = (categoryName, recipesInCategory) => {
    if (!recipesInCategory || recipesInCategory.length === 0) return null;
    return (
      <div key={categoryName} className="mb-8 p-4 bg-gray-50 rounded-lg shadow-md w-full md:w-[calc(50%-1rem)] lg:w-[calc(33.33%-1rem)]">
        <h4 className="text-xl font-semibold text-green-600 border-b-2 border-green-400 pb-2 mb-4">{categoryName.charAt(0).toUpperCase() + categoryName.slice(1)} Recipes</h4>
        <ul className="list-none p-0">{recipesInCategory.map((recipe) => (<li key={recipe.id} onClick={() => fetchFullRecipeDetails(recipe.name)} className="mb-3 bg-white p-4 rounded-lg shadow-sm border-l-4 border-green-500 flex items-center gap-4 cursor-pointer hover:shadow-md"><img src={recipe.image} alt={recipe.name} className="w-20 h-20 rounded-lg object-cover flex-shrink-0" onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/80x80/cccccc/000000?text=No+Img"; }} /><div className="flex-grow flex flex-col"><div className="flex justify-between items-start w-full"><strong className="text-green-700 text-lg mr-2">{recipe.name}</strong>{recipe.calories && recipe.calories !== 'N/A' && (<span className="text-gray-600 text-sm font-medium whitespace-nowrap">{recipe.calories}</span>)}</div></div></li>))}</ul>
      </div>
    );
  };
  
  return (
    <div className="container mx-auto p-6 bg-white shadow-xl rounded-lg mt-8">
      <h2 className="text-3xl font-bold text-center text-green-800 mb-6">Recipe Scanner 📸</h2>
      
      <div className="p-4 bg-green-50 rounded-lg shadow-inner text-center">
        <div className="flex flex-col items-center gap-4">
            <label className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold cursor-pointer hover:bg-gray-700">
                <span>Capture Photo</span>
                <input type="file" accept="image/*" onChange={handleFileChange} className="hidden" capture="camera" />
            </label>
            <label className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold cursor-pointer hover:bg-gray-700">
                <span>Upload File</span>
                <input type="file" accept="image/*" onChange={handleFileChange} className="hidden" />
            </label>
            {previewUrl && (<div className="mt-4"><h4 className="font-semibold text-lg mb-2">Preview:</h4><img src={previewUrl} alt="Selected preview" className="max-w-xs mx-auto rounded-lg shadow-md"/></div>)}
          </div>
      </div>

      <div className="text-center my-6">
        <button onClick={handleUpload} disabled={isLoading || !imageFile} className={`py-2 px-8 rounded-full text-white font-semibold transition duration-300 ${isLoading || !imageFile ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'}`}>{isLoading ? 'Analyzing...' : 'Get Recipes'}</button>
      </div>

      {text && (<div className="mb-8"><h3 className="text-2xl font-semibold text-gray-700 mb-3">Detected Text:</h3><pre className="bg-gray-100 p-4 rounded-lg whitespace-pre-wrap">{text}</pre></div>)}
      {recipes.length > 0 && (<div className="mt-8"><h3 className="text-2xl font-semibold text-gray-700 mb-6 text-center">Suggested Recipes ✨</h3><div className="flex flex-wrap justify-center gap-4">{Object.entries(categorizedRecipes).map(([category, list]) => renderRecipeList(category, list))}</div></div>)}
      
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
          <div className="bg-white p-6 md:p-8 rounded-xl shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto relative">
            <button onClick={closeModal} className="absolute top-4 right-4 text-3xl text-gray-400 hover:text-gray-700">&times;</button>
            {isFullRecipeLoading ? (<p className="text-center py-20">Loading details...</p>) : selectedRecipeDetails ? (
              <>
                <h3 className="text-3xl md:text-4xl font-bold text-green-800 mb-4">{selectedRecipeDetails.title}</h3>
                <img src={selectedRecipeDetails.image} alt={selectedRecipeDetails.title} className="w-full h-48 md:h-64 rounded-lg mb-6 object-cover" onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/600x400/cccccc/000000?text=No+Image"; }} />
                
                <div className="flex justify-center text-center gap-8 p-4 bg-green-50 rounded-lg mb-6">
                  <div><p className="text-sm text-gray-600">Servings</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.servings || 'N/A'}</p></div>
                  <div><p className="text-sm text-gray-600">Ready in</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.readyInMinutes ? `${selectedRecipeDetails.readyInMinutes} min` : 'N/A'}</p></div>
                </div>

                {selectedRecipeDetails.nutrition?.nutrients && (
                  <div className="my-6">
                    <h4 className="text-2xl font-semibold text-gray-800 mb-3">Nutritional Information</h4>
                    <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                      {selectedRecipeDetails.nutrition.nutrients.filter(n => ['Calories', 'Carbohydrates', 'Protein', 'Fat'].includes(n.name)).map((nutrient, i) => (
                        <div key={i} className="bg-gray-100 p-3 rounded-lg"><p className="text-sm text-gray-600">{nutrient.name}</p><p className="font-bold text-lg text-green-800">{nutrient.amount.toFixed(0)}{nutrient.unit}</p></div>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="mt-8 border-t pt-6 grid md:grid-cols-2 md:gap-x-8">
                  <div className="mb-6 md:mb-0"><h4 className="text-2xl font-semibold text-gray-800 mb-3">Ingredients:</h4><ul className="list-disc pl-5 text-gray-700 space-y-2">{selectedRecipeDetails.extendedIngredients?.map((ing, i) => <li key={i}>{ing.original}</li>)}</ul></div>
                  <div><h4 className="text-2xl font-semibold text-gray-800 mb-3">Instructions:</h4>{selectedRecipeDetails.instructions ? (<div className="prose prose-sm md:prose-base max-w-none text-gray-700" dangerouslySetInnerHTML={{ __html: selectedRecipeDetails.instructions }} />) : (<p className="text-gray-600">No instructions available.</p>)}</div>
                </div>
              </>
            ) : <p className="text-center py-20">Details could not be loaded.</p>}
          </div>
        </div>
      )}
    </div>
  );
};
