import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Signup from './components/Signup';
import Navbar from './components/Navbar';
import HomePage from './pages/Home';
import RecipeScanner from './pages/RecipeScanner';
import FoodDatabasePage from './pages/FoodDatabase';
import ProfilePage from './pages/Profile';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    setCurrentPage('home');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
  };

  const handleSignupSuccess = () => {
    setShowSignup(false);
  };

  const renderAppContent = () => {
    switch (currentPage) {
        case 'home': return <HomePage />;
        case 'scanner': return <RecipeScanner />;
        case 'food_db': return <FoodDatabasePage />;
        case 'profile': return <ProfilePage />;
        default: return <HomePage />;
    }
  };

  if (!isLoggedIn) {
    if (showSignup) {
      return <Signup onSignupSuccess={handleSignupSuccess} switchToLogin={() => setShowSignup(false)} />;
    }
    return <Login onLoginSuccess={handleLoginSuccess} switchToSignup={() => setShowSignup(true)} />;
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar setCurrentPage={setCurrentPage} onLogout={handleLogout} />
      <main className="py-8">{renderAppContent()}</main>
    </div>
  );
}

export default App;
