import React, { useState } from 'react';

const App = () => {
Â  // State for active page and recipe generator data
Â  const [activePage, setActivePage] = useState('home');
Â  const [image, setImage] = useState(null);
Â  const [text, setText] = useState('');
Â  const [status, setStatus] = useState('Idle');
Â  const [recipes, setRecipes] = useState([]);
Â  const [selectedRecipe, setSelectedRecipe] = useState(null);
Â  const [errorMessage, setErrorMessage] = useState('');
Â  const [isDarkMode, setIsDarkMode] = useState(true); // State to track dark mode

Â  // Handles the file input change event
Â  const handleImageChange = (event) => {
Â  Â  if (event.target.files && event.target.files[0]) {
Â  Â  Â  setImage(event.target.files[0]);
Â  Â  Â  setText('');
Â  Â  Â  setRecipes([]);
Â  Â  Â  setSelectedRecipe(null);
Â  Â  Â  setErrorMessage('');
Â  Â  Â  setStatus('Image selected');
Â  Â  }
Â  };

Â  // Main function to process the image and find recipes
Â  const processImage = async () => {
Â  Â  setErrorMessage('');
Â  Â  if (!image) {
Â  Â  Â  setErrorMessage('Please select an image first.');
Â  Â  Â  return;
Â  Â  }

Â  Â  setStatus('Uploading and processing...');
Â  Â  setText('');
Â  Â  setRecipes([]);
Â  Â  setSelectedRecipe(null);

Â  Â  try {
Â  Â  Â  // Step 1: Upload image for OCR
Â  Â  Â  const formData = new FormData();
Â  Â  Â  formData.append('image', image);
Â  Â  Â  const ocrResponse = await fetch('http://localhost:5000/ocr', {
Â  Â  Â  Â  method: 'POST',
Â  Â  Â  Â  body: formData,
Â  Â  Â  });

Â  Â  Â  if (!ocrResponse.ok) {
Â  Â  Â  Â  throw new Error(`OCR failed with status: ${ocrResponse.status}`);
Â  Â  Â  }
Â  Â  Â  const ocrData = await ocrResponse.json();
Â  Â  Â  const extractedText = ocrData.text;
Â  Â  Â  setText(extractedText);
Â  Â  Â  
Â  Â  Â  // Step 2: Format text into a list of ingredients
Â  Â  Â  const ingredients = extractedText.toLowerCase().split(/\s*[\r\n-]\s*/).filter(item => item.trim() !== '');

Â  Â  Â  if (ingredients.length === 0) {
Â  Â  Â  Â  setStatus('No ingredients found in the image.');
Â  Â  Â  Â  return;
Â  Â  Â  }

Â  Â  Â  // Step 3: Send ingredients to get a list of recipes
Â  Â  Â  setStatus('Fetching recipes from Spoonacular...');
Â  Â  Â  const recipesResponse = await fetch('http://localhost:5000/get_recipes', {
Â  Â  Â  Â  method: 'POST',
Â  Â  Â  Â  headers: {
Â  Â  Â  Â  Â  'Content-Type': 'application/json',
Â  Â  Â  Â  },
Â  Â  Â  Â  body: JSON.stringify({ ingredients }),
Â  Â  Â  });

Â  Â  Â  if (!recipesResponse.ok) {
Â  Â  Â  Â  throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}`);
Â  Â  Â  }
Â  Â  Â  const recipesData = await recipesResponse.json();
Â  Â  Â  setRecipes(recipesData.recipes);
Â  Â  Â  setStatus('Complete!');

Â  Â  } catch (error) {
Â  Â  Â  console.error('API Error:', error);
Â  Â  Â  setErrorMessage('An error occurred. Please check the backend server.');
Â  Â  Â  setStatus('An error occurred.');
Â  Â  Â  setRecipes([]);
Â  Â  }
Â  };

Â  // Fetch detailed information for a specific recipe
Â  const fetchRecipeDetails = async (id) => {
Â  Â  setStatus('Fetching recipe details...');
Â  Â  setErrorMessage('');
Â  Â  try {
Â  Â  Â  const response = await fetch(`http://localhost:5000/get_recipe_details/${id}`);
Â  Â  Â  if (!response.ok) {
Â  Â  Â  Â  throw new Error(`Failed to fetch recipe details: ${response.status}`);
Â  Â  Â  }
Â  Â  Â  const details = await response.json();
Â  Â  Â  setSelectedRecipe(details);
Â  Â  Â  setStatus('Details loaded!');
Â  Â  } catch (error) {
Â  Â  Â  console.error('Error fetching details:', error);
Â  Â  Â  setErrorMessage('Failed to load details. Please try again.');
Â  Â  Â  setStatus('Failed to load details.');
Â  Â  }
Â  };
Â  
Â  // Go back to the recipe list from the detailed view
Â  const goBackToList = () => {
Â  Â  setSelectedRecipe(null);
Â  Â  setStatus('Complete!');
Â  };

Â  // Handle the "Yes, cooking this!" button click
Â  const handleCookThis = () => {
Â  Â  alert(`Great choice! We hope you enjoy cooking ${selectedRecipe.title}!`);
Â  Â  // You can add more logic here, such as:
Â  Â  // - Logging the recipe to a user's profile
Â  Â  // - Sending a notification
Â  Â  // - Triggering a state change to move the user to a "My Meals" page
Â  };

Â  // Toggle dark/light mode
Â  const toggleDarkMode = () => {
Â  Â  setIsDarkMode(prevMode => !prevMode);
Â  };

Â  const themeClasses = isDarkMode 
Â  Â  ? {
Â  Â  Â  bg: 'bg-gray-900',
Â  Â  Â  text: 'text-white',
Â  Â  Â  container: 'bg-gray-800 border-gray-700',
Â  Â  Â  card: 'bg-gray-700',
Â  Â  Â  cardHover: 'hover:bg-gray-600',
Â  Â  Â  button: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
Â  Â  Â  buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700',
Â  Â  Â  subheading: 'text-purple-300',
Â  Â  Â  infoBox: 'bg-gray-700 border-gray-700',
Â  Â  Â  input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100',
Â  Â  Â  nutrients: 'bg-gray-600 text-gray-100',
Â  Â  Â  nutrientsSubtext: 'text-gray-400',
Â  Â  Â  listText: 'text-gray-300',
Â  Â  Â  navText: 'text-gray-300 hover:text-white',
Â  Â  Â  navActive: 'border-b-2 border-purple-400',
Â  Â  }
Â  Â  : {
Â  Â  Â  bg: 'bg-gray-100',
Â  Â  Â  text: 'text-gray-900',
Â  Â  Â  container: 'bg-white border-gray-300',
Â  Â  Â  card: 'bg-gray-200',
Â  Â  Â  cardHover: 'hover:bg-gray-300',
Â  Â  Â  button: 'bg-gray-300 text-gray-800 hover:bg-gray-400',
Â  Â  Â  buttonPrimary: 'bg-gradient-to-r from-indigo-500 to-teal-600 hover:from-indigo-600 hover:to-teal-700',
Â  Â  Â  subheading: 'text-indigo-500',
Â  Â  Â  infoBox: 'bg-gray-200 border-gray-300',
Â  Â  Â  input: 'file:text-indigo-700 file:bg-indigo-50 hover:file:bg-indigo-100',
Â  Â  Â  nutrients: 'bg-gray-300 text-gray-800',
Â  Â  Â  nutrientsSubtext: 'text-gray-600',
Â  Â  Â  listText: 'text-gray-700',
Â  Â  Â  navText: 'text-gray-600 hover:text-gray-900',
Â  Â  Â  navActive: 'border-b-2 border-indigo-500',
Â  Â  };

Â  return (
Â  Â  <div className={`${themeClasses.bg} min-h-screen ${themeClasses.text} font-sans flex flex-col items-center p-4 sm:p-8 transition-colors duration-500`}>
Â  Â  Â  {/* Navbar based on MyPlanPlate design with active link indicator */}
Â  Â  Â  <header className="w-full max-w-4xl flex justify-between items-center mb-8">
Â  Â  Â  Â  <div className="flex items-center">
Â  Â  Â  Â  Â  <svg className="w-8 h-8 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
Â  Â  Â  Â  Â  Â  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5" />
Â  Â  Â  Â  Â  </svg>
Â  Â  Â  Â  Â  <span className="text-xl font-semibold">MyPlanPlate</span>
Â  Â  Â  Â  </div>
Â  Â  Â  Â  <nav className="flex space-x-6">
Â  Â  Â  Â  Â  <a href="#home" onClick={() => setActivePage('home')} className={`${themeClasses.navText} ${activePage === 'home' ? themeClasses.navActive : ''}`}>Home</a>
Â  Â  Â  Â  Â  <a href="#daily-goals" onClick={() => setActivePage('daily-goals')} className={`${themeClasses.navText} ${activePage === 'daily-goals' ? themeClasses.navActive : ''}`}>Daily Goals</a>
Â  Â  Â  Â  Â  <a href="#about" onClick={() => setActivePage('about')} className={`${themeClasses.navText} ${activePage === 'about' ? themeClasses.navActive : ''}`}>About</a>
Â  Â  Â  Â  Â  <a href="#profile" onClick={() => setActivePage('profile')} className={`${themeClasses.navText} ${activePage === 'profile' ? themeClasses.navActive : ''}`}>Profile</a>
Â  Â  Â  Â  </nav>
Â  Â  Â  Â  <div className="flex space-x-4">
Â  Â  Â  Â  Â  {/* Kept only bag icon */}
Â  Â  Â  Â  Â  <button className={themeClasses.button}>
Â  Â  Â  Â  Â  Â  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
Â  Â  Â  Â  Â  Â  Â  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 3h18M3 21h18M3 9h18M3 15h18" />
Â  Â  Â  Â  Â  Â  </svg>
Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  </div>
Â  Â  Â  </header>

Â  Â  Â  <main className={`w-full max-w-4xl p-6 rounded-3xl shadow-lg border ${themeClasses.container} transition-colors duration-500`}>
Â  Â  Â  Â  {activePage === 'home' && (
Â  Â  Â  Â  Â  <div className="flex flex-col items-center">
Â  Â  Â  Â  Â  Â  <div className="w-full text-center">
Â  Â  Â  Â  Â  Â  Â  <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
Â  Â  Â  Â  Â  Â  Â  Â  <input
Â  Â  Â  Â  Â  Â  Â  Â  Â  type="file"
Â  Â  Â  Â  Â  Â  Â  Â  Â  onChange={handleImageChange}
Â  Â  Â  Â  Â  Â  Â  Â  Â  accept="image/*"
Â  Â  Â  Â  Â  Â  Â  Â  Â  capture="environment"
Â  Â  Â  Â  Â  Â  Â  Â  Â  className={`block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold ${themeClasses.input}`}
Â  Â  Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  Â  Â  Â  onClick={processImage}
Â  Â  Â  Â  Â  Â  Â  Â  Â  disabled={!image || status.includes('...') || status === 'Error.'}
Â  Â  Â  Â  Â  Â  Â  Â  Â  className={`w-full sm:w-auto px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-lg`}
Â  Â  Â  Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Â  Â  Â  {status.includes('...') ? 'Processing...' : 'Find Recipes'}
Â  Â  Â  Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  
Â  Â  Â  Â  Â  Â  {/* Status and error messages */}
Â  Â  Â  Â  Â  Â  {errorMessage && (
Â  Â  Â  Â  Â  Â  Â  <p className="mt-4 text-sm font-semibold text-center text-red-400">
Â  Â  Â  Â  Â  Â  Â  Â  {errorMessage}
Â  Â  Â  Â  Â  Â  Â  </p>
Â  Â  Â  Â  Â  Â  )}
Â  Â  Â  Â  Â  Â  {!errorMessage && (
Â  Â  Â  Â  Â  Â  Â  <p className={`mt-4 text-sm font-semibold text-center ${themeClasses.listText}`}>
Â  Â  Â  Â  Â  Â  Â  Â  <strong>Status:</strong> {status}
Â  Â  Â  Â  Â  Â  Â  </p>
Â  Â  Â  Â  Â  Â  )}

Â  Â  Â  Â  Â  Â  {text && (
Â  Â  Â  Â  Â  Â  Â  <div className={`mt-6 p-4 rounded-xl shadow-inner w-full ${themeClasses.infoBox}`}>
Â  Â  Â  Â  Â  Â  Â  Â  <h3 className={`text-lg font-semibold mb-2 ${themeClasses.subheading}`}>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Extracted Ingredients:
Â  Â  Â  Â  Â  Â  Â  Â  </h3>
Â  Â  Â  Â  Â  Â  Â  Â  <pre className={`whitespace-pre-wrap text-sm ${themeClasses.listText}`}>{text}</pre>
Â  Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  )}
Â  Â  
Â  Â  Â  Â  Â  Â  {recipes.length > 0 && (
