import React, { useState } from 'react';
import './App.css'; 

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [status, setStatus] = useState('Idle');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setImage(event.target.files[0]);
      setText('');
      setRecipes([]);
      setSelectedRecipe(null);
      setStatus('Image selected');
    }
  };

  const processImage = async () => {
    if (!image) {
      alert('Please select an image first.');
      return;
    }

    setStatus('Uploading and processing...');
    setText('');
    setRecipes([]);
    setSelectedRecipe(null);

    try {
      // Step 1: Upload image for OCR
      const formData = new FormData();
      formData.append('image', image);
      const ocrResponse = await fetch('http://localhost:5000/ocr', {
        method: 'POST',
        body: formData,
      });

      if (!ocrResponse.ok) {
        throw new Error(`OCR failed with status: ${ocrResponse.status}`);
      }
      const ocrData = await ocrResponse.json();
      const extractedText = ocrData.text;
      setText(extractedText);
      
      // Step 2: Format text into a list of ingredients
      const ingredients = extractedText.toLowerCase().split(/\s*[\r\n-]\s*/).filter(item => item.trim() !== '');

      // Step 3: Send ingredients to get a list of recipes
      setStatus('Fetching recipes from Spoonacular...');
      const recipesResponse = await fetch('http://localhost:5000/get_recipes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients }),
      });

      if (!recipesResponse.ok) {
        throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}`);
      }
      const recipesData = await recipesResponse.json();
      setRecipes(recipesData.recipes);
      setStatus('Complete!');

    } catch (error) {
      console.error('API Error:', error);
      setStatus('An error occurred.');
      setRecipes([]);
    }
  };

  const fetchRecipeDetails = async (id) => {
    setStatus('Fetching recipe details...');
    try {
      const response = await fetch(`http://localhost:5000/get_recipe_details/${id}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch recipe details: ${response.status}`);
      }
      const details = await response.json();
      setSelectedRecipe(details);
      setStatus('Details loaded!');
    } catch (error) {
      console.error('Error fetching details:', error);
      setStatus('Failed to load details.');
    }
  };
  
  const goBackToList = () => {
    setSelectedRecipe(null);
    setStatus('Complete!');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>OCR & Recipe Suggester üìú</h1>
        
        {selectedRecipe ? (
          // Display detailed recipe view
          <div className="recipe-details">
            <button onClick={goBackToList} className="back-button">‚Üê Back to Recipes</button>
            <h2>{selectedRecipe.title}</h2>
            <img src={selectedRecipe.image} alt={selectedRecipe.title} className="recipe-detail-image" />
            
            <div className="details-section">
              <h3>Nutrition (per serving):</h3>
              <div className="nutrition-grid">
                <div className="nutrition-item"><span>Calories</span> <span>{selectedRecipe.nutrients.calories}</span></div>
                <div className="nutrition-item"><span>Protein</span> <span>{selectedRecipe.nutrients.protein}g</span></div>
                <div className="nutrition-item"><span>Carbohydrates</span> <span>{selectedRecipe.nutrients.carbohydrates}g</span></div>
                <div className="nutrition-item"><span>Fat</span> <span>{selectedRecipe.nutrients.fat}g</span></div>
              </div>
            </div>

            <div className="details-section">
              <h3>Ingredients:</h3>
              <ul className="ingredients-list">
                {selectedRecipe.ingredients.map((ingredient) => (
                  <li key={ingredient.id}>
                    {ingredient.original}
                  </li>
                ))}
              </ul>
            </div>

            <div className="details-section">
              <h3>Instructions:</h3>
              <p className="instructions-text">
                {selectedRecipe.instructions || 'Instructions not available.'}
              </p>
            </div>
          </div>
        ) : (
          // Display initial and recipe list view
          <>
            <div className="input-section">
              <input type="file" onChange={handleImageChange} accept="image/*" />
              <button onClick={processImage} disabled={status.includes('processing')}>
                Find Recipes
              </button>
            </div>
            <p><strong>Status:</strong> {status}</p>
            
            {text && (
              <div className="extracted-ingredients-box">
                <h3>Extracted Ingredients:</h3>
                <pre>{text}</pre>
              </div>
            )}
    
            {recipes.length > 0 && (
              <div className="recipes-container">
                <h3>Suggested Recipes üëá</h3>
                <div className="recipe-grid">
                  {recipes.map((recipe) => (
                    <div key={recipe.id} className="recipe-card" onClick={() => fetchRecipeDetails(recipe.id)}>
                      <h4>{recipe.title}</h4>
                      <img src={recipe.image} alt={recipe.title} className="recipe-image" />
                    </div>
                  ))}
                </div>
              </div>
            )}
            {status === 'Complete!' && recipes.length === 0 && (
              <p>No recipes found. Try a different image or more common ingredients!</p>
            )}
          </>
        )}
      </header>
    </div>
  );
}

export default App;
