import React, { useState, useEffect } from 'react';

// Main App component for the diet recommender application.
const App = () => {
  // State variables
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [status, setStatus] = useState('Idle');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [activePage, setActivePage] = useState('home'); // Default page
  const [userProfile, setUserProfile] = useState({
    name: 'Paula',
    email: 'paula@zylker.com',
    userId: '630083685',
    dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.',
    weeklyGoal: 'N/A'
  });

  // Sync activePage with URL hash
  useEffect(() => {
    const hash = window.location.hash.replace('#', '').toLowerCase() || 'home';
    setActivePage(hash);
  }, []); // Run once on mount

  // Handle navigation click and update URL hash
  const handleNavClick = (page) => {
    const formattedPage = page.toLowerCase().replace(' ', '-');
    window.location.hash = formattedPage;
    setActivePage(formattedPage);
  };

  // Handles the file input change event
  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setImage(event.target.files[0]);
      setText('');
      setRecipes([]);
      setSelectedRecipe(null);
      setErrorMessage('');
      setStatus('Image selected');
    }
  };

  // Main function to process the image and find recipes
  const processImage = async () => {
    setErrorMessage('');
    if (!image) {
      setErrorMessage('Please select an image first.');
      return;
    }

    setStatus('Uploading and processing...');
    setText('');
    setRecipes([]);
    setSelectedRecipe(null);

    try {
      // Step 1: Upload image for OCR
      const formData = new FormData();
      formData.append('image', image);
      const ocrResponse = await fetch('http://localhost:5000/ocr', {
        method: 'POST',
        body: formData,
      });

      if (!ocrResponse.ok) {
        throw new Error(`OCR failed with status: ${ocrResponse.status}`);
      }
      const ocrData = await ocrResponse.json();
      const extractedText = ocrData.text;
      setText(extractedText);
      
      // Step 2: Format text into a list of ingredients
      const ingredients = extractedText.toLowerCase().split(/\s*[\r\n-]\s*/).filter(item => item.trim() !== '');

      if (ingredients.length === 0) {
        setStatus('No ingredients found in the image.');
        return;
      }

      // Step 3: Send ingredients to get a list of recipes from the local dataset
      setStatus('Searching local database for recipes...');
      const recipesResponse = await fetch('http://localhost:5000/get_recipes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients }),
      });

      if (!recipesResponse.ok) {
        throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}`);
      }
      const recipesData = await recipesResponse.json();
      setRecipes(recipesData.recipes);
      setStatus('Complete!');

    } catch (error) {
      console.error('API Error:', error);
      setErrorMessage('An error occurred. Please check the backend server.');
      setStatus('An error occurred.');
      setRecipes([]);
    }
  };

  // Fetch detailed information for a specific recipe from the local dataset
  const fetchRecipeDetails = async (id, title) => {
    setStatus('Fetching recipe details...');
    setErrorMessage('');
    try {
      const response = await fetch(`http://localhost:5000/get_recipe_details/${id}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch recipe details: ${response.status}`);
      }
      const details = await response.json();
      setSelectedRecipe(details);
      setStatus('Details loaded!');
    } catch (error) {
      console.error('Error fetching details:', error);
      setErrorMessage('Failed to load details. Please try again.');
      setStatus('Failed to load details.');
    }
  };
  
  // Go back to the recipe list from the detailed view
  const goBackToList = () => {
    setSelectedRecipe(null);
    setStatus('Complete!');
  };

  // Toggle dark/light mode
  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };

  const themeClasses = isDarkMode
    ? {
        bg: 'bg-gray-900',
        text: 'text-white',
        container: 'bg-gray-800 border-gray-700',
        card: 'bg-gray-700',
        cardHover: 'hover:bg-gray-600',
        button: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
        buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700',
        subheading: 'text-purple-300',
        infoBox: 'bg-gray-700 border-gray-700',
        input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100',
        nutrients: 'bg-gray-600 text-gray-100',
        nutrientsSubtext: 'text-gray-400',
        listText: 'text-gray-300',
        navText: 'text-gray-300 hover:text-white',
        navActive: 'border-b-2 border-purple-400',
      }
    : {
        bg: 'bg-gray-100',
        text: 'text-gray-900',
        container: 'bg-white border-gray-300',
        card: 'bg-gray-200',
        cardHover: 'hover:bg-gray-300',
        button: 'bg-gray-300 text-gray-800 hover:bg-gray-400',
        buttonPrimary: 'bg-gradient-to-r from-indigo-500 to-teal-600 hover:from-indigo-600 hover:to-teal-700',
        subheading: 'text-indigo-500',
        infoBox: 'bg-gray-200 border-gray-300',
        input: 'file:text-indigo-700 file:bg-indigo-50 hover:file:bg-indigo-100',
        nutrients: 'bg-gray-300 text-gray-800',
        nutrientsSubtext: 'text-gray-600',
        listText: 'text-gray-700',
        navText: 'text-gray-600 hover:text-gray-900',
        navActive: 'border-b-2 border-indigo-500',
      };

  // Render content based on active page
  const renderPageContent = () => {
    switch (activePage) {
      case 'home':
        if (selectedRecipe) {
          return (
            <div className="animate-fade-in-up">
              <button
                onClick={goBackToList}
                className={`px-4 py-2 mb-4 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
                aria-label="Back to recipe list"
              >
                ← Back to Recipes
              </button>
              <h2 className={`text-3xl font-bold text-center ${themeClasses.subheading}`}>
                {selectedRecipe.title}
              </h2>
              <div className="flex flex-col items-center my-4">
                <img
                  src={selectedRecipe.image}
                  alt={selectedRecipe.title}
                  className="w-full max-w-md h-auto object-cover rounded-xl shadow-lg"
                />
              </div>
              <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>
                  Nutrition (per serving):
                </h3>
                <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                  <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                    <span className="block text-xl font-bold">{selectedRecipe.nutrients.calories}</span>
                    <span className={`block ${themeClasses.nutrientsSubtext}`}>Calories</span>
                  </div>
                  <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                    <span className="block text-xl font-bold">{selectedRecipe.nutrients.protein}</span>
                    <span className={`block ${themeClasses.nutrientsSubtext}`}>Protein</span>
                  </div>
                  <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                    <span className="block text-xl font-bold">
                      {selectedRecipe.nutrients.carbohydrates}
                    </span>
                    <span className={`block ${themeClasses.nutrientsSubtext}`}>Carbohydrates</span>
                  </div>
                  <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                    <span className="block text-xl font-bold">{selectedRecipe.nutrients.fat}</span>
                    <span className={`block ${themeClasses.nutrientsSubtext}`}>Fat</span>
                  </div>
                </div>
              </div>
              <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Ingredients:</h3>
                <ul className="list-disc list-inside space-y-2">
                  {selectedRecipe.ingredients.map((ingredient, index) => (
                    <li key={index} className={`pl-2 ${themeClasses.listText}`}>
                      {ingredient}
                    </li>
                  ))}
                </ul>
              </div>
              <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Instructions:</h3>
                <div
                  className={`leading-relaxed ${themeClasses.listText}`}
                  dangerouslySetInnerHTML={{ __html: selectedRecipe.instructions || 'Instructions not available.' }}
                />
              </div>
            </div>
          );
        }
        return (
          <div className="flex flex-col items-center">
            <div className="w-full text-center">
              <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept="image/*"
                  capture="environment"
                  className={`block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold ${themeClasses.input}`}
                />
                <button
                  onClick={processImage}
                  disabled={!image || status.includes('...') || status === 'Error.'}
                  className={`w-full sm:w-auto px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-lg`}
                >
                  {status.includes('...') ? 'Processing...' : 'Find Recipes'}
                </button>
              </div>
            </div>
            {errorMessage && <p className="mt-4 text-sm font-semibold text-center text-red-400">{errorMessage}</p>}
            {!errorMessage && (
              <p className={`mt-4 text-sm font-semibold text-center ${themeClasses.listText}`}>
                <strong>Status:</strong> {status}
              </p>
            )}
            {text && (
              <div className={`mt-6 p-4 rounded-xl shadow-inner w-full ${themeClasses.infoBox}`}>
                <h3 className={`text-lg font-semibold mb-2 ${themeClasses.subheading}`}>
                  Extracted Ingredients:
                </h3>
                <pre className={`whitespace-pre-wrap text-sm ${themeClasses.listText}`}>
                  {text || 'No text extracted.'}
                </pre>
              </div>
            )}
            {recipes.length > 0 && (
              <div className="recipes-container mt-8 w-full">
                <h3 className={`text-2xl font-bold text-center mb-4 ${themeClasses.subheading}`}>
                  Suggested Recipes 👇
                </h3>
                <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {recipes.map((recipe) => (
                    <div
                      key={recipe.id}
                      className={`p-4 rounded-2xl shadow-lg cursor-pointer transform hover:scale-105 transition-transform duration-200 ${themeClasses.card} ${themeClasses.cardHover}`}
                      onClick={() => fetchRecipeDetails(recipe.id, recipe.title)}
                      role="button"
                      tabIndex={0}
                      onKeyDown={(e) => e.key === 'Enter' && fetchRecipeDetails(recipe.id, recipe.title)}
                      aria-label={`View details for ${recipe.title}`}
                    >
                      <img
                        src={recipe.image}
                        alt={recipe.title}
                        className="w-full h-40 object-cover rounded-xl mb-3"
                      />
                      <h4
                        className={`text-lg font-semibold text-center ${
                          isDarkMode ? 'text-gray-100' : 'text-gray-800'
                        }`}
                      >
                        {recipe.title}
                      </h4>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {status === 'Complete!' && recipes.length === 0 && (
              <p className={`mt-4 text-center ${themeClasses.listText}`}>
                No recipes found. Try a different image or more common ingredients!
              </p>
            )}
          </div>
        );
      case 'daily-goal':
        return <div className="text-center">Daily Goal content goes here.</div>;
      case 'profile':
        return (
          <div className="flex flex-col items-center p-6 space-y-8">
            <h2 className={`text-3xl font-bold ${themeClasses.subheading}`}>My Profile</h2>
            <div className={`flex items-center space-x-6 p-6 rounded-2xl shadow-lg w-full max-w-xl ${themeClasses.infoBox}`}>
              <img
                src="https://placehold.co/100x100/A0B5FF/000000?text=P" // Placeholder for profile picture
                alt="Profile"
                className="w-24 h-24 rounded-full border-4 border-purple-400"
              />
              <div className="flex flex-col space-y-1">
                <h3 className="text-2xl font-semibold">{userProfile.name}</h3>
                <p className={`text-sm ${themeClasses.listText}`}>{userProfile.email}</p>
                <p className={`text-sm ${themeClasses.listText}`}>User ID: {userProfile.userId}</p>
              </div>
            </div>
            <div className={`w-full max-w-xl p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
              <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>My Diet Plan</h3>
              <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                {userProfile.dietPlan}
              </p>
            </div>
            <div className={`w-full max-w-xl p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
              <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>My Weekly Goal</h3>
              <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                {userProfile.weeklyGoal}
              </p>
            </div>
            <div className="flex space-x-4 mt-4">
              <button
                className={`px-6 py-2 rounded-full font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg`}
                onClick={() => alert("Simulating sign out...")}
              >
                Sign Out
              </button>
            </div>
          </div>
        );
      case 'weekend-summary':
        return <div className="text-center">Weekend Summary content goes here.</div>;
      default:
        return <div className="text-center">Page not found.</div>;
    }
  };

  return (
    <div
      className={`${themeClasses.bg} min-h-screen ${themeClasses.text} font-sans flex flex-col items-center p-4 sm:p-8 transition-colors duration-500`}
    >
      <header className="w-full max-w-4xl flex justify-between items-center mb-8">
        <div className="flex items-center">
          <svg
            className="w-8 h-8 mr-2"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5"
            />
          </svg>
          <span className="text-xl font-semibold">PlanMyPlate</span>
        </div>
        <nav className="flex space-x-6">
          {['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => {
            const formattedPage = page.toLowerCase().replace(' ', '-');
            return (
              <button
                key={page}
                onClick={() => handleNavClick(page)}
                className={`px-2 py-1 transition-all duration-300 ${themeClasses.navText} ${
                  activePage === formattedPage ? themeClasses.navActive : ''
                }`}
                aria-current={activePage === formattedPage ? 'page' : undefined}
                aria-label={`Navigate to ${page}`}
              >
                {page}
              </button>
            );
          })}
        </nav>
        <div className="flex space-x-4">
          <button className={themeClasses.button} aria-label="Search">
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
              />
            </svg>
          </button>
          <button className={themeClasses.button} aria-label="Profile">
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"
              />
            </svg>
          </button>
          <button className={themeClasses.button} aria-label="Menu">
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M3 3h18M3 21h18M3 9h18M3 15h18"
              />
            </svg>
          </button>
        </div>
      </header>
      <main
        className={`w-full max-w-4xl p-6 rounded-3xl shadow-lg border ${themeClasses.container} transition-colors duration-500`}
      >
        {renderPageContent()}
      </main>
      <button
        onClick={toggleDarkMode}
        className={`fixed bottom-4 right-4 p-2 rounded-full ${themeClasses.button} shadow-md transition-colors duration-200`}
        aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
      >
        {isDarkMode ? '🌞 Light Mode' : '🌙 Dark Mode'}
      </button>
    </div>
  );
};

export default App;
