import React, { useState } from 'react';
import axios from 'axios';

const Signup = ({ onSignupSuccess, switchToLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
 
    const handleSignup = async () => {
      try {
        const response = await axios.post('http://localhost:5000/api/signup', { email, password });
        alert(response.data.message);
        onSignupSuccess();
      } catch (error) {
        alert(error.response?.data?.message || 'Signup failed.');
      }
    };
 
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
          <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md">
              <h2 className="text-3xl font-bold mb-6 text-center text-green-700">Create Account</h2>
              <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-4 p-3 w-full border rounded-lg" />
              <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-6 p-3 w-full border rounded-lg" />
              <button onClick={handleSignup} className="bg-green-600 text-white py-3 px-4 rounded-lg w-full font-semibold hover:bg-green-700 transition">Sign Up</button>
              <p className="mt-6 text-center">
                Already have an account? <button onClick={switchToLogin} className="text-blue-500 hover:underline">Login here</button>
              </p>
          </div>
      </div>
    );
};

export default Signup;
