import React, { useState } from "react";
import axios from "axios";

function App() {
  const [image, setImage] = useState(null);
  const [ingredients, setIngredients] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleOCR = async () => {
    if (!image) return;
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append("file", image);

      // üîπ Call your OCR API here
      const ocrResponse = await axios.post("http://localhost:5000/ocr", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Expecting { text: "tomato\npotato\ncarrot..." }
      const rawText = ocrResponse.data.text || "";
      const lines = rawText
        .split("\n")
        .map((l) => l.trim().toLowerCase())
        .filter((l) => l);

      setIngredients(lines);

      // üîπ Call Spoonacular API
      const apiKey = "YOUR_SPOONACULAR_API_KEY"; // replace with your key
      const query = lines.join(","); // join ingredients with commas

      const recipeResponse = await axios.get(
        `https://api.spoonacular.com/recipes/findByIngredients`,
        {
          params: {
            ingredients: query,
            number: 5, // how many recipes to fetch
            apiKey: apiKey,
          },
        }
      );

      setRecipes(recipeResponse.data);
      setLoading(false);
    } catch (err) {
      console.error("Error:", err);
      setLoading(false);
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center" }}>
        OCR Image Upload & Recipe Suggester üç¥
      </h2>

      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <input type="file" onChange={handleFileChange} />
        <button
          onClick={handleOCR}
          style={{
            marginLeft: "10px",
            padding: "8px 14px",
            background: "green",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
          disabled={loading}
        >
          {loading ? "Processing..." : "Upload and Extract Text"}
        </button>
      </div>

      <h3>Extracted Ingredients:</h3>
      <div
        style={{
          background: "#f8f9fa",
          padding: "10px",
          borderRadius: "5px",
          minHeight: "100px",
        }}
      >
        {ingredients.length > 0 ? (
          <pre>{ingredients.join("\n")}</pre>
        ) : (
          <p style={{ color: "#888" }}>No ingredients extracted yet.</p>
        )}
      </div>

      <h3 style={{ marginTop: "20px" }}>Suggested Recipes üí°</h3>
      <div>
        {recipes.length > 0 ? (
          recipes.map((r, i) => (
            <div
              key={i}
              style={{
                background: "#e6ffe6",
                padding: "10px",
                marginBottom: "10px",
                borderRadius: "5px",
              }}
            >
              <b>{r.title}</b>
              <br />
              <img
                src={r.image}
                alt={r.title}
                style={{ width: "120px", marginTop: "8px", borderRadius: "5px" }}
              />
              <br />
              <small>(Used ingredients: {r.usedIngredientCount})</small>
            </div>
          ))
        ) : (
          <p style={{ color: "#888" }}>No recipes found yet.</p>
        )}
      </div>
    </div>
  );
}

export default App;
