import React, { useState, useEffect, useCallback } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from 'recharts';

// --- NutritionalChart Component ---
const NutritionalChart = ({ data, colors, isDarkMode }) => {
    const getTextColor = () => isDarkMode ? 'white' : 'black';
    const formattedData = Object.keys(data)
        .filter(key => data[key] > 0)
        .map(key => ({
            name: key.replace('Content', '').replace(/([A-Z])/g, ' $1').trim(),
            value: data[key],
        }));
    const totalCalories = data['Calories'] || 0;
    if (totalCalories === 0) return <p className={`text-center ${getTextColor()} p-4`}>No nutritional data logged today. Log a meal below!</p>;
    const renderCustomizedLabel = ({ cx, cy }) => (
        <text x={cx} y={cy} dy={5} textAnchor="middle" fill={getTextColor()} className="text-sm">
            <tspan x={cx} dy="-0.6em" className="text-xl font-bold">{totalCalories}</tspan>
            <tspan x={cx} dy="1.5em" className="text-sm font-light">LOGGED CALORIES</tspan>
        </text>
    );
    return (
        <div className="flex flex-col items-center">
            <h3 className={`text-xl font-semibold mb-4 ${isDarkMode ? 'text-purple-300' : 'text-indigo-500'} transition-colors duration-500`}>
                Daily Nutritional Summary:
            </h3>
            <div className="flex justify-center items-center w-full h-80">
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={formattedData} cx="50%" cy="50%" innerRadius={70} outerRadius={110} fill="#8884d8" paddingAngle={2} dataKey="value" labelLine={false} label={renderCustomizedLabel} cornerRadius={5}
                        >
                            {formattedData.map((entry, index) => (<Cell key={`cell-${index}`} fill={colors[index % colors.length]} />))}
                        </Pie>
                        <Tooltip
                             contentStyle={{ backgroundColor: isDarkMode ? '#1f2937' : 'white', borderColor: isDarkMode ? '#4b5563' : '#d1d5db', borderRadius: '8px', fontSize: '14px', color: isDarkMode ? 'white' : 'black', padding: '10px' }}
                             formatter={(value, name) => [`${value} ${name.toLowerCase().includes('fat') || name.toLowerCase().includes('protein') || name.toLowerCase().includes('carbohydr') ? 'g' : ''}`, name.replace('Content', '')]}
                        />
                    </PieChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

// --- Main App Component ---

const App = () => {
    // ------------------------------------
    // 1. STATIC DATASET (For recipe listing simulation)
    // ------------------------------------
    const STATIC_RECIPE_DATA = [
        { "id": 1, "title": "5 Eggs (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=5+Eggs", "nutrients": { "calories": 173, "protein": 42.4, "carbohydrates": 83.7, "fat": 1.5 } },
        { "id": 2, "title": "1 Apple (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=1+Apple", "nutrients": { "calories": 66, "protein": 39.2, "carbohydrates": 13.8, "fat": 3.2 } },
        { "id": 3, "title": "5 Chicken Breast (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=5+Chicken", "nutrients": { "calories": 226, "protein": 27.1, "carbohydrates": 79.1, "fat": 25.8 } },
        { "id": 4, "title": "2 Banana (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=2+Banana", "nutrients": { "calories": 116, "protein": 43.4, "carbohydrates": 47.1, "fat": 16.1 } },
        { "id": 5, "title": "5 Oats (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=5+Oats", "nutrients": { "calories": 387, "protein": 31.2, "carbohydrates": 6.8, "fat": 39.5 } },
        { "id": 6, "title": "4 Carrot (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=4+Carrot", "nutrients": { "calories": 138, "protein": 28.2, "carbohydrates": 12.4, "fat": 20.2 } },
        { "id": 7, "title": "2 Cookies (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=2+Cookies", "nutrients": { "calories": 69, "protein": 36.8, "carbohydrates": 17.8, "fat": 15 } },
        { "id": 8, "title": "4 Quinoa (Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=4+Quinoa", "nutrients": { "calories": 204, "protein": 19.3, "carbohydrates": 62.7, "fat": 31.8 } },
        { "id": 9, "title": "5 Oats (Second Sample)", "image": "https://placehold.co/400x300/e5e7eb/1f2937?text=5+Oats+2", "nutrients": { "calories": 108, "protein": 40.6, "carbohydrates": 13.9, "fat": 12.2 } }
    ];

    const API_URL = 'http://localhost:5000'; 

    // --- State Variables ---
    const [image, setImage] = useState(null);
    const [ingredientText, setIngredientText] = useState('');
    const [extractedText, setExtractedText] = useState('');
    const [status, setStatus] = useState('Idle');
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipe, setSelectedRecipe] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(true);
    const [activePage, setActivePage] = useState('Home');
    const [imageLoading, setImageLoading] = useState({});
    const [todayMeal, setTodayMeal] = useState(null);
    
    // Logging/Goal States
    const [dailyMealLog, setDailyMealLog] = useState([]);
    const [dailyNutritionSummary, setDailyNutritionSummary] = useState({
        'Calories': 0, 'FatContent': 0, 'SaturatedFatContent': 0, 'CholesterolContent': 0,
        'SodiumContent': 0, 'CarbohydrateContent': 0, 'FiberContent': 0, 'SugarContent': 0, 'ProteinContent': 0,
    });
    const [age, setAge] = useState(30);
    const [height, setHeight] = useState(175);
    const [weight, setWeight] = useState(70);
    const [gender, setGender] = useState('male');
    const [activity, setActivity] = useState(3);
    const [mealsPerDay, setMealsPerDay] = useState(3);
    const [healthMetrics, setHealthMetrics] = useState(null);
    const [bmi, setBmi] = useState(null);
    const [userProfile, setUserProfile] = useState({
        name: 'Paula', email: 'paula@zylker.com', userId: '630083685',
        dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.', weeklyGoal: 'N/A'
    });
    const chartColors = [
        '#667eea', '#4FD1C5', '#F6AD55', '#E53E3E', '#63B3ED', '#81E6D9', '#D53F8C', '#ECC94B', '#9F7AEA'
    ];
    // --- End State Variables ---

    // --- Helper Functions ---
    const logMealToDailyGoal = (mealData) => {
        const isRecipe = mealData.nutrients && mealData.nutrients.calories !== undefined;
        const mealNutrients = {
            'Calories': parseInt(mealData.nutrients?.calories || mealData.calories) || 0,
            'FatContent': parseFloat(mealData.nutrients?.fat || 0) || 0,
            'SaturatedFatContent': parseFloat(mealData.nutrients?.fat || 0) / 4 || 0, 
            'CholesterolContent': 0, 
            'SodiumContent': 0, 
            'CarbohydrateContent': parseFloat(mealData.nutrients?.carbohydrates || 0) || 0,
            'FiberContent': 0,
            'SugarContent': 0,
            'ProteinContent': parseFloat(mealData.nutrients?.protein || 0) || 0,
        };
        setDailyMealLog(prevLog => [
            ...prevLog, 
            {
                name: mealData.title || mealData.name,
                calories: mealNutrients.Calories,
                type: isRecipe ? 'Recipe' : 'Dataset Lookup',
                timestamp: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
            }
        ]);
        setDailyNutritionSummary(prevSummary => ({
            'Calories': prevSummary.Calories + mealNutrients.Calories,
            'FatContent': prevSummary.FatContent + mealNutrients.FatContent,
            'SaturatedFatContent': prevSummary.SaturatedFatContent + mealNutrients.SaturatedFatContent,
            'CholesterolContent': prevSummary.CholesterolContent + mealNutrients.CholesterolContent,
            'SodiumContent': prevSummary.SodiumContent + mealNutrients.SodiumContent,
            'CarbohydrateContent': prevSummary.CarbohydrateContent + mealNutrients.CarbohydrateContent,
            'FiberContent': prevSummary.FiberContent + mealNutrients.FiberContent,
            'SugarContent': prevSummary.SugarContent + mealNutrients.SugarContent,
            'ProteinContent': prevSummary.ProteinContent + mealNutrients.ProteinContent,
        }));
        handleNavClick('Daily Goal');
    };

    const handleLogSuggestedMeal = () => {
        if (todayMeal) {
            logMealToDailyGoal(todayMeal); 
        }
    };
    
    const toggleDarkMode = () => setIsDarkMode(prev => !prev);
    const handleNavClick = (page) => { const formattedPage = page.toLowerCase().replace(' ', '-'); window.location.hash = formattedPage; setActivePage(page); };
    const goBackToList = () => { setSelectedRecipe(null); setStatus('Complete! Recipes found from static dataset.'); setImageLoading({}); };
    const chooseMealForToday = () => { if (!selectedRecipe) return; setTodayMeal(selectedRecipe); setSelectedRecipe(null); setStatus(`Meal chosen: ${selectedRecipe.title}. Log it to your Daily Goal!`); };
    const handleImageChange = (event) => { /* existing logic */ };
    const handleTextChange = (event) => { /* existing logic */ };
    const processImage = async () => { /* existing logic */ };
    const fetchRecipeDetails = async (id, title) => { /* existing logic */ };
    const calculateHealthMetrics = () => { /* existing logic */ };

    const themeClasses = {
        bg: 'bg-gray-900', text: 'text-white', container: 'bg-gray-800 border-gray-700 shadow-2xl',
        card: 'bg-gray-700', cardHover: 'hover:bg-gray-600', button: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
        buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700',
        subheading: 'text-purple-300', infoBox: 'bg-gray-700 border-gray-700',
        input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100', nutrients: 'bg-gray-600 text-gray-100',
        nutrientsSubtext: 'text-gray-400', listText: 'text-gray-300', navText: 'text-gray-300 hover:text-white',
        navActive: 'border-b-4 border-purple-400 font-extrabold', bgFilter: 'bg-black/80 backdrop-blur-sm'
    };


    // --- Daily Goal Manual Input Form Component (Simplified to only Food Item) ---
    const ManualMealLogger = ({ logMeal, themeClasses }) => {
        const [mealName, setMealName] = useState('');
        
        // State to hold the retrieved nutrients before logging
        const [retrievedNutrients, setRetrievedNutrients] = useState(null);
        
        const [logError, setLogError] = useState('');
        const [isLookupLoading, setIsLookupLoading] = useState(false);
        const [lastLookupName, setLastLookupName] = useState('');

        const handleLookup = useCallback(async (name) => {
            if (!name) return;
            setLogError('');
            setIsLookupLoading(true);
            setLastLookupName(name);
            setRetrievedNutrients(null); // Clear previous data

            try {
                setLogError('');
                
                const response = await fetch(`${API_URL}/lookup_nutrition`, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ food_name: name }),
                });

                if (!response.ok) {
                    const errorJson = await response.json();
                    setLogError(errorJson.error || `Food item "${name}" not found in dataset.`);
                    return;
                }

                const data = await response.json();
                
                // Store all retrieved fields
                setRetrievedNutrients(data); 
                setLogError(''); 

            } catch (e) {
                setLogError('Network error or server issue during lookup. Ensure Flask server is running.');
            } finally {
                setIsLookupLoading(false);
            }
        }, [API_URL]);

        // Effect to trigger lookup when mealName changes (debounced)
        useEffect(() => {
            if (mealName.trim() && mealName.toLowerCase() !== lastLookupName.toLowerCase()) {
                const timer = setTimeout(() => handleLookup(mealName.trim()), 500); // Debounce by 0.5s
                return () => clearTimeout(timer);
            }
        }, [mealName, handleLookup, lastLookupName]);


        const handleSubmit = (e) => {
            e.preventDefault();
            setLogError('');

            if (!mealName.trim()) {
                setLogError('Please enter a meal name.');
                return;
            }
            if (!retrievedNutrients || retrievedNutrients.calories === undefined || retrievedNutrients.calories === null) {
                 setLogError(`Data lookup failed for "${mealName.trim()}". Please check spelling and try again.`);
                 return;
            }

            // Log the meal using all retrieved data
            logMeal({ 
                name: retrievedNutrients.name, 
                // Pass the data in the same structure as a recipe
                nutrients: {
                    calories: parseFloat(retrievedNutrients.calories),
                    protein: parseFloat(retrievedNutrients.protein),
                    carbohydrates: parseFloat(retrievedNutrients.carbohydrates),
                    fat: parseFloat(retrievedNutrients.fat),
                }
            });
            
            // Clear fields after successful log
            setMealName('');
            setRetrievedNutrients(null);
            setLastLookupName('');
            setStatus('Manual meal logged!');
        };
        
        // Helper to display current nutrition status
        const getNutritionStatusText = () => {
            if (isLookupLoading) {
                return <span className="text-purple-400">Searching dataset...</span>;
            }
            if (retrievedNutrients) {
                return (
                    <div className="flex flex-wrap justify-center space-x-4 font-bold text-sm bg-gray-600/50 p-2 rounded-lg mt-2">
                        <span className="text-green-400"> Data Found:</span>
                        <span className="text-white">{retrievedNutrients.calories} kcal</span>
                        <span className="text-white">{retrievedNutrients.protein}g P</span>
                        <span className="text-white">{retrievedNutrients.carbohydrates}g C</span>
                        <span className="text-white">{retrievedNutrients.fat}g F</span>
                    </div>
                );
            }
            if (mealName.trim() && !logError) {
                 return <span className="text-yellow-400">Type a full name from the dataset (e.g., Eggs, Banana).</span>;
            }
            return <span className={themeClasses.listText}>Type a food item to automatically retrieve nutrition data.</span>;
        }

        return (
            <div className={`p-6 rounded-2xl shadow-xl ${themeClasses.infoBox} transition-all duration-500`}>
                <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Log Meal by Name Lookup</h3>
                <form onSubmit={handleSubmit} className="space-y-4">
                    
                    {/* Meal Name Input (Triggers Lookup) */}
                    <div className="relative">
                        <input
                            type="text"
                            value={mealName}
                            onChange={(e) => {
                                setMealName(e.target.value);
                            }}
                            placeholder="Type Food Item Name (e.g., Eggs, Banana, Oats)"
                            className={`w-full p-3 rounded-lg border-2 ${themeClasses.card} ${themeClasses.text} focus:ring-purple-500 focus:border-purple-500 transition-colors duration-500`}
                        />
                         {isLookupLoading && (
                            <span className="absolute right-3 top-3">
                                <svg className="animate-spin h-5 w-5 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg>
                            </span>
                        )}
                    </div>
                    
                    {/* Nutrition Status Display */}
                    <div className="text-sm font-semibold text-center">
                        {getNutritionStatusText()}
                    </div>

                    <button
                        type="submit"
                        disabled={isLookupLoading || !mealName.trim() || !retrievedNutrients}
                        className={`w-full px-4 py-3 rounded-full text-white font-bold bg-green-600 hover:bg-green-700 transition-transform duration-200 shadow-md disabled:opacity-50`}
                    >
                        Log Meal
                    </button>
                    {logError && <p className="text-sm text-red-400 text-center">{logError}</p>}
                </form>
            </div>
        );
    };

    // --- Render Page Content ---
    const renderHomePageContent = () => {
        if (selectedRecipe) {
            // Detailed Recipe View (with "Choose this Meal" button)
            return (
                <div className="animate-fade-in-up p-4">
                    <div className="flex justify-between items-center mb-6">
                        <button onClick={goBackToList} className={`px-4 py-2 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button} flex items-center`} aria-label="Back to recipe list">
                            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path></svg>
                            Back to Recipes
                        </button>
                        <button onClick={chooseMealForToday} className={`px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 bg-teal-500 hover:bg-teal-600 shadow-lg text-lg flex items-center`}>
                            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a12.01 12.01 0 00-3.997 2.128 12.01 12.01 0 00-2.887 5.922 12.01 12.01 0 00-1.4 6.879m16.175-1.92A12.007 12.007 0 0012 21.056c-2.31 0-4.516-.653-6.425-1.803M12 2h0" /></svg>
                            Choose this Meal
                        </button>
                    </div>
                    <h2 className={`text-4xl font-extrabold text-center mb-6 ${themeClasses.subheading} transition-colors duration-500`}>{selectedRecipe.title}</h2>
                    <div className="grid md:grid-cols-2 gap-8">
                        <div className="flex flex-col items-center">
                            <div className="relative w-full max-w-lg">
                                {false && (<div className="absolute inset-0 flex items-center justify-center bg-gray-800/50 rounded-xl"><svg className="animate-spin h-10 w-10 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg></div>)}
                                <img src={selectedRecipe.image} alt={selectedRecipe.title} className={`w-full h-80 object-cover rounded-2xl shadow-xl transition-opacity duration-500 ${false ? 'opacity-50' : 'opacity-100'}`} onLoad={() => {}} onError={(e) => { e.target.src = `https://placehold.co/400x300/4c4c4c/ffffff?text=Image+Not+Found`; }} />
                            </div>
                            <div className={`p-4 rounded-xl shadow-lg mt-6 w-full max-w-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Nutrition (per serving):</h3>
                                <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                                    <div className={`p-3 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.calories}</span>
                                        <span className={`block text-xs ${themeClasses.nutrientsSubtext}`}>Calories</span>
                                    </div>
                                    <div className={`p-3 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.protein}</span>
                                        <span className={`block text-xs ${themeClasses.nutrientsSubtext}`}>Protein (g)</span>
                                    </div>
                                    <div className={`p-3 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.carbohydrates}</span>
                                        <span className={`block text-xs ${themeClasses.nutrientsSubtext}`}>Carbs (g)</span>
                                    </div>
                                    <div className={`p-3 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.fat}</span>
                                        <span className={`block text-xs ${themeClasses.nutrientsSubtext}`}>Fat (g)</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="space-y-6">
                            <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Ingredients:</h3>
                                <ul className="list-disc list-inside space-y-2">
                                    {selectedRecipe.ingredients.map((ingredient, index) => (<li key={index} className={`pl-2 ${themeClasses.listText}`}>{ingredient}</li>))}
                                </ul>
                            </div>
                            <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Instructions:</h3>
                                <div dangerouslySetInnerHTML={{ __html: selectedRecipe.instructions || 'No instructions available.' }} className={`leading-relaxed space-y-4 ${themeClasses.listText}`} />
                            </div>
                        </div>
                    </div>
                </div>
            );
        }

        // Simplified Main Home Page: Combined Input and Today's Meal Display
        return (
            <div className="flex flex-col items-center w-full">
                <h2 className={`text-4xl font-extrabold text-center mb-10 ${themeClasses.subheading}`}>Scan. Plan , Eat </h2>
                
                {/* --- Today's Chosen Meal --- */}
                {todayMeal && (<div className={`w-full max-w-6xl p-6 mb-8 rounded-2xl shadow-2xl ${themeClasses.infoBox} border-4 border-green-500 transition-all duration-500`}><div className="flex flex-col md:flex-row items-center justify-between"><div className="flex items-center space-x-4"><span className="text-4xl"></span><div><h3 className={`text-2xl font-extrabold text-green-400`}>MEAL SELECTED:</h3><p className={`text-xl font-semibold ${themeClasses.text}`}>{todayMeal.title}</p><p className={`text-sm ${themeClasses.listText}`}>Calories: {todayMeal.nutrients.calories} kcal</p></div></div><button onClick={handleLogSuggestedMeal} className={`mt-4 md:mt-0 px-6 py-3 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 bg-green-600 hover:bg-green-700 shadow-lg text-lg flex items-center`}><svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>LOG & View Daily Goal</button></div></div>)}
                {/* --- End Today's Chosen Meal --- */}

                {/* --- Combined Input Section --- */}
                <div className={`w-full max-w-2xl p-8 rounded-2xl shadow-xl ${themeClasses.infoBox} transition-all duration-500 space-y-6`}>
                    <h3 className={`text-2xl font-bold ${themeClasses.subheading}`}>Find Recipes by Image or Text</h3>
                    
                    {/* File Input */}
                    <div className="w-full">
                        <label className={`block text-md font-medium mb-2 ${themeClasses.listText}`}>1. Scan an ingredient list (Image Upload):</label>
                        <input type="file" onChange={handleImageChange} accept="image/*" capture="environment" className={`block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold ${themeClasses.input} transition-colors duration-500`} />
                        {image && (<p className={`text-xs mt-2 ${themeClasses.listText} truncate`}>**Image Selected:** {image.name}</p>)}
                    </div>
                    
                    <div className="flex items-center w-full">
                        <div className={`flex-grow border-t ${themeClasses.nutrientsSubtext}`}></div>
                        <span className={`px-4 text-sm font-semibold ${themeClasses.listText}`}>OR</span>
                        <div className={`flex-grow border-t ${themeClasses.nutrientsSubtext}`}></div>
                    </div>

                    {/* Text Input */}
                    <div className="w-full">
                        <label htmlFor="ingredient-text" className={`block text-md font-medium mb-2 ${themeClasses.listText}`}>2. Enter ingredients manually (Text Area):</label>
                        <textarea id="ingredient-text" value={ingredientText} onChange={handleTextChange} placeholder="e.g., chicken, broccoli, olive oil, rice" rows="4" className={`w-full p-3 rounded-lg border-2 dark:border-gray-600 ${themeClasses.card} ${themeClasses.text} focus:ring-purple-500 focus:border-purple-500 resize-none transition-colors duration-500`} disabled={!!image} />
                    </div>
                    
                    <button onClick={processImage} disabled={(!image && ingredientText.trim() === '') || status.includes('Processing...') || status.includes('Error.')} className={`w-full px-8 py-3 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-lg text-lg`}>
                        {status.includes('...') ? (<span className="flex items-center justify-center"><svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg>Processing...</span>) : 'Find Recipes'}
                    </button>
                    
                    {errorMessage && <p className="mt-4 text-sm font-semibold text-center text-red-400">{errorMessage}</p>}
                    {!errorMessage && (<p className={`mt-4 text-sm font-semibold text-center ${themeClasses.listText}`}><strong>Status:</strong> <span className={errorMessage ? 'text-red-400' : 'text-green-400'}>{errorMessage || status}</span></p>)}
                </div>
                {/* --- End Combined Input Section --- */}

                {/* --- Extracted Text Display --- */}
                {extractedText && (<div className={`mt-10 p-6 rounded-xl shadow-inner w-full max-w-2xl ${themeClasses.infoBox} transition-colors duration-500`}><h3 className={`text-xl font-bold mb-3 ${themeClasses.subheading}`}>Source Text Used:</h3><pre className={`whitespace-pre-wrap text-base p-4 rounded ${themeClasses.listText} bg-gray-600/50 dark:bg-gray-800/50 border border-dashed border-gray-600`}>{extractedText || 'No text provided.'}</pre></div>)}
                {/* --- Recipe List --- */}
                {recipes.length > 0 && (<div className="recipes-container mt-12 w-full max-w-6xl"><h3 className={`text-3xl font-extrabold text-center mb-6 ${themeClasses.subheading}`}>Suggested Recipes Based on Ingredients </h3><div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">{recipes.map((recipe) => (<div key={recipe.id} className={`p-4 rounded-3xl shadow-xl cursor-pointer transform hover:scale-[1.02] transition-all duration-300 ${themeClasses.card} ${themeClasses.cardHover}`} onClick={() => fetchRecipeDetails(recipe.id, recipe.title)} role="button" tabIndex={0} onKeyDown={(e) => e.key === 'Enter' && fetchRecipeDetails(recipe.id, recipe.title)} aria-label={`View details for ${recipe.title}`}><div className="relative">{false && (<div className="absolute inset-0 flex items-center justify-center bg-gray-800/50 rounded-xl"><svg className="animate-spin h-8 w-8 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg></div>)}<img src={recipe.image} alt={recipe.title} className={`w-full h-48 object-cover rounded-2xl mb-3 transition-opacity duration-500 ${false ? 'opacity-50' : 'opacity-100'}`} onLoad={() => {}} onError={(e) => { e.target.src = `https://placehold.co/400x300/4c4c4c/ffffff?text=Image+Not+Found`; }} /></div><h4 className={`text-xl font-extrabold text-center mt-2 ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>{recipe.title}</h4></div>))}</div></div>)}
                {status.includes('Complete!') && recipes.length === 0 && (<p className={`mt-4 text-center text-lg font-medium ${themeClasses.listText}`}> No recipes found. Try a different image or include more common ingredients!</p>)}
            </div>
        );
    };

    const renderPageContent = () => {
        switch (activePage) {
            case 'Home':
                return renderHomePageContent();
            case 'Daily Goal':
                return (
                    <div className="w-full max-w-4xl mx-auto space-y-8">
                        <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox} transition-colors duration-500`}>
                            <NutritionalChart data={dailyNutritionSummary} colors={chartColors} isDarkMode={isDarkMode} />
                        </div>
                        <ManualMealLogger logMeal={logMealToDailyGoal} themeClasses={themeClasses} />
                        <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox} transition-colors duration-500`}>
                            <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Meals Logged Today ({dailyMealLog.length})</h3>
                            {dailyMealLog.length === 0 ? (
                                <p className={`text-center ${themeClasses.listText}`}>No meals tracked yet. Use the form above or log a recipe from the Home page!</p>
                            ) : (
                                <ul className="space-y-3">
                                    {dailyMealLog.map((meal, index) => (<li key={index} className={`flex justify-between items-center p-3 rounded-lg ${themeClasses.card}`}><div className="flex items-center space-x-3"><span className={`px-2 py-1 text-xs font-bold rounded ${meal.type === 'Recipe' ? 'bg-purple-500 text-white' : 'bg-gray-400 text-gray-900'}`}>{meal.type}</span><span className={`font-semibold ${themeClasses.text}`}>{meal.name}</span></div><div className="text-right"><span className={`text-lg font-extrabold ${themeClasses.subheading}`}>{meal.calories} kcal</span><p className={`text-xs ${themeClasses.nutrientsSubtext}`}>{meal.timestamp}</p></div></li>))}
                                </ul>
                            )}
                        </div>
                    </div>
                );
            case 'Profile': return (
                <div className="flex flex-col items-center p-6 space-y-8 max-w-4xl mx-auto">
                     <h2 className={`text-3xl font-bold ${themeClasses.subheading} transition-colors duration-500`}>My Health Profile</h2>
                        <div className={`w-full mx-auto rounded-3xl shadow-2xl overflow-hidden p-8 space-y-8 ${themeClasses.infoBox} transition-colors duration-500`}>
                        <div className="text-center">
                            <h2 className={`text-2xl font-extrabold ${themeClasses.subheading}`}>Calorie Goal Calculator </h2>
                        </div>
                        <div className="flex justify-center pt-6">
                            <button onClick={calculateHealthMetrics} className={`w-full sm:w-auto px-10 py-3 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg text-lg`}>
                                Calculate My Goals
                            </button>
                        </div>
                        </div>
                </div>
            );
            case 'Weekend Summary': return (<div className="text-center p-6">Weekend Summary Content</div>);
            default: return (<div className="text-center">Page not found.</div>);
        }
    };
    
    // Main App return block
    return (
        <div className={`min-h-screen font-sans flex flex-col items-center p-4 sm:p-8 transition-all duration-500 ease-in-out`} style={{ backgroundImage: 'url("https://images.unsplash.com/photo-1543353071-10c84333b207?q=80&w=2670&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D")', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
            <div className={`absolute inset-0 z-0 ${themeClasses.bgFilter} transition-colors duration-500`}></div>
            <header className={`w-full max-w-7xl flex justify-between items-center mb-10 z-10 transition-colors duration-500 ${themeClasses.text}`}>
                <div className="flex items-center"><svg className="w-8 h-8 mr-2 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5" /></svg><span className="text-2xl font-extrabold tracking-wider">PlanMyPlate</span></div>
                <nav className="hidden md:flex space-x-6 text-lg">{['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => (<button key={page} onClick={() => handleNavClick(page)} className={`px-2 py-1 transition-all duration-300 ${themeClasses.navText} ${activePage === page ? themeClasses.navActive : ''} hover:scale-105`} aria-current={activePage === page ? 'page' : undefined} aria-label={`Maps to ${page}`}>{page}</button>))}</nav>
                <div className="flex space-x-4"><button onClick={toggleDarkMode} className={`p-3 rounded-full font-bold transition-all duration-500 hover:scale-110 ${themeClasses.buttonPrimary} shadow-md text-white`} aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}>{isDarkMode ? '' : ''}</button><button className={`${themeClasses.button} p-3 rounded-full hidden sm:block transition-colors duration-500`} aria-label="Search"><svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg></button><button className={`${themeClasses.button} p-3 rounded-full hidden sm:block transition-colors duration-500`} aria-label="Menu"><svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" /></svg></button></div>
            </header>
            <main className={`w-full max-w-7xl min-h-[70vh] p-8 rounded-3xl z-10 border-t-4 border-b-4 border-purple-500 ${themeClasses.container} transition-all duration-500 ease-in-out`}>{renderPageContent()}</main>
        </div>
    );
};

export default App;
