import React, { useState, useRef, useCallback, useEffect } from 'react';
import axios from 'axios';
import Webcam from 'react-webcam';

// --- Navbar Component (with Logout) ---
const Navbar = ({ setCurrentPage, isLoggedIn, onLogout }) => {
  return (
    <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-white text-3xl font-bold tracking-wide">
          <span className="text-green-200">Plan</span>MyPlate
        </h1>
        <ul className="flex space-x-6">
          <li><button onClick={() => setCurrentPage('home')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Home</button></li>
          <li><button onClick={() => setCurrentPage('scanner')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Grocipes</button></li>
          <li><button onClick={() => setCurrentPage('profile')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Your Profile</button></li>
          {isLoggedIn && (
            <li>
              <button onClick={onLogout} className="text-white bg-red-600 hover:bg-red-700 py-2 px-3 rounded-md font-medium">Logout</button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

// --- Login & Signup Components ---
const Login = ({ onLogin, switchToSignup }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Login</h2>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onLogin(email, password)} className="bg-green-600 text-white py-2 px-4 rounded w-full">Login</button>
      <p className="mt-4 text-center">
        Don't have an account? <button onClick={switchToSignup} className="text-blue-500 hover:underline">Sign Up here</button>
      </p>
    </div>
  );
};

const Signup = ({ onSignup, switchToLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Sign Up</h2>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onSignup(email, password)} className="bg-green-600 text-white py-2 px-4 rounded w-full">Sign Up</button>
      <p className="mt-4 text-center">
        Already have an account? <button onClick={switchToLogin} className="text-blue-500 hover:underline">Login here</button>
      </p>
    </div>
  );
};

// --- ProfileForm, Questionnaire, HomePage, ProfilePage, RecipeScanner ---
// (Keep your existing implementations for these components)

// --- Main App Component with Auth & Logout ---
function App() {
  const [userData, setUserData] = useState(() => {
    const savedData = localStorage.getItem('userData');
    return savedData ? JSON.parse(savedData) : {
      email: null,
      profile: null,
      questionnaire: null,
    };
  });
  const [currentPage, setCurrentPage] = useState('home');
  const [authStep, setAuthStep] = useState('login'); // 'login' | 'signup' | 'profileForm' | 'questionnaire' | 'main'

  useEffect(() => {
    localStorage.setItem('userData', JSON.stringify(userData));
  }, [userData]);

  const handleLogin = (email, password) => {
    // You can add backend authentication here
    setUserData(prev => ({ ...prev, email }));
    setAuthStep('profileForm');
  };

  const handleSignup = (email, password) => {
    // You can add backend registration here
    setUserData(prev => ({ ...prev, email }));
    setAuthStep('profileForm');
  };

  const handleProfileSubmit = (data) => {
    setUserData(prev => ({ ...prev, profile: data }));
    setAuthStep('questionnaire');
  };

  const handleQuestionnaireSubmit = (data) => {
    setUserData(prev => ({ ...prev, questionnaire: data }));
    setAuthStep('main');
    setCurrentPage('home');
  };

  const handleLogout = () => {
    setUserData({ email: null, profile: null, questionnaire: null });
    setAuthStep('login');
    setCurrentPage('home');
    localStorage.removeItem('userData');
  };

  // Render the appropriate component based on authStep and user data
  const renderPage = () => {
    if (!userData.email) {
      if (authStep === 'signup') {
        return <Signup onSignup={handleSignup} switchToLogin={() => setAuthStep('login')} />;
      }
      return <Login onLogin={handleLogin} switchToSignup={() => setAuthStep('signup')} />;
    }
    if (!userData.profile) {
      return <ProfileForm onSubmit={handleProfileSubmit} />;
    }
    if (!userData.questionnaire) {
      return <Questionnaire onSubmit={handleQuestionnaireSubmit} />;
    }

    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'scanner':
        return <RecipeScanner />;
      case 'profile':
        return <ProfilePage userData={userData} />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar setCurrentPage={setCurrentPage} isLoggedIn={!!userData.email} onLogout={handleLogout} />
      <main className="py-8">
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
