import React, { useState } from "react";
import axios from "axios";
import CameraCapture from "./components/CameraCapture";

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState("");
  const [source, setSource] = useState(""); // "upload" or "camera"

  const handleUploadChange = (e) => {
    setImage(e.target.files[0]);
    setSource("upload");
  };

  const handleCameraCapture = (capturedImage) => {
    setImage(capturedImage);
    setSource("camera");
  };

  const handleExtractText = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const res = await axios.post("http://localhost:5000/extract-text", formData);
      setText(res.data.text);
    } catch (err) {
      console.error("Error extracting text:", err);
      alert("Failed to extract text.");
    }
  };

  return (
    <div>
      <h1>OCR App</h1>

      <div>
        <h3>Upload Image</h3>
        <input type="file" accept="image/*" onChange={handleUploadChange} />
      </div>

      <div>
        <h3>Or Capture Image</h3>
        <CameraCapture onCapture={handleCameraCapture} />
      </div>

      <button onClick={handleExtractText}>Extract Text</button>

      {text && (
        <div>
          <h2>Extracted Text:</h2>
          <pre>{text}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
