import React, { useState, useRef, useCallback } from 'react';
import Webcam from 'react-webcam';
// We are simulating axios, but you can add it back for a real backend
// import axios from 'axios';

// The demo data is embedded directly as a JavaScript object.
const demoData = {
  "userProfile": { "userId": "user123", "username": "AlexDoe", "email": "alex.doe@example.com", "age": 30, "height": { "value": 175, "unit": "cm" }, "initialWeight": { "value": 85, "unit": "kg" }, "currentWeight": { "value": 82, "unit": "kg" }, "goalWeight": { "value": 75, "unit": "kg" }, "activityLevel": "moderately_active", "dietaryPreferences": ["low-carb", "high-protein"] },
  "dailyGoals": { "calories": 2000, "protein": 150, "carbohydrates": 100, "fats": 70, "water": 3000 },
  "weightLog": [ { "date": "2025-07-20", "weight": 85 }, { "date": "2025-07-27", "weight": 83.5 }, { "date": "2025-07-31", "weight": 82 } ],
  "foodDatabase": [ { "foodId": "FDC171477", "name": "Chicken Breast, Boneless, Skinless, Raw", "source": "USDA FoodData Central", "servingSize": { "value": 100, "unit": "g" }, "nutrients": { "calories": 112, "protein": 23.1, "carbohydrates": 0, "fats": 1.2, "fiber": 0 } }, { "foodId": "FDC170379", "name": "Broccoli, Raw", "source": "USDA FoodData Central", "servingSize": { "value": 100, "unit": "g" }, "nutrients": { "calories": 34, "protein": 2.82, "carbohydrates": 6.64, "fats": 0.37, "fiber": 2.6 } }, { "foodId": "FDC174825", "name": "Apple, Fuji, Raw", "source": "USDA FoodData Central", "servingSize": { "value": 100, "unit": "g" }, "nutrients": { "calories": 59, "protein": 0.2, "carbohydrates": 14.9, "fats": 0.1, "fiber": 2 } } ]
};

// --- LoginPage Component ---
const LoginPage = ({ onLogin, onNavigateToSignUp }) => { return ( <div className="min-h-screen flex items-center justify-center bg-gray-100"> <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md"> <h1 className="text-center text-4xl font-bold text-green-700 mb-2">Welcome Back!</h1> <p className="text-center text-gray-500 mb-8">Sign in to continue to PlanMyPlate.</p> <div className="space-y-6"> <div> <label className="block text-sm font-medium text-gray-700">Email Address</label> <input type="email" placeholder="you@example.com" className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-green-500 focus:border-green-500" /> </div> <div> <label className="block text-sm font-medium text-gray-700">Password</label> <input type="password" placeholder="••••••••" className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-green-500 focus:border-green-500" /> </div> <button onClick={onLogin} className="w-full py-3 px-4 bg-green-600 hover:bg-green-700 text-white font-semibold rounded-md shadow-md transition duration-300"> Sign In </button> </div> <p className="text-center text-sm text-gray-600 mt-8"> Don't have an account?{' '} <button onClick={onNavigateToSignUp} className="font-medium text-green-600 hover:text-green-500"> Sign Up </button> </p> </div> </div> ); };
// --- SignUpPage Component ---
const SignUpPage = ({ onSignUp, onNavigateToLogin }) => { return ( <div className="min-h-screen flex items-center justify-center bg-gray-100"> <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md"> <h1 className="text-center text-4xl font-bold text-green-700 mb-2">Create Account</h1> <p className="text-center text-gray-500 mb-8">Get started with PlanMyPlate today!</p> <div className="space-y-6"> <div> <label className="block text-sm font-medium text-gray-700">Username</label> <input type="text" placeholder="Your Name" className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-green-500 focus:border-green-500" /> </div> <div> <label className="block text-sm font-medium text-gray-700">Email Address</label> <input type="email" placeholder="you@example.com" className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-green-500 focus:border-green-500" /> </div> <div> <label className="block text-sm font-medium text-gray-700">Password</label> <input type="password" placeholder="••••••••" className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-green-500 focus:border-green-500" /> </div> <button onClick={onSignUp} className="w-full py-3 px-4 bg-green-600 hover:bg-green-700 text-white font-semibold rounded-md shadow-md transition duration-300"> Create Account </button> </div> <p className="text-center text-sm text-gray-600 mt-8"> Already have an account?{' '} <button onClick={onNavigateToLogin} className="font-medium text-green-600 hover:text-green-500"> Sign In </button> </p> </div> </div> ); };
// --- Navbar Component ---
const Navbar = ({ setCurrentPage, onLogout }) => { return ( <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg"> <div className="container mx-auto flex justify-between items-center"> <h1 className="text-white text-3xl font-bold tracking-wide"><span className="text-green-200">Plan</span>MyPlate</h1> <ul className="flex items-center space-x-6"> <li><button onClick={() => setCurrentPage('home')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Home</button></li> <li><button onClick={() => setCurrentPage('scanner')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Grocipes</button></li> <li><button onClick={() => setCurrentPage('food_db')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Food DB</button></li> <li><button onClick={() => setCurrentPage('profile')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Your Profile</button></li> <li><button onClick={onLogout} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-full text-sm transition duration-300">Logout</button></li> </ul> </div> </nav> ); };
// --- HomePage Component ---
const HomePage = () => { return (<div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center"><h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. 🍽️</h2><p className="text-lg text-gray-700 leading-relaxed mb-8">Unlock culinary possibilities with ingredients you already have.</p><div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8"><div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3><p className="text-gray-600">Upload an image of your ingredients and get instant recipe ideas.</p></div><div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3><p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p></div><div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3><p className="text-gray-600">Recipes categorized by meal type for easy browsing.</p></div></div><p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to the "Recipe Scanner"!</p></div>); };
// --- ProfilePage Component ---
const ProfilePage = () => { const [profileData] = useState(demoData); if (!profileData) { return <div className="text-center p-8">Error: Profile data could not be loaded.</div>; } const { userProfile, dailyGoals, weightLog } = profileData; return (<div className="container mx-auto p-8"><div className="bg-white shadow-xl rounded-lg p-8"><div className="flex items-center space-x-6 mb-8"><div className="bg-green-500 text-white w-24 h-24 rounded-full flex items-center justify-center text-4xl font-bold">{userProfile.username.charAt(0).toUpperCase()}</div><div><h2 className="text-4xl font-extrabold text-green-700">{userProfile.username}</h2><p className="text-gray-600">{userProfile.email}</p></div></div><div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 text-center"><div className="bg-green-50 p-4 rounded-lg"><p className="text-gray-500 text-sm">Current Weight</p><p className="text-2xl font-bold text-green-800">{userProfile.currentWeight.value} {userProfile.currentWeight.unit}</p></div><div className="bg-green-50 p-4 rounded-lg"><p className="text-gray-500 text-sm">Goal Weight</p><p className="text-2xl font-bold text-green-800">{userProfile.goalWeight.value} {userProfile.goalWeight.unit}</p></div><div className="bg-green-50 p-4 rounded-lg"><p className="text-gray-500 text-sm">Height</p><p className="text-2xl font-bold text-green-800">{userProfile.height.value} {userProfile.height.unit}</p></div></div><div className="mb-8"><h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Daily Goals</h3><div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-gray-800"><div className="p-2"><strong>Calories:</strong> {dailyGoals.calories} kcal</div><div className="p-2"><strong>Protein:</strong> {dailyGoals.protein}g</div><div className="p-2"><strong>Carbs:</strong> {dailyGoals.carbohydrates}g</div><div className="p-2"><strong>Fats:</strong> {dailyGoals.fats}g</div></div></div><div><h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Weight History</h3><ul className="space-y-2">{weightLog.map((entry, index) => (<li key={index} className="flex justify-between bg-gray-50 p-3 rounded-md"><span className="text-gray-600">{new Date(entry.date).toLocaleDateString()}</span><span className="font-bold text-gray-800">{entry.weight} kg</span></li>))}</ul></div></div></div>); };
// --- FoodDatabasePage Component ---
const FoodDatabasePage = () => { const [foodItems] = useState(demoData.foodDatabase); return (<div className="container mx-auto p-8"><div className="bg-white shadow-xl rounded-lg p-8"><h2 className="text-4xl font-extrabold text-green-700 mb-6 text-center">Food Database 🥕</h2><p className="text-lg text-gray-600 text-center mb-8">Browse nutritional information for various food items sourced from the USDA.</p><div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">{foodItems.map(item => (<div key={item.foodId} className="bg-green-50 p-6 rounded-lg shadow-md border-l-4 border-green-500"><h3 className="text-2xl font-semibold text-green-800 mb-2">{item.name}</h3><p className="text-sm text-gray-500 mb-4">Source: {item.source}</p><div className="space-y-1 text-gray-700"><p><strong>Serving:</strong> {item.servingSize.value}{item.servingSize.unit}</p><p><strong>Calories:</strong> {item.nutrients.calories} kcal</p><p><strong>Protein:</strong> {item.nutrients.protein}g</p><p><strong>Carbs:</strong> {item.nutrients.carbohydrates}g</p><p><strong>Fats:</strong> {item.nutrients.fats}g</p><p><strong>Fiber:</strong> {item.nutrients.fiber}g</p></div></div>))}</div></div></div>); };

// --- RecipeScanner Component (UPDATED to simulate API calls) ---
const RecipeScanner = () => {
  const [imageFile, setImageFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [cameraMode, setCameraMode] = useState(false);
  const webcamRef = useRef(null);
  const [text, setText] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedRecipeDetails, setSelectedRecipeDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isFullRecipeLoading, setIsFullRecipeLoading] = useState(false);

  const base64ToFile = (base64, filename) => {
    const arr = base64.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) { u8arr[n] = bstr.charCodeAt(n); }
    return new File([u8arr], filename, { type: mime });
  };
  
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (imageSrc) {
      const file = base64ToFile(imageSrc, 'capture.jpg');
      setImageFile(file);
      setPreviewUrl(imageSrc);
      setCameraMode(false);
    }
  }, [webcamRef]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // --- SIMULATED API CALL ---
  const handleUpload = async () => {
    if (!imageFile) {
      alert('Please take a photo or upload an image first.');
      return;
    }
    setIsLoading(true);
    setText('');
    setRecipes([]);

    // Simulate a network delay for OCR
    await new Promise(resolve => setTimeout(resolve, 1500));

    const extractedText = "chicken, broccoli, apple, rice";
    setText(extractedText);
    
    // Simulate fetching recipes
    const mockRecipes = [
        { id: 1, name: "Chicken and Broccoli Stir-fry", image: "https://placehold.co/80x80/a3e635/ffffff?text=Recipe", calories: "350 kcal", meal: "Dinner" },
        { id: 2, name: "Grilled Chicken Salad", image: "https://placehold.co/80x80/a3e635/ffffff?text=Recipe", calories: "400 kcal", meal: "Lunch" },
        { id: 3, name: "Apple Cinnamon Rice Pudding", image: "https://placehold.co/80x80/a3e635/ffffff?text=Recipe", calories: "250 kcal", meal: "Dessert" },
    ];
    setRecipes(mockRecipes);
    setIsLoading(false);
  };
  
  // --- SIMULATED API CALL for full details ---
  const fetchFullRecipeDetails = async (recipeName) => {
    setIsFullRecipeLoading(true);
    setShowModal(true);
    
    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    const mockDetails = {
        title: recipeName,
        image: "https://placehold.co/600x400/84cc16/ffffff?text=Delicious+Food",
        servings: 4,
        readyInMinutes: 30,
        nutrition: { nutrients: [ { name: 'Calories', amount: 350, unit: 'kcal' }, { name: 'Protein', amount: 30, unit: 'g' }, { name: 'Carbohydrates', amount: 25, unit: 'g' }, { name: 'Fat', amount: 15, unit: 'g' } ] },
        extendedIngredients: [ { original: '1 lb chicken breast, cut into cubes' }, { original: '2 cups broccoli florets' }, { original: '1 tbsp soy sauce' }, { original: '1 tsp ginger, minced' } ],
        instructions: "<p>1. Cook chicken in a large skillet over medium-high heat.</p><p>2. Add broccoli and stir-fry for 5-7 minutes.</p><p>3. Stir in soy sauce and ginger. Serve over rice.</p>"
    };
    setSelectedRecipeDetails(mockDetails);
    setIsFullRecipeLoading(false);
  };
  
  const closeModal = () => {
    setShowModal(false);
    setSelectedRecipeDetails(null);
  };
  
  const groupRecipesByMealType = (recipesList) => {
    const grouped = { breakfast: [], lunch: [], dinner: [], dessert: [], other: [] };
    recipesList.forEach(recipe => {
      const meal = recipe.meal ? recipe.meal.toLowerCase() : 'other';
      if (grouped[meal]) {
        grouped[meal].push(recipe);
      } else {
        grouped.other.push(recipe);
      }
    });
    return grouped;
  };
  const categorizedRecipes = groupRecipesByMealType(recipes);

  const renderRecipeList = (categoryName, recipesInCategory) => {
    if (!recipesInCategory || recipesInCategory.length === 0) return null;
    return (
      <div key={categoryName} className="mb-8 p-4 bg-gray-50 rounded-lg shadow-md w-full md:w-[calc(50%-1rem)] lg:w-[calc(33.33%-1rem)]">
        <h4 className="text-xl font-semibold text-green-600 border-b-2 border-green-400 pb-2 mb-4">{categoryName.charAt(0).toUpperCase() + categoryName.slice(1)} Recipes</h4>
        <ul className="list-none p-0">{recipesInCategory.map((recipe) => (<li key={recipe.id} onClick={() => fetchFullRecipeDetails(recipe.name)} className="mb-3 bg-white p-4 rounded-lg shadow-sm border-l-4 border-green-500 flex items-center gap-4 cursor-pointer hover:shadow-md"><img src={recipe.image} alt={recipe.name} className="w-20 h-20 rounded-lg object-cover flex-shrink-0" /><div className="flex-grow flex flex-col"><div className="flex justify-between items-start w-full"><strong className="text-green-700 text-lg mr-2">{recipe.name}</strong>{recipe.calories && (<span className="text-gray-600 text-sm font-medium whitespace-nowrap">{recipe.calories}</span>)}</div></div></li>))}</ul>
      </div>
    );
  };
  
  return (
    <div className="container mx-auto p-6 bg-white shadow-xl rounded-lg mt-8">
      <h2 className="text-3xl font-bold text-center text-green-800 mb-6">Recipe Scanner 📸</h2>
      <div className="p-4 bg-green-50 rounded-lg shadow-inner text-center">
        {cameraMode ? (
          <div className="flex flex-col items-center gap-4">
            <Webcam audio={false} ref={webcamRef} screenshotFormat="image/jpeg" className="w-full max-w-lg rounded-lg border-2 border-gray-300" />
            <div className="flex gap-4 mt-4">
              <button onClick={capture} className="py-2 px-6 bg-blue-600 text-white rounded-full font-semibold">Take Photo</button>
              <button onClick={() => setCameraMode(false)} className="py-2 px-6 bg-gray-500 text-white rounded-full font-semibold">Cancel</button>
            </div>
          </div>
        ) : (
          <div className="flex flex-col items-center gap-4">
            <div className="flex gap-4">
              <button onClick={() => setCameraMode(true)} className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold hover:bg-gray-700">Open Camera</button>
              <label className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold cursor-pointer hover:bg-gray-700"><span>Upload File</span><input type="file" accept="image/*" onChange={handleFileChange} className="hidden" /></label>
            </div>
            {previewUrl && (<div className="mt-4"><h4 className="font-semibold text-lg mb-2">Preview:</h4><img src={previewUrl} alt="Selected preview" className="max-w-xs mx-auto rounded-lg shadow-md"/></div>)}
          </div>
        )}
      </div>
      <div className="text-center my-6"><button onClick={handleUpload} disabled={isLoading || !imageFile} className={`py-2 px-8 rounded-full text-white font-semibold transition duration-300 ${isLoading || !imageFile ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'}`}>{isLoading ? 'Analyzing...' : 'Get Recipes'}</button></div>
      {text && (<div className="mb-8"><h3 className="text-2xl font-semibold text-gray-700 mb-3">Detected Text:</h3><pre className="bg-gray-100 p-4 rounded-lg whitespace-pre-wrap">{text}</pre></div>)}
      {recipes.length > 0 && (<div className="mt-8"><h3 className="text-2xl font-semibold text-gray-700 mb-6 text-center">Suggested Recipes ✨</h3><div className="flex flex-wrap justify-center gap-4">{Object.entries(categorizedRecipes).map(([category, list]) => renderRecipeList(category, list))}</div></div>)}
      
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
          <div className="bg-white p-6 md:p-8 rounded-xl shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto relative">
            <button onClick={closeModal} className="absolute top-4 right-4 text-3xl text-gray-400 hover:text-gray-700">&times;</button>
            {isFullRecipeLoading ? (<p className="text-center py-20">Loading details...</p>) : selectedRecipeDetails ? (
              <>
                <h3 className="text-3xl md:text-4xl font-bold text-green-800 mb-4">{selectedRecipeDetails.title}</h3>
                <img src={selectedRecipeDetails.image} alt={selectedRecipeDetails.title} className="w-full h-48 md:h-64 rounded-lg mb-6 object-cover" />
                <div className="flex justify-center text-center gap-8 p-4 bg-green-50 rounded-lg mb-6">
                  <div><p className="text-sm text-gray-600">Servings</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.servings || 'N/A'}</p></div>
                  <div><p className="text-sm text-gray-600">Ready in</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.readyInMinutes ? `${selectedRecipeDetails.readyInMinutes} min` : 'N/A'}</p></div>
                </div>
                {selectedRecipeDetails.nutrition?.nutrients && (
                  <div className="my-6">
                    <h4 className="text-2xl font-semibold text-gray-800 mb-3">Nutritional Information</h4>
                    <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                      {selectedRecipeDetails.nutrition.nutrients.filter(n => ['Calories', 'Carbohydrates', 'Protein', 'Fat'].includes(n.name)).map((nutrient, i) => (
                        <div key={i} className="bg-gray-100 p-3 rounded-lg">
                          <p className="text-sm text-gray-600">{nutrient.name}</p>
                          <p className="font-bold text-lg text-green-800">{nutrient.amount.toFixed(0)}{nutrient.unit}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
                <div className="mt-8 border-t pt-6 grid md:grid-cols-2 md:gap-x-8">
                  <div className="mb-6 md:mb-0">
                    <h4 className="text-2xl font-semibold text-gray-800 mb-3">Ingredients:</h4>
                    <ul className="list-disc pl-5 text-gray-700 space-y-2">
                      {selectedRecipeDetails.extendedIngredients?.map((ing, i) => <li key={i}>{ing.original}</li>)}
                    </ul>
                  </div>
                  <div>
                    <h4 className="text-2xl font-semibold text-gray-800 mb-3">Instructions:</h4>
                    {selectedRecipeDetails.instructions ? (
                      <div className="prose prose-sm md:prose-base max-w-none text-gray-700" dangerouslySetInnerHTML={{ __html: selectedRecipeDetails.instructions }} />
                    ) : (
                      <p className="text-gray-600">No instructions available.</p>
                    )}
                  </div>
                </div>
              </>
            ) : <p className="text-center py-20">Details could not be loaded.</p>}
          </div>
        </div>
      )}
    </div>
  );
};

// --- Main App Component ---
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [authPage, setAuthPage] = useState('login');
  const [currentPage, setCurrentPage] = useState('home');

  const handleLogin = () => { setIsLoggedIn(true); setCurrentPage('home'); };
  const handleLogout = () => { setIsLoggedIn(false); };
  const handleSignUp = () => { setIsLoggedIn(true); setCurrentPage('home'); };

  if (!isLoggedIn) {
    if (authPage === 'login') {
      return <LoginPage onLogin={handleLogin} onNavigateToSignUp={() => setAuthPage('signup')} />;
    } else {
      return <SignUpPage onSignUp={handleSignUp} onNavigateToLogin={() => setAuthPage('login')} />;
    }
  }

  const renderPage = () => {
    switch (currentPage) {
      case 'home': return <HomePage />;
      case 'scanner': return <RecipeScanner />;
      case 'food_db': return <FoodDatabasePage />;
      case 'profile': return <ProfilePage />;
      default: return <HomePage />;
    }
  };

  return (
    <div>
      <Navbar setCurrentPage={setCurrentPage} onLogout={handleLogout} />
      <main className="py-8">{renderPage()}</main>
    </div>
  );
}

export default App;
