import React, { useState, useEffect } from 'react';

// Firebase imports for data persistence
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, getDoc, setDoc, onSnapshot, collection, query, where, getDocs } from 'firebase/firestore';

// Define the PlanMyPlate component
const PlanMyPlate = ({ themeClasses, goToHome }) => (
  <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
    <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>About Plan My Plate</h3>
    <div className="flex flex-col md:flex-row items-center md:items-start space-y-4 md:space-y-0 md:space-x-8">
      <div className="md:w-1/2">
        <p className={`mb-4 ${themeClasses.listText}`}>
          Plan My Plate is a visual guide for building healthy meals. It divides your plate into sections to show the recommended proportions of different food groups.
        </p>
        <ul className={`list-disc list-inside space-y-2 ${themeClasses.listText}`}>
          <li>**Half your plate** should be fruits and vegetables ğŸ¥¦ğŸ“.</li>
          <li>**A quarter of your plate** should be grains ğŸš.</li>
          <li>**The remaining quarter** should be lean proteins ğŸ—.</li>
          <li>**Include a glass of milk or yogurt** and a **small portion of healthy fats** like nuts ğŸ¥›ğŸ¥œ.</li>
        </ul>
      </div>
      <div className="md:w-1/2 flex justify-center">
        <img src="https://www.myplate.gov/sites/default/files/styles/full_width_100/public/2020-12/MyPlate-full-color-graphic-2020.jpg" alt="A visual guide to MyPlate" className="w-full h-auto object-contain max-h-80 rounded-lg shadow-lg" />
      </div>
    </div>
    <button
      onClick={goToHome}
      className={`mt-6 px-4 py-2 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
    >
      Close
    </button>
  </div>
);

// Define the Profile component
const Profile = ({ themeClasses, userId, savedRecipes, goToHome }) => (
  <div className="p-6 rounded-2xl my-6">
    <h2 className={`text-3xl font-bold mb-4 ${themeClasses.subheading}`}>User Profile</h2>
    <div className={`text-center mb-6 ${themeClasses.uidText}`}>
      Your User ID: {userId}
    </div>

    <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Saved Recipes</h3>
    {savedRecipes.length > 0 ? (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 w-full">
        {savedRecipes.map(recipe => (
          <div key={recipe.id} className={themeClasses.savedRecipeCard}>
            <img src={recipe.image} alt={recipe.title} className={themeClasses.savedRecipeImage} />
            <div className={themeClasses.savedRecipeTitle}>{recipe.title}</div>
          </div>
        ))}
      </div>
    ) : (
      <p className={themeClasses.listText}>No recipes saved yet!</p>
    )}

    <div className="flex justify-center mt-8 space-x-4">
      <button
        onClick={goToHome}
        className={`px-6 py-3 rounded-full font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg`}
      >
        Go to Home
      </button>
    </div>
  </div>
);

// Define the DailyGoal component
const DailyGoal = ({ themeClasses, goToHome }) => (
  <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
    <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Your Daily Goal</h3>
    <p className={themeClasses.listText}>
      This page would show your progress towards your daily nutritional goals.
    </p>
    <button
      onClick={goToHome}
      className={`mt-6 px-4 py-2 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
    >
      Close
    </button>
  </div>
);

// Define the AboutUs component
const AboutUs = ({ themeClasses, goToHome }) => (
  <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
    <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>About Us</h3>
    <p className={`mb-4 ${themeClasses.listText}`}>
      Plan My Plate is an app designed to help you make healthier eating choices by scanning ingredients and suggesting balanced recipes. We believe in making nutrition accessible and fun for everyone!
    </p>
    <button
      onClick={goToHome}
      className={`mt-6 px-4 py-2 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
    >
      Close
    </button>
  </div>
);


const App = () => {
  // State variables for the core app functionality
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [status, setStatus] = useState('Idle');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showWelcomeMessage, setShowWelcomeMessage] = useState(true);

  // State for page navigation
  const [currentPage, setCurrentPage] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false); // NEW: State to toggle the mobile menu

  // State variables for user profile and data persistence
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);

  // Firebase app and database instances
  const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
  const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

  // Initialize Firebase and set up auth listener on mount
  useEffect(() => {
    try {
      const app = initializeApp(firebaseConfig);
      const firestore = getFirestore(app);
      const authInstance = getAuth(app);
      setDb(firestore);
      setAuth(authInstance);
    } catch (e) {
      console.error("Firebase initialization failed:", e);
      setErrorMessage("Failed to initialize the app. Please check the configuration.");
      return;
    }
  }, [firebaseConfig]);

  useEffect(() => {
    if (!auth) return;
    const unsub = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setUserId(user.uid);
      } else {
        try {
          // Sign in anonymously if no user is authenticated
          const anonUser = await signInAnonymously(auth);
          setUserId(anonUser.user.uid);
        } catch (error) {
          console.error("Anonymous sign-in failed:", error);
          setErrorMessage("Failed to sign in. Please check your connection.");
        }
      }
      setIsAuthReady(true);
    });

    return () => unsub();
  }, [auth]);

  // Effect to load user's saved recipes from Firestore
  useEffect(() => {
    if (!isAuthReady || !userId || !db) return;

    const userRecipesCollection = collection(db, `artifacts/${appId}/users/${userId}/saved_recipes`);
    const q = query(userRecipesCollection);

    const unsub = onSnapshot(q, (snapshot) => {
      const recipesList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setSavedRecipes(recipesList);
    }, (error) => {
      console.error("Failed to fetch saved recipes:", error);
      setErrorMessage("Failed to load your saved recipes.");
    });

    return () => unsub();
  }, [isAuthReady, userId, db, appId]);

  // Function to save a recipe to Firestore
  const saveRecipe = async (recipe) => {
    if (!userId || !db) {
      setErrorMessage("User not authenticated. Please try again.");
      return;
    }

    try {
      setStatus("Saving recipe...");
      const docRef = doc(db, `artifacts/${appId}/users/${userId}/saved_recipes`, String(recipe.id));
      await setDoc(docRef, recipe, { merge: true });
      setStatus("Recipe saved!");
    } catch (error) {
      console.error("Failed to save recipe:", error);
      setErrorMessage("Failed to save recipe.");
    }
  };

  // Main function to process the image and find recipes
  const processImage = async () => {
    setErrorMessage('');
    if (!image) {
      setErrorMessage('Please select an image first.');
      return;
    }

    setStatus('Uploading and processing...');
    setText('');
    setRecipes([]);
    setSelectedRecipe(null);
    setShowWelcomeMessage(false); // Hide welcome message on scan

    // Placeholder for OCR and recipe generation logic
    try {
      const mockText = "lettuce, tomatoes, cucumber, vinaigrette";
      setText(mockText);

      const mockRecipes = [
        { id: 1, title: "Mock Salad", image: "https://placehold.co/400x200/505050/FFFFFF?text=Mock+Salad" },
        { id: 2, title: "Mock Stir-fry", image: "https://placehold.co/400x200/505050/FFFFFF?text=Mock+Stir-fry" }
      ];
      setRecipes(mockRecipes);
      setStatus('Mock recipes loaded!');
      setCurrentPage('recipes'); // Navigate to the recipes page
    } catch (error) {
      setErrorMessage('An error occurred. Please check the backend server.');
    }
  };

  // Fetch detailed information for a specific recipe
  const fetchRecipeDetails = async (id) => {
    setStatus('Fetching recipe details...');
    setErrorMessage('');
    try {
      const mockDetails = {
        1: { id: 1, title: "Mock Salad", image: "https://placehold.co/600x400/505050/FFFFFF?text=Mock+Salad", nutrients: { calories: 250, protein: 10, carbohydrates: 20, fat: 15 }, ingredients: ["1 head of lettuce", "1 cup cherry tomatoes", "1 cucumber", "1/4 cup vinaigrette"], instructions: "1. Chop lettuce and vegetables. 2. Combine in a bowl. 3. Drizzle with vinaigrette and toss." },
        2: { id: 2, title: "Mock Stir-fry", image: "https://placehold.co/600x400/505050/FFFFFF?text=Mock+Stir-fry", nutrients: { calories: 350, protein: 15, carbohydrates: 40, fat: 18 }, ingredients: ["2 cups broccoli", "1 bell pepper", "1 onion", "soy sauce", "olive oil"], instructions: "1. Heat oil. 2. Add vegetables and stir-fry. 3. Add soy sauce." }
      };
      setSelectedRecipe(mockDetails[id]);
      setStatus('Details loaded!');
      setCurrentPage('recipeDetails'); // Navigate to the recipe details page
    } catch (error) {
      setErrorMessage('Failed to load details. Please try again.');
    }
  };

  // Navigation functions
  const goToHome = () => {
    setCurrentPage('home');
    setSelectedRecipe(null);
    setRecipes([]);
    setErrorMessage('');
    setShowWelcomeMessage(true); // Show welcome message when returning home
    setIsMenuOpen(false); // Close menu
  };
  const goToProfile = () => { setCurrentPage('profile'); setIsMenuOpen(false); };
  const goToDailyGoal = () => { setCurrentPage('dailyGoal'); setIsMenuOpen(false); };
  const goToAboutUs = () => { setCurrentPage('aboutUs'); setIsMenuOpen(false); };

  const goBackToList = () => {
    setCurrentPage('recipes');
    setSelectedRecipe(null);
  };
  const goBackToRecipesFromDetails = () => {
    setSelectedRecipe(null);
    setCurrentPage('recipes');
  };

  const toggleDarkMode = () => setIsDarkMode(prevMode => !prevMode);
  const toggleMenu = () => setIsMenuOpen(prev => !prev);

  // Theme classes for light/dark mode
  const themeClasses = isDarkMode 
    ? {
      bg: 'bg-gray-900',
      text: 'text-white',
      container: 'bg-gray-800 border-gray-700',
      card: 'bg-gray-700',
      cardHover: 'hover:bg-gray-600',
      button: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
      buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700',
      headerText: 'text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500',
      subheading: 'text-purple-300',
      infoBox: 'bg-gray-700 border-gray-700',
      input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100',
      nutrients: 'bg-gray-600 text-gray-100',
      nutrientsSubtext: 'text-gray-400',
      listText: 'text-gray-300',
      planMyPlateButton: 'bg-gradient-to-r from-teal-400 to-green-500 hover:from-teal-500 hover:to-green-600 text-white',
      profileButton: 'bg-gray-700 text-gray-200 hover:bg-gray-600',
      uidText: 'text-sm font-mono text-gray-400 p-2 border border-gray-600 rounded-md',
      profileTitle: 'text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-teal-500',
      savedRecipeCard: 'bg-gray-700 rounded-lg shadow-lg overflow-hidden',
      savedRecipeImage: 'w-full h-48 object-cover',
      savedRecipeTitle: 'p-4 text-xl font-bold text-gray-100',
      navButton: 'px-3 py-2 rounded-lg text-sm font-semibold transition-colors duration-200 hover:bg-gray-700',
      navActiveButton: 'bg-blue-600 text-white hover:bg-blue-700',
      menuButton: 'text-white',
      mobileMenu: 'bg-gray-800',
      mobileNavLink: 'block px-4 py-2 text-sm text-gray-200 hover:bg-gray-700',
    }
    : {
      bg: 'bg-gray-100',
      text: 'text-gray-900',
      container: 'bg-white border-gray-300',
      card: 'bg-gray-200',
      cardHover: 'hover:bg-gray-300',
      button: 'bg-gray-300 text-gray-800 hover:bg-gray-400',
      buttonPrimary: 'bg-gradient-to-r from-indigo-500 to-teal-600 hover:from-indigo-600 hover:to-teal-700',
      headerText: 'text-transparent bg-clip-text bg-gradient-to-r from-indigo-500 to-teal-600',
      subheading: 'text-indigo-500',
      infoBox: 'bg-gray-200 border-gray-300',
      input: 'file:text-indigo-700 file:bg-indigo-50 hover:file:bg-indigo-100',
      nutrients: 'bg-gray-300 text-gray-800',
      nutrientsSubtext: 'text-gray-600',
      listText: 'text-gray-700',
      planMyPlateButton: 'bg-gradient-to-r from-green-400 to-lime-500 hover:from-green-500 hover:to-lime-600 text-white',
      profileButton: 'bg-gray-300 text-gray-800 hover:bg-gray-400',
      uidText: 'text-sm font-mono text-gray-600 p-2 border border-gray-400 rounded-md',
      profileTitle: 'text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-600 to-teal-700',
      savedRecipeCard: 'bg-gray-200 rounded-lg shadow-lg overflow-hidden',
      savedRecipeImage: 'w-full h-48 object-cover',
      savedRecipeTitle: 'p-4 text-xl font-bold text-gray-800',
      navButton: 'px-3 py-2 rounded-lg text-sm font-semibold transition-colors duration-200 hover:bg-gray-200',
      navActiveButton: 'bg-indigo-500 text-white hover:bg-indigo-600',
      menuButton: 'text-gray-900',
      mobileMenu: 'bg-white',
      mobileNavLink: 'block px-4 py-2 text-sm text-gray-800 hover:bg-gray-200',
    };

  // Main render logic based on currentPage state
  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return (
          <div className="flex flex-col items-center">
            {showWelcomeMessage && (
              <div className={`text-center mb-8 ${themeClasses.listText}`}>
                <p>
                  Welcome to Plan My Plate!
                </p>
                <p>
                  Use the scanner below to capture your ingredients.
                </p>
                <p>
                  We'll do the rest to find you delicious recipes.
                </p>
              </div>
            )}
            <div className="w-full text-center">
              <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                <input
                  type="file"
                  onChange={(e) => { handleImageChange(e); setShowWelcomeMessage(false); }}
                  accept="image/*"
                  capture="environment"
                  className={`block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold ${themeClasses.input}`}
                />
                <button
                  onClick={processImage}
                  disabled={!image || status.includes('...') || status === 'Error.'}
                  className={`w-full sm:w-auto px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200
                             disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-lg`}
                >
                  {status.includes('...') ? 'Processing...' : 'Find Recipes'}
                </button>
              </div>
            </div>
            {errorMessage && (
              <p className="mt-4 text-sm font-semibold text-center text-red-400">
                {errorMessage}
              </p>
            )}
            {!errorMessage && (
              <p className={`mt-4 text-sm font-semibold text-center ${themeClasses.listText}`}>
                <strong>Status:</strong> {status}
              </p>
            )}
          </div>
        );
      case 'recipes':
        return (
          <div className="flex flex-col items-center">
            <button
              onClick={goToHome}
              className={`px-4 py-2 mb-4 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
            >
              â† Back to Scanner
            </button>
            <div className="recipes-container mt-8 w-full">
              <h3 className={`text-2xl font-bold text-center mb-4 ${themeClasses.subheading}`}>
                Suggested Recipes ğŸ‘‡
              </h3>
              <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {recipes.length > 0 ? (
                  recipes.map((recipe) => (
                    <div 
                      key={recipe.id}
                      className={`p-4 rounded-2xl shadow-lg cursor-pointer transform hover:scale-105 transition-transform duration-200 ${themeClasses.card} ${themeClasses.cardHover}`} 
                      onClick={() => fetchRecipeDetails(recipe.id)}
                    >
                      <img src={recipe.image} alt={recipe.title} className="w-full h-40 object-cover rounded-xl mb-3" />
                      <h4 className={`text-lg font-semibold text-center ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>{recipe.title}</h4>
                    </div>
                  ))
                ) : (
                  <p className={`mt-4 text-center ${themeClasses.listText}`}>
                    No recipes found. Try a different image or more common ingredients!
                  </p>
                )}
              </div>
            </div>
          </div>
        );
      case 'recipeDetails':
        return (
          <div className="animate-fade-in-up">
            <button
              onClick={goBackToRecipesFromDetails}
              className={`px-4 py-2 mb-4 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
            >
              â† Back to Recipes
            </button>
            <h2 className={`text-3xl font-bold text-center ${themeClasses.subheading}`}>{selectedRecipe.title}</h2>
            <div className="flex justify-center my-4">
              <img
                src={selectedRecipe.image}
                alt={selectedRecipe.title}
                className="w-full max-w-md h-auto object-cover rounded-xl shadow-lg"
              />
            </div>
            
            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
              <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Nutrition (per serving):</h3>
              <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                  <span className="block text-xl font-bold">{selectedRecipe.nutrients.calories}</span>
                  <span className={`block ${themeClasses.nutrientsSubtext}`}>Calories</span>
                </div>
                <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                  <span className="block text-xl font-bold">{selectedRecipe.nutrients.protein}</span>
                  <span className={`block ${themeClasses.nutrientsSubtext}`}>Protein</span>
                </div>
                <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                  <span className="block text-xl font-bold">{selectedRecipe.nutrients.carbohydrates}</span>
                  <span className={`block ${themeClasses.nutrientsSubtext}`}>Carbohydrates</span>
                </div>
                <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                  <span className="block text-xl font-bold">{selectedRecipe.nutrients.fat}</span>
                  <span className={`block ${themeClasses.nutrientsSubtext}`}>Fat</span>
                </div>
              </div>
            </div>

            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
              <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Ingredients:</h3>
              <ul className="list-disc list-inside space-y-2">
                {selectedRecipe.ingredients.map((ingredient, index) => (
                  <li key={index} className={`pl-2 ${themeClasses.listText}`}>{ingredient}</li>
                ))}
              </ul>
            </div>

            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
              <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Instructions:</h3>
              <p className={`leading-relaxed whitespace-pre-wrap ${themeClasses.listText}`}>
                {selectedRecipe.instructions || 'Instructions not available.'}
              </p>
            </div>
            {selectedRecipe.id && (
              <button
                onClick={() => saveRecipe(selectedRecipe)}
                className={`mt-4 px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg`}
              >
                Save Recipe
              </button>
            )}
          </div>
        );
      case 'dailyGoal':
        return <DailyGoal themeClasses={themeClasses} goToHome={goToHome} />;
      case 'aboutUs':
        return <AboutUs themeClasses={themeClasses} goToHome={goToHome} />;
      case 'profile':
        return <Profile themeClasses={themeClasses} userId={userId} savedRecipes={savedRecipes} goToHome={goToHome} />;
      default:
        return null;
    }
  };

  return (
    <div className={`${themeClasses.bg} min-h-screen ${themeClasses.text} font-sans flex flex-col items-center p-4 sm:p-8 transition-colors duration-500`}>
      <script src="https://cdn.tailwindcss.com"></script>
      <header className="w-full max-w-4xl mb-8 relative">
        <div className="flex justify-between items-center">
          <h1 className={`text-4xl sm:text-5xl font-bold ${themeClasses.headerText}`}>
            Plan My Plate ğŸ½ï¸
          </h1>
          <div className="flex space-x-2 items-center">
            <div className="hidden md:flex space-x-2">
              <button
                onClick={goToHome}
                className={`${themeClasses.navButton} ${currentPage === 'home' ? themeClasses.navActiveButton : themeClasses.button}`}
              >
                Home
              </button>
              <button
                onClick={goToProfile}
                className={`${themeClasses.navButton} ${currentPage === 'profile' ? themeClasses.navActiveButton : themeClasses.button}`}
              >
                Profile
              </button>
              <button
                onClick={goToDailyGoal}
                className={`${themeClasses.navButton} ${currentPage === 'dailyGoal' ? themeClasses.navActiveButton : themeClasses.button}`}
              >
                Daily Goal
              </button>
              <button
                onClick={goToAboutUs}
                className={`${themeClasses.navButton} ${currentPage === 'aboutUs' ? themeClasses.navActiveButton : themeClasses.button}`}
              >
                About Us
              </button>
            </div>
            <button
              onClick={toggleDarkMode}
              className={`p-2 rounded-full ${themeClasses.button} shadow-md transition-colors duration-200`}
            >
              {isDarkMode ? 'ğŸŒ' : 'ğŸŒ™'}
            </button>
            <button
              onClick={toggleMenu}
              className={`md:hidden p-2 rounded-full ${themeClasses.button} shadow-md transition-colors duration-200`}
            >
              â˜°
            </button>
          </div>
        </div>
        
        {isMenuOpen && (
          <div className={`md:hidden absolute top-full left-0 w-full z-10 rounded-b-lg shadow-lg ${themeClasses.mobileMenu}`}>
            <button onClick={goToHome} className={themeClasses.mobileNavLink}>Home</button>
            <button onClick={goToProfile} className={themeClasses.mobileNavLink}>Profile</button>
            <button onClick={goToDailyGoal} className={themeClasses.mobileNavLink}>Daily Goal</button>
            <button onClick={goToAboutUs} className={themeClasses.mobileNavLink}>About Us</button>
          </div>
        )}
      </header>

      <main className={`w-full max-w-4xl p-6 rounded-3xl shadow-lg border ${themeClasses.container} transition-colors duration-500`}>
        {renderPage()}
      </main>
    </div>
  );
};

export default App;
