import React, { useState, useEffect } from 'react';

// Main App component for the recipe suggester
const App = () => {
  // State variables to manage the app's UI and data
  const [image, setImage] = useState(null); // Stores the selected image file
  const [extractedText, setExtractedText] = useState(''); // Stores text extracted from the image
  const [status, setStatus] = useState('Select an image to get started.'); // Displays current status to the user
  const [recipes, setRecipes] = useState([]); // Stores the list of suggested recipes
  const [selectedRecipe, setSelectedRecipe] = useState(null); // Stores details for a single selected recipe
  const [errorMessage, setErrorMessage] = useState(''); // Stores any error messages to display
  const [showStatus, setShowStatus] = useState(true); // Controls visibility of the status message

  // Gemini API configuration
  const API_KEY = ""; // Do not change this, Canvas will provide the key at runtime
  const OCR_MODEL = "gemini-2.5-flash-preview-05-20";
  const RECIPE_GENERATION_MODEL = "gemini-2.5-flash-preview-05-20";
  const BASE_API_URL = "https://generativelanguage.googleapis.com/v1beta/models/";

  /**
   * Helper function to handle fetch requests with exponential backoff for retries.
   * This improves robustness against temporary network issues or rate limits.
   * @param {string} url - The URL to fetch from.
   * @param {object} options - Fetch options (method, headers, body, etc.).
   * @param {number} retries - Number of retry attempts.
   */
  const fetchWithBackoff = async (url, options, retries = 3) => {
    for (let i = 0; i < retries; i++) {
      try {
        const response = await fetch(url, options);
        if (response.ok) {
          return response;
        } else if (response.status === 429) {
          const delay = Math.pow(2, i) * 1000 + Math.random() * 1000;
          console.warn(`Rate limit hit. Retrying in ${delay}ms...`);
          await new Promise(res => setTimeout(res, delay));
          continue;
        } else {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
      } catch (error) {
        if (i === retries - 1) throw error;
      }
    }
  };

  /**
   * Handles the file input change event.
   * Resets the app state and displays a preview of the selected image.
   * @param {object} event - The file input change event.
   */
  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      const file = event.target.files[0];
      setImage(file);
      setExtractedText('');
      setRecipes([]);
      setSelectedRecipe(null);
      setErrorMessage('');
      setShowStatus(true);
      setStatus('Image selected. Click "Find Recipes" to analyze.');
    }
  };

  /**
   * Processes the selected image to extract text and then find recipes.
   * This is the main logic function for the application.
   */
  const processImage = async () => {
    setErrorMessage('');
    if (!image) {
      setErrorMessage('Please select an image first.');
      return;
    }

    setRecipes([]);
    setSelectedRecipe(null);
    setStatus('Analyzing image for ingredients...');

    // Convert the image file to a Base64 string for the API
    const reader = new FileReader();
    reader.readAsDataURL(image);
    reader.onload = async () => {
      const base64Data = reader.result.split(',')[1];
      try {
        // Step 1: Use Gemini API to perform OCR (image to text)
        const ocrPayload = {
          contents: [{
            parts: [
              { text: "Describe the text content and objects in this image. Focus on listing any ingredients, food items, or cooking materials." },
              { inlineData: { mimeType: image.type, data: base64Data } }
            ]
          }]
        };

        const ocrUrl = `${BASE_API_URL}${OCR_MODEL}:generateContent?key=${API_KEY}`;
        const ocrResponse = await fetchWithBackoff(ocrUrl, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(ocrPayload)
        });
        const ocrData = await ocrResponse.json();
        const textFromImage = ocrData?.candidates?.[0]?.content?.parts?.[0]?.text || '';
        setExtractedText(textFromImage);

        if (!textFromImage) {
          setStatus('No recognizable text or ingredients found. Please try a different image.');
          setShowStatus(true);
          return;
        }

        // Step 2: Use Gemini API to get a structured list of recipes
        setStatus('Generating recipes based on ingredients...');
        const recipePrompt = `Given the following text, provide a list of 5 recipes. The recipes should be simple and use as many of the listed ingredients as possible.
        
        Text: "${textFromImage}"
        
        Provide the response as a JSON array of objects. Each object should have an "id" (a unique number), "title" (the recipe name), and "image" (a placeholder image URL using https://placehold.co/ and the recipe title). Do not include any other text or markdown.`;

        const recipePayload = {
          contents: [{ parts: [{ text: recipePrompt }] }],
          generationConfig: {
            responseMimeType: "application/json",
            responseSchema: {
              type: "ARRAY",
              items: {
                type: "OBJECT",
                properties: {
                  "id": { "type": "NUMBER" },
                  "title": { "type": "STRING" },
                  "image": { "type": "STRING" },
                }
              }
            }
          }
        };

        const recipeUrl = `${BASE_API_URL}${RECIPE_GENERATION_MODEL}:generateContent?key=${API_KEY}`;
        const recipeResponse = await fetchWithBackoff(recipeUrl, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(recipePayload)
        });
        const recipeData = await recipeResponse.json();
        const recipesJson = JSON.parse(recipeData?.candidates?.[0]?.content?.parts?.[0]?.text || "[]");
        setRecipes(recipesJson);
        setStatus('Recipes generated successfully!');
        setShowStatus(false);

      } catch (error) {
        console.error('API Error:', error);
        setErrorMessage('An error occurred while processing your request. Please try again.');
        setStatus('Error.');
        setShowStatus(true);
      }
    };
  };

  /**
   * Fetches and displays detailed information for a single recipe.
   * @param {object} recipe - The recipe object from the list.
   */
  const fetchRecipeDetails = async (recipe) => {
    setErrorMessage('');
    setStatus('Fetching recipe details...');
    setShowStatus(true);
    try {
      const detailsPrompt = `Provide a detailed recipe for "${recipe.title}". Include the following information in a JSON object:
      - "id": The same ID as the input recipe.
      - "title": The recipe title.
      - "image": The same image URL.
      - "nutrients": An object with "calories", "protein", "carbohydrates", and "fat" (provide approximate values).
      - "ingredients": An array of strings with ingredient names and quantities.
      - "instructions": A detailed, step-by-step string of instructions.
      
      Do not include any other text or markdown outside the JSON object.`;

      const detailsPayload = {
        contents: [{ parts: [{ text: detailsPrompt }] }],
        generationConfig: {
          responseMimeType: "application/json",
          responseSchema: {
            type: "OBJECT",
            properties: {
              "id": { "type": "NUMBER" },
              "title": { "type": "STRING" },
              "image": { "type": "STRING" },
              "nutrients": {
                "type": "OBJECT",
                "properties": {
                  "calories": { "type": "NUMBER" },
                  "protein": { "type": "NUMBER" },
                  "carbohydrates": { "type": "NUMBER" },
                  "fat": { "type": "NUMBER" }
                }
              },
              "ingredients": {
                "type": "ARRAY",
                "items": { "type": "STRING" }
              },
              "instructions": { "type": "STRING" }
            }
          }
        }
      };

      const detailsUrl = `${BASE_API_URL}${RECIPE_GENERATION_MODEL}:generateContent?key=${API_KEY}`;
      const detailsResponse = await fetchWithBackoff(detailsUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(detailsPayload)
      });
      const detailsData = await detailsResponse.json();
      const detailsJson = JSON.parse(detailsData?.candidates?.[0]?.content?.parts?.[0]?.text || "{}");
      setSelectedRecipe(detailsJson);
      setStatus('Details loaded!');
      setShowStatus(false);
    } catch (error) {
      console.error('API Error:', error);
      setErrorMessage('Failed to load recipe details. Please try again.');
      setStatus('Error fetching details.');
      setShowStatus(true);
    }
  };

  /**
   * Resets the view back to the recipe list from the detailed view.
   */
  const goBackToList = () => {
    setSelectedRecipe(null);
    setStatus('Ready to explore more recipes.');
    setShowStatus(false);
  };

  /**
   * Copies the extracted text to the clipboard.
   */
  const copyExtractedText = () => {
    if (extractedText) {
      navigator.clipboard.writeText(extractedText)
        .then(() => {
          setStatus('Extracted text copied!');
          setShowStatus(true);
        })
        .catch(err => {
          console.error('Failed to copy text:', err);
          setErrorMessage('Could not copy text to clipboard.');
        });
    }
  };

  return (
    <div className="bg-gray-900 min-h-screen text-white font-sans flex flex-col items-center p-4 sm:p-8">
      <script src="https://cdn.tailwindcss.com"></script>
      <header className="w-full max-w-4xl text-center mb-8">
        <h1 className="text-4xl sm:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500 rounded-lg">
          OCR & Recipe Suggester
        </h1>
        <p className="mt-2 text-gray-300 text-lg">
          Snap a pic of your ingredients and find out what to cook!
        </p>
      </header>

      {/* Main content area */}
      <main className="w-full max-w-4xl bg-gray-800 p-6 rounded-3xl shadow-lg border border-gray-700">
        {selectedRecipe ? (
          // Display detailed recipe view
          <div className="animate-fade-in-up">
            <button
              onClick={goBackToList}
              className="px-4 py-2 mb-4 text-sm font-semibold text-gray-200 bg-gray-700 rounded-full shadow-md hover:bg-gray-600 transition-colors duration-200"
            >
              ‚Üê Back to Recipes
            </button>
            <h2 className="text-3xl font-bold text-center text-blue-300">{selectedRecipe.title}</h2>
            <div className="flex justify-center my-4">
              <img
                src={selectedRecipe.image}
                alt={selectedRecipe.title}
                className="w-full max-w-md h-auto object-cover rounded-xl shadow-lg"
              />
            </div>
            
            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Nutrition (Approximate per serving):</h3>
              <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                {['calories', 'protein', 'carbohydrates', 'fat'].map((nutrient, index) => (
                  <div key={index} className="bg-gray-600 p-4 rounded-xl shadow-inner">
                    <span className="block text-xl font-bold text-gray-100">{selectedRecipe.nutrients[nutrient] || 'N/A'}</span>
                    <span className="block text-gray-400 capitalize">{nutrient}</span>
                  </div>
                ))}
              </div>
            </div>

            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Ingredients:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-300">
                {selectedRecipe.ingredients?.map((ingredient, index) => (
                  <li key={index} className="pl-2">{ingredient}</li>
                ))}
              </ul>
            </div>

            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Instructions:</h3>
              <p className="text-gray-300 leading-relaxed whitespace-pre-wrap">
                {selectedRecipe.instructions || 'Instructions not available.'}
              </p>
            </div>
          </div>
        ) : (
          // Display initial and recipe list view
          <div className="flex flex-col items-center">
            <div className="w-full text-center">
              <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept="image/*"
                  className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
                <button
                  onClick={processImage}
                  disabled={!image || status.includes('...') || status === 'Error.'}
                  className="w-full sm:w-auto px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200
                             disabled:opacity-50 disabled:cursor-not-allowed bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 shadow-lg"
                >
                  {status.includes('...') ? 'Processing...' : 'Find Recipes'}
                </button>
              </div>
            </div>
            
            {showStatus && (
              <p className={`mt-4 text-sm font-semibold text-center ${errorMessage ? 'text-red-400' : 'text-gray-400'}`}>
                {errorMessage || status}
              </p>
            )}

            {image && (
              <div className="mt-6 p-4 bg-gray-700 rounded-xl shadow-inner w-full flex flex-col items-center">
                <h3 className="text-lg font-semibold text-gray-300">
                  Extracted Ingredients:
                </h3>
                <pre className="text-gray-400 whitespace-pre-wrap text-sm">{extractedText}</pre>
              </div>
            )}
    
            {recipes.length > 0 && (
              <div className="recipes-container mt-8 w-full">
                <h3 className="text-2xl font-bold text-center text-purple-300 mb-4">
                  Suggested Recipes üëá
                </h3>
                <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {recipes.map((recipe) => (
                    <div 
                      key={recipe.id}
                      className="recipe-card bg-gray-700 p-4 rounded-2xl shadow-lg cursor-pointer transform hover:scale-105 transition-transform duration-200" 
                      onClick={() => fetchRecipeDetails(recipe)}
                    >
                      <img src={recipe.image} alt={recipe.title} className="w-full h-40 object-cover rounded-xl mb-3" />
                      <h4 className="text-lg font-semibold text-center text-gray-100">{recipe.title}</h4>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {status === 'Complete!' && recipes.length === 0 && (
              <p className="mt-4 text-gray-400 text-center">
                No recipes found. Try a different image or more common ingredients!
              </p>
            )}
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
