import React, { useState } from 'react';
import axios from 'axios';
const [recipes, setRecipes] = useState([]);


function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
   const [recipes, setRecipes] = useState([]);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await axios.post('http://localhost:5000/extract', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setText(response.data.text);
    } catch (error) {
      console.error('Error:', error);
      alert("Failed to extract text.");
    }
  };
  const suggestRecipes = (text) => {
  const ingredients = text.toLowerCase().split(/[\s,.\n-]+/);
  const uniqueIngredients = new Set(ingredients);

  const possibleRecipes = [
    {
      name: "Egg Sandwich",
      items: ["egg", "bread"],
      meal: "Breakfast"
    },
    {
      name: "Tomato Toast",
      items: ["bread", "tomato"],
      meal: "Breakfast"
    },
    {
      name: "Vegetable Rice",
      items: ["rice", "potato", "tomato"],
      meal: "Lunch"
    },
    {
      name: "Egg Fried Rice",
      items: ["rice", "egg", "tomato"],
      meal: "Lunch"
    },
    {
      name: "Tomato Potato Curry",
      items: ["tomato", "potato"],
      meal: "Dinner"
    },
    {
      name: "Bread & Scrambled Eggs",
      items: ["bread", "egg"],
      meal: "Dinner"
    }
  ];

  const matched = possibleRecipes.filter(recipe =>
    recipe.items.every(item => uniqueIngredients.has(item))
  );

  setRecipes(matched);
};


  return (
    <div style={{ padding: '2rem' }}>
      <h2>OCR Image Upload</h2>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: '1rem' }}>Upload and Extract Text</button>
      <pre style={{ marginTop: '2rem', background: '#f0f0f0', padding: '1rem' }}>{text}</pre>
    </div>
  );
}

export default App;
