import React, { useState } from 'react';

const App = () => {
  // State variables to manage the app's UI and data
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [status, setStatus] = useState('Idle');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  // Handle image file selection
  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setImage(event.target.files[0]);
      setText('');
      setRecipes([]);
      setSelectedRecipe(null);
      setErrorMessage('');
      setStatus('Image selected');
    }
  };

  // Main function to process the image and find recipes
  const processImage = async () => {
    setErrorMessage('');
    if (!image) {
      setErrorMessage('Please select an image first.');
      return;
    }

    setStatus('Uploading and processing...');
    setText('');
    setRecipes([]);
    setSelectedRecipe(null);

    try {
      // Step 1: Upload image for OCR
      const formData = new FormData();
      formData.append('image', image);
      const ocrResponse = await fetch('http://localhost:5000/ocr', {
        method: 'POST',
        body: formData,
      });

      if (!ocrResponse.ok) {
        throw new Error(`OCR failed with status: ${ocrResponse.status}`);
      }
      const ocrData = await ocrResponse.json();
      const extractedText = ocrData.text;
      setText(extractedText);
      
      // Step 2: Format text into a list of ingredients
      // This is a simple splitting, you might need more complex logic for better results
      const ingredients = extractedText.toLowerCase().split(/\s*[\r\n-]\s*/).filter(item => item.trim() !== '');

      if (ingredients.length === 0) {
        setStatus('No ingredients found in the image.');
        return;
      }

      // Step 3: Send ingredients to get a list of recipes
      setStatus('Fetching recipes from Spoonacular...');
      const recipesResponse = await fetch('http://localhost:5000/get_recipes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients }),
      });

      if (!recipesResponse.ok) {
        throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}`);
      }
      const recipesData = await recipesResponse.json();
      setRecipes(recipesData.recipes);
      setStatus('Complete!');

    } catch (error) {
      console.error('API Error:', error);
      setErrorMessage('An error occurred. Please check the backend server.');
      setStatus('An error occurred.');
      setRecipes([]);
    }
  };

  // Fetch detailed information for a specific recipe
  const fetchRecipeDetails = async (id) => {
    setStatus('Fetching recipe details...');
    setErrorMessage('');
    try {
      const response = await fetch(`http://localhost:5000/get_recipe_details/${id}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch recipe details: ${response.status}`);
      }
      const details = await response.json();
      setSelectedRecipe(details);
      setStatus('Details loaded!');
    } catch (error) {
      console.error('Error fetching details:', error);
      setErrorMessage('Failed to load details. Please try again.');
      setStatus('Failed to load details.');
    }
  };
  
  // Go back to the recipe list from the detailed view
  const goBackToList = () => {
    setSelectedRecipe(null);
    setStatus('Complete!');
  };

  return (
    <div className="bg-gray-900 min-h-screen text-white font-sans flex flex-col items-center p-4 sm:p-8">
      <script src="https://cdn.tailwindcss.com"></script>
      <header className="w-full max-w-4xl text-center mb-8">
        <h1 className="text-4xl sm:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500 rounded-lg">
          OCR & Recipe Suggester 📜
        </h1>
        <p className="mt-2 text-gray-300 text-lg">
          Snap a pic of your ingredients and find out what to cook!
        </p>
      </header>

      <main className="w-full max-w-4xl bg-gray-800 p-6 rounded-3xl shadow-lg border border-gray-700">
        {selectedRecipe ? (
          // Display detailed recipe view
          <div className="animate-fade-in-up">
            <button
              onClick={goBackToList}
              className="px-4 py-2 mb-4 text-sm font-semibold text-gray-200 bg-gray-700 rounded-full shadow-md hover:bg-gray-600 transition-colors duration-200"
            >
              ← Back to Recipes
            </button>
            <h2 className="text-3xl font-bold text-center text-blue-300">{selectedRecipe.title}</h2>
            <div className="flex justify-center my-4">
              <img
                src={selectedRecipe.image}
                alt={selectedRecipe.title}
                className="w-full max-w-md h-auto object-cover rounded-xl shadow-lg"
              />
            </div>
            
            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Nutrition (per serving):</h3>
              <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                <div className="bg-gray-600 p-4 rounded-xl shadow-inner">
                  <span className="block text-xl font-bold text-gray-100">{selectedRecipe.nutrients.calories}</span>
                  <span className="block text-gray-400">Calories</span>
                </div>
                <div className="bg-gray-600 p-4 rounded-xl shadow-inner">
                  <span className="block text-xl font-bold text-gray-100">{selectedRecipe.nutrients.protein}</span>
                  <span className="block text-gray-400">Protein</span>
                </div>
                <div className="bg-gray-600 p-4 rounded-xl shadow-inner">
                  <span className="block text-xl font-bold text-gray-100">{selectedRecipe.nutrients.carbohydrates}</span>
                  <span className="block text-gray-400">Carbohydrates</span>
                </div>
                <div className="bg-gray-600 p-4 rounded-xl shadow-inner">
                  <span className="block text-xl font-bold text-gray-100">{selectedRecipe.nutrients.fat}</span>
                  <span className="block text-gray-400">Fat</span>
                </div>
              </div>
            </div>

            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Ingredients:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-300">
                {selectedRecipe.ingredients.map((ingredient, index) => (
                  <li key={index} className="pl-2">{ingredient}</li>
                ))}
              </ul>
            </div>

            <div className="bg-gray-700 p-6 rounded-2xl my-6">
              <h3 className="text-xl font-bold text-purple-300 mb-4">Instructions:</h3>
              <p className="text-gray-300 leading-relaxed whitespace-pre-wrap">
                {selectedRecipe.instructions || 'Instructions not available.'}
              </p>
            </div>
          </div>
        ) : (
          // Display initial and recipe list view
          <div className="flex flex-col items-center">
            <div className="w-full text-center">
              <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept="image/*"
                  className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
                <button
                  onClick={processImage}
                  disabled={!image || status.includes('...') || status === 'Error.'}
                  className="w-full sm:w-auto px-6 py-2 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200
                             disabled:opacity-50 disabled:cursor-not-allowed bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 shadow-lg"
                >
                  {status.includes('...') ? 'Processing...' : 'Find Recipes'}
                </button>
              </div>
            </div>
            
            {/* Status and error messages */}
            {errorMessage && (
              <p className="mt-4 text-sm font-semibold text-center text-red-400">
                {errorMessage}
              </p>
            )}
            {!errorMessage && (
              <p className="mt-4 text-sm font-semibold text-center text-gray-400">
                <strong>Status:</strong> {status}
              </p>
            )}

            {text && (
              <div className="mt-6 p-4 bg-gray-700 rounded-xl shadow-inner w-full">
                <h3 className="text-lg font-semibold text-gray-300 mb-2">
                  Extracted Ingredients:
                </h3>
                <pre className="text-gray-400 whitespace-pre-wrap text-sm">{text}</pre>
              </div>
            )}
    
            {recipes.length > 0 && (
              <div className="recipes-container mt-8 w-full">
                <h3 className="text-2xl font-bold text-center text-purple-300 mb-4">
                  Suggested Recipes 👇
                </h3>
                <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {recipes.map((recipe) => (
                    <div 
                      key={recipe.id}
                      className="recipe-card bg-gray-700 p-4 rounded-2xl shadow-lg cursor-pointer transform hover:scale-105 transition-transform duration-200" 
                      onClick={() => fetchRecipeDetails(recipe.id)}
                    >
                      <img src={recipe.image} alt={recipe.title} className="w-full h-40 object-cover rounded-xl mb-3" />
                      <h4 className="text-lg font-semibold text-center text-gray-100">{recipe.title}</h4>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {status === 'Complete!' && recipes.length === 0 && (
              <p className="mt-4 text-gray-400 text-center">
                No recipes found. Try a different image or more common ingredients!
              </p>
            )}
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
