import React, { useState, useRef, useCallback, useEffect } from 'react';
import axios from 'axios';
import Webcam from 'react-webcam';

// --- Navbar Component (with Logout) ---
const Navbar = ({ setCurrentPage, isLoggedIn, onLogout }) => (
  <nav className="bg-gradient-to-r from-green-600 to-green-800 p-4 shadow-lg rounded-b-lg">
    <div className="container mx-auto flex justify-between items-center">
      <h1 className="text-white text-3xl font-bold tracking-wide">
        <span className="text-green-200">Plan</span>MyPlate
      </h1>
      <ul className="flex space-x-6">
        <li><button onClick={() => setCurrentPage('home')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Home</button></li>
        <li><button onClick={() => setCurrentPage('scanner')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Grocipes</button></li>
        <li><button onClick={() => setCurrentPage('profile')} className="text-white hover:text-green-200 transition duration-300 ease-in-out text-lg font-medium py-2 px-3 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-300">Your Profile</button></li>
        {isLoggedIn && (
          <li>
            <button onClick={onLogout} className="text-white bg-red-600 hover:bg-red-700 py-2 px-3 rounded-md font-medium">Logout</button>
          </li>
        )}
      </ul>
    </div>
  </nav>
);

// --- Login & Signup Components ---
const Login = ({ onLogin, switchToSignup }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Login</h2>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onLogin(email, password)} className="bg-green-600 text-white py-2 px-4 rounded w-full">Login</button>
      <p className="mt-4 text-center">
        Don't have an account? <button onClick={switchToSignup} className="text-blue-500 hover:underline">Sign Up here</button>
      </p>
    </div>
  );
};

const Signup = ({ onSignup, switchToLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Sign Up</h2>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onSignup(email, password)} className="bg-green-600 text-white py-2 px-4 rounded w-full">Sign Up</button>
      <p className="mt-4 text-center">
        Already have an account? <button onClick={switchToLogin} className="text-blue-500 hover:underline">Login here</button>
      </p>
    </div>
  );
};

// --- ProfileForm Component ---
const ProfileForm = ({ onSubmit }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Create Profile</h2>
      <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Age" value={age} onChange={e => setAge(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Weight (kg)" value={weight} onChange={e => setWeight(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <input type="number" placeholder="Height (cm)" value={height} onChange={e => setHeight(e.target.value)} className="mb-3 p-2 w-full border rounded" />
      <button onClick={() => onSubmit({ name, age, weight, height })} className="bg-green-600 text-white py-2 px-4 rounded w-full">Save Profile</button>
    </div>
  );
};

// --- Questionnaire Component ---
const Questionnaire = ({ onSubmit }) => {
  const [problems, setProblems] = useState([]);
  const [needs, setNeeds] = useState([]);
  const problemOptions = ['Diabetes', 'Hypertension', 'Obesity', 'None'];
  const needOptions = ['Weight Loss', 'Healthy Recipes', 'Track Nutrition', 'Other'];
  return (
    <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 max-w-md">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Tell us about you</h2>
      <div className="mb-4">
        <p className="font-semibold mb-2">What health problems do you have?</p>
        {problemOptions.map(opt => (
          <label key={opt} className="block mb-1">
            <input type="checkbox" checked={problems.includes(opt)} onChange={() => setProblems(problems.includes(opt) ? problems.filter(p => p !== opt) : [...problems, opt])} />
            <span className="ml-2">{opt}</span>
          </label>
        ))}
      </div>
      <div className="mb-4">
        <p className="font-semibold mb-2">What do you want from this app?</p>
        {needOptions.map(opt => (
          <label key={opt} className="block mb-1">
            <input type="checkbox" checked={needs.includes(opt)} onChange={() => setNeeds(needs.includes(opt) ? needs.filter(n => n !== opt) : [...needs, opt])} />
            <span className="ml-2">{opt}</span>
          </label>
        ))}
      </div>
      <button onClick={() => onSubmit({ problems, needs })} className="bg-green-600 text-white py-2 px-4 rounded w-full">Continue</button>
    </div>
  );
};

// --- HomePage Component ---
const HomePage = () => (
  <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg mt-8 text-center">
    <h2 className="text-4xl font-extrabold text-green-700 mb-6">Hey there! Welcome to PlanMyPlate. üçΩÔ∏è</h2>
    <p className="text-lg text-gray-700 leading-relaxed mb-8">
      Use your camera or upload a picture of your ingredients, and let us suggest delicious recipes for you.
    </p>
    <div className="flex flex-col md:flex-row justify-center items-center space-y-6 md:space-y-0 md:space-x-8">
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Scan & Discover</h3><p className="text-gray-600">Use your camera or upload an image to get instant recipe ideas.</p></div>
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Nutritional Insights</h3><p className="text-gray-600">View calorie counts and detailed nutrition for each dish.</p></div>
      <div className="bg-green-50 p-6 rounded-lg shadow-md max-w-sm"><h3 className="text-2xl font-semibold text-green-600 mb-3">Organized Meals</h3><p className="text-gray-600">Recipes categorized by meal type for easy Browse.</p></div>
    </div>
    <p className="text-md text-gray-500 mt-10">Start your culinary journey now by navigating to "Grocipes"!</p>
  </div>
);

// --- ProfilePage Component ---
const ProfilePage = ({ userData }) => {
  const { email, profile, questionnaire } = userData;
  if (!profile || !questionnaire) {
    return <div className="text-center p-8 text-gray-600">Please complete your profile to view this page.</div>;
  }
  return (
    <div className="container mx-auto p-8">
      <div className="bg-white shadow-xl rounded-lg p-8">
        <div className="flex items-center space-x-6 mb-8">
          <div className="bg-green-500 text-white w-24 h-24 rounded-full flex items-center justify-center text-4xl font-bold">
            {profile.name.charAt(0).toUpperCase()}
          </div>
          <div>
            <h2 className="text-4xl font-extrabold text-green-700">{profile.name}</h2>
            <p className="text-gray-600">{email}</p>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 text-center">
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Age</p>
            <p className="text-2xl font-bold text-green-800">{profile.age} years</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Weight</p>
            <p className="text-2xl font-bold text-green-800">{profile.weight} kg</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <p className="text-gray-500 text-sm">Height</p>
            <p className="text-2xl font-bold text-green-800">{profile.height} cm</p>
          </div>
        </div>
        <div className="mb-8">
          <h3 className="text-2xl font-semibold text-gray-700 mb-4 border-b pb-2">Health & Goals</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-gray-800">
            <div className="p-2">
              <strong>Health Problems:</strong> {questionnaire.problems.join(', ') || 'None'}
            </div>
            <div className="p-2">
              <strong>App Goals:</strong> {questionnaire.needs.join(', ') || 'N/A'}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- RecipeScanner Component (Placeholder) ---
const RecipeScanner = () => (
  <div className="container mx-auto p-8">
    <div className="bg-white shadow-xl rounded-lg p-8">
      <h2 className="text-2xl font-bold text-green-700 mb-4">Recipe Scanner</h2>
      <p className="text-gray-500">Scanner functionality goes here.</p>
    </div>
  </div>
);

// --- Main App Component with Auth & Logout ---
function App() {
  const [userData, setUserData] = useState(() => {
    const savedData = localStorage.getItem('userData');
    return savedData ? JSON.parse(savedData) : {
      email: null,
      profile: null,
      questionnaire: null,
    };
  });
  const [currentPage, setCurrentPage] = useState('home');
  const [authStep, setAuthStep] = useState('login'); // 'login' | 'signup' | 'profileForm' | 'questionnaire' | 'main'

  useEffect(() => {
    localStorage.setItem('userData', JSON.stringify(userData));
  }, [userData]);

  const handleLogin = (email, password) => {
    setUserData(prev => ({ ...prev, email }));
    setAuthStep('profileForm');
  };

  const handleSignup = (email, password) => {
    setUserData(prev => ({ ...prev, email }));
    setAuthStep('profileForm');
  };

  const handleProfileSubmit = (data) => {
    setUserData(prev => ({ ...prev, profile: data }));
    setAuthStep('questionnaire');
  };

  const handleQuestionnaireSubmit = (data) => {
    setUserData(prev => ({ ...prev, questionnaire: data }));
    setAuthStep('main');
    setCurrentPage('home');
  };

  const handleLogout = () => {
    setUserData({ email: null, profile: null, questionnaire: null });
    setAuthStep('login');
    setCurrentPage('home');
    localStorage.removeItem('userData');
  };

  // Render the appropriate component based on authStep and user data
  const renderPage = () => {
    if (!userData.email) {
      if (authStep === 'signup') {
        return <Signup onSignup={handleSignup} switchToLogin={() => setAuthStep('login')} />;
      }
      return <Login onLogin={handleLogin} switchToSignup={() => setAuthStep('signup')} />;
    }
    if (!userData.profile) {
      return <ProfileForm onSubmit={handleProfileSubmit} />;
    }
    if (!userData.questionnaire) {
      return <Questionnaire onSubmit={handleQuestionnaireSubmit} />;
    }

    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'scanner':
        return <RecipeScanner />;
      case 'profile':
        return <ProfilePage userData={userData} />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar setCurrentPage={setCurrentPage} isLoggedIn={!!userData.email} onLogout={handleLogout} />
      <main className="py-8">
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
