import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from 'recharts';

// Since the external index.css file is causing resolution errors, we are
// embedding the necessary CSS directly into this file using a <style> block.

const GlobalStyles = () => (
    <style jsx="true">{`
        /* * GLOBAL STYLES (MERGED FROM src/index.css) 
         * Includes Tailwind directives and full-screen background setup.
         */
        
        /* Ensures the body is full height for background image */
        body {
            min-height: 100vh;
            margin: 0;
            font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
                'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
                sans-serif;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
        }

        /* Full-screen Background Image Setup applied to the root element (#root) */
        #root {
            min-height: 100vh;
            width: 100%;
            background-image: url('https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=2670&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D');
            background-size: cover;
            background-position: center;
            background-attachment: fixed;
        }

        /* Custom Scrollbar Styling (The 'cool' touch) */
        ::-webkit-scrollbar {
            width: 8px;
        }
        ::-webkit-scrollbar-thumb {
            background-color: #6d28d9; /* Purple-700 (Used for accent) */
            border-radius: 4px;
        }
        ::-webkit-scrollbar-track {
            background-color: #1f2937; /* Gray-800 (Dark background color) */
        }
    `}</style>
);


// Component to render the nutritional donut chart
const NutritionalChart = ({ data, colors, isDarkMode }) => {
    // A simple function to determine the text color based on dark mode
    const getTextColor = () => isDarkMode ? 'white' : 'black';

    // Format the data for the chart, adding a name for the tooltip
    const formattedData = Object.keys(data).map(key => ({
        name: key,
        value: data[key],
    }));

    // Render the chart only if data is available
    if (!formattedData || formattedData.length === 0) {
        return <p className={`text-center ${getTextColor()}`}>No nutritional data available.</p>;
    }

    // A custom label for the center of the chart
    const renderCustomizedLabel = ({ cx, cy }) => {
        return (
            <text x={cx} y={cy} dy={-5} textAnchor="middle" fill={getTextColor()} className="text-xl font-bold">
                Calories
            </text>
        );
    };

    return (
        <div className="flex flex-col items-center">
            <h3 className={`text-xl font-semibold mb-4 ${isDarkMode ? 'text-purple-300' : 'text-indigo-500'}`}>
                Nutritional Values:
            </h3>
            <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                    <Pie
                        data={formattedData}
                        cx="50%"
                        cy="50%"
                        innerRadius={60}
                        outerRadius={100}
                        fill="#8884d8"
                        paddingAngle={5}
                        dataKey="value"
                        labelLine={false}
                        label={renderCustomizedLabel}
                    >
                        {formattedData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                        ))}
                    </Pie>
                    <Tooltip />
                </PieChart>
            </ResponsiveContainer>
        </div>
    );
};

// Placeholder image array for recipes
const FOOD_IMAGE_URLS = [
    'https://placehold.co/400x300/667eea/ffffff?text=Vegetable+Stirfry',
    'https://placehold.co/400x300/4FD1C5/ffffff?text=Chicken+Curry',
    'https://placehold.co/400x300/F6AD55/ffffff?text=Salmon+Fillet',
    'https://placehold.co/400x300/E53E3E/ffffff?text=Pasta+Salad',
    'https://placehold.co/400x300/63B3ED/ffffff?text=Tofu+Bowl',
    'https://placehold.co/400x300/D53F8C/ffffff?text=Lentil+Soup',
];

// Helper function to simulate backend data generation
const generateSimulatedRecipes = () => {
    return Array.from({ length: 6 }).map((_, index) => ({
        id: index + 1,
        title: `Simulated Recipe ${index + 1}`,
        image: FOOD_IMAGE_URLS[index % FOOD_IMAGE_URLS.length],
    }));
};

// Main App component for the diet recommender application.
const App = () => {
    // State variables
    const [image, setImage] = useState(null);
    const [text, setText] = useState('');
    const [status, setStatus] = useState('Idle');
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipe, setSelectedRecipe] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(true);
    const [activePage, setActivePage] = useState('home');
    const [imageLoading, setImageLoading] = useState({});

    // New state variables for the health profile form
    const [age, setAge] = useState(2);
    const [height, setHeight] = useState(50);
    const [weight, setWeight] = useState(10);
    const [gender, setGender] = useState('male');
    const [activity, setActivity] = useState(1);
    const [mealsPerDay, setMealsPerDay] = useState(3);
    const [healthMetrics, setHealthMetrics] = useState(null); // Stores BMR and TDEE
    const [bmi, setBmi] = useState(null); // New state for BMI

    // Existing user profile state
    const [userProfile, setUserProfile] = useState({
        name: 'Paula',
        email: 'paula@zylker.com',
        userId: '630083685',
        dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.',
        weeklyGoal: 'N/A'
    });

    // Fix for ReferenceError: process is not defined
    const API_URL = 'http://localhost:5000';

    // Hardcoded nutritional data for the chart, based on the user's image.
    // In a real application, this would be calculated or fetched from an API.
    const nutritionalData = {
        'Calories': 1238,
        'FatContent': 20,
        'SaturatedFatContent': 5,
        'CholesterolContent': 100,
        'SodiumContent': 500,
        'CarbohydrateContent': 150,
        'FiberContent': 25,
        'SugarContent': 50,
        'ProteinContent': 80,
    };
    const chartColors = [
        '#667eea', // H:236 S:60% I:60%
        '#4FD1C5', // H:174 S:62% I:62%
        '#F6AD55', // H:36 S:66% I:66%
        '#E53E3E', // H:0 S:75% I:57%
        '#63B3ED', // H:208 S:60% I:60%
        '#81E6D9', // H:174 S:50% I:70%
        '#D53F8C', // H:325 S:65% I:57%
        '#ECC94B', // H:46 S:65% I:70%
        '#9F7AEA'  // H:260 S:50% I:60%
    ];

    // Helper function to get the activity level label
    const getActivityLabel = (level) => {
      switch (level) {
        case 1:
          return 'Little/no exercise';
        case 2:
          return 'Lightly active';
        case 3:
          return 'Moderately active';
        case 4:
          return 'Very active';
        case 5:
          return 'Extra active';
        default:
          return 'Unknown';
      }
    };

    // Sync activePage with URL hash
    useEffect(() => {
        const hash = window.location.hash.replace('#', '').toLowerCase() || 'home';
        setActivePage(hash);
    }, []);

    // Handle navigation click and update URL hash
    const handleNavClick = (page) => {
        const formattedPage = page.toLowerCase().replace(' ', '-');
        window.location.hash = formattedPage;
        setActivePage(formattedPage);
    };

    // Handles the file input change event
    const handleImageChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setImage(event.target.files[0]);
            setText('');
            setRecipes([]);
            setSelectedRecipe(null);
            setErrorMessage('');
            setStatus('Image selected');
            setImageLoading({});
        }
    };

    // Main function to process the image and find recipes
    const processImage = async () => {
        setErrorMessage('');
        if (!image) {
            setErrorMessage('Please select an image first.');
            return;
        }

        setStatus('Uploading and processing...');
        setText('Simulating OCR result: 1 cup chopped spinach, 1/2 onion, 2 large eggs, 50g feta cheese.'); // Simulated text
        setRecipes([]);
        setSelectedRecipe(null);
        setImageLoading({});

        try {
            // **SIMULATED API CALLS**
            await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate OCR processing time
            
            // Step 2: Format text into a list of ingredients (Simulated)
            const ingredients = ['spinach', 'onion', 'eggs', 'feta cheese'];
            
            // Step 3: Send ingredients to get a list of recipes (Simulated)
            setStatus('Searching local database for recipes...');
            await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate recipe lookup time

            const simulatedRecipes = generateSimulatedRecipes();
            
            // The backend now consistently returns a 'recipes' key
            setRecipes(simulatedRecipes);
            setImageLoading(simulatedRecipes.reduce((acc, recipe) => ({
                ...acc,
                [recipe.id]: true // Set initial loading state for all new recipe images
            }), {}) || {});
            setStatus('Complete! Found recipes.');

        } catch (error) {
            console.error('API Error:', error);
            setErrorMessage(`Error: Failed to process image or find recipes. Check backend connection at ${API_URL}.`);
            setStatus('An error occurred.');
            setRecipes([]);
            setImageLoading({});
        }
    };

    // Fetch detailed information for a specific recipe (Simulated)
    const fetchRecipeDetails = async (id, title) => {
        setStatus('Fetching recipe details...');
        setErrorMessage('');
        setImageLoading(prev => ({ ...prev, [id]: true }));
        
        await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate loading time

        const simulatedDetails = {
            id: id,
            title: title,
            image: FOOD_IMAGE_URLS[(id - 1) % FOOD_IMAGE_URLS.length],
            nutrients: {
                calories: Math.floor(Math.random() * 500) + 300,
                protein: `${Math.floor(Math.random() * 40) + 10}g`,
                carbohydrates: `${Math.floor(Math.random() * 70) + 20}g`,
                fat: `${Math.floor(Math.random() * 30) + 5}g`,
            },
            ingredients: [
                '1 cup quinoa, cooked',
                '1/2 avocado, diced',
                '1/2 red bell pepper, chopped',
                '1/4 cup corn',
                '1/4 cup black beans',
                'Lime dressing: 1 tbsp olive oil, 1 tbsp lime juice, salt, pepper',
            ],
            instructions: `
                <p><strong>Step 1: Prep Ingredients</strong> - Cook the quinoa according to package instructions. Chop all vegetables.</p>
                <p><strong>Step 2: Make Dressing</strong> - Whisk together the olive oil, lime juice, salt, and pepper in a small bowl.</p>
                <p><strong>Step 3: Combine</strong> - In a large bowl, combine the cooked quinoa, avocado, bell pepper, corn, and black beans. Pour the lime dressing over the mixture.</p>
                <p><strong>Step 4: Serve</strong> - Toss gently to combine and serve immediately or chill for later.</p>
            `,
        };

        setSelectedRecipe(simulatedDetails);
        setStatus('Details loaded!');
        setImageLoading(prev => ({ ...prev, [id]: false }));
    };
    
    // Go back to the recipe list from the detailed view
    const goBackToList = () => {
        setSelectedRecipe(null);
        setStatus('Complete! Found recipes.');
        setImageLoading({});
    };

    // Toggle dark/light mode
    const toggleDarkMode = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    const themeClasses = isDarkMode
        ? {
            // Dark Mode for Content & Background Overlay
            bg: 'bg-black/80', 
            text: 'text-white',
            container: 'bg-gray-800/80 border-gray-700 backdrop-blur-sm', 
            card: 'bg-gray-700/70', 
            cardHover: 'hover:bg-gray-600/90', 
            button: 'bg-gray-700 text-gray-200 hover:bg-gray-600', 
            buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700', 
            subheading: 'text-purple-300', 
            infoBox: 'bg-gray-700/70 border-gray-700 backdrop-blur-sm', 
            nutrients: 'bg-gray-600 text-gray-100', 
            nutrientsSubtext: 'text-gray-400', 
            listText: 'text-gray-300', 
            navText: 'text-gray-300 hover:text-white', 
            navActive: 'border-b-2 border-purple-400', 
          }
        : {
            // Light Mode for Content & Background Overlay
            bg: 'bg-white/80',
            text: 'text-gray-900',
            container: 'bg-white/80 border-gray-300 backdrop-blur-sm',
            card: 'bg-gray-200/80', 
            cardHover: 'hover:bg-gray-300/90', 
            button: 'bg-gray-300 text-gray-800 hover:bg-gray-400', 
            buttonPrimary: 'bg-gradient-to-r from-indigo-500 to-teal-600 hover:from-indigo-600 hover:to-teal-700', 
            subheading: 'text-indigo-500', 
            infoBox: 'bg-gray-200/80 border-gray-300 backdrop-blur-sm', 
            nutrients: 'bg-gray-300 text-gray-800', 
            nutrientsSubtext: 'text-gray-600', 
            listText: 'text-gray-700', 
            navText: 'text-gray-600 hover:text-gray-900', 
            navActive: 'border-b-2 border-indigo-500', 
          };

    // Function to calculate BMR and TDEE based on form inputs
    const calculateHealthMetrics = () => {
        // Validation and Calculation logic remains unchanged
        if (isNaN(age) || isNaN(height) || isNaN(weight) || height <= 0 || weight <= 0) {
            setHealthMetrics({ error: 'Please enter valid numbers for age, height, and weight.' });
            return;
        }

        let bmr;
        if (gender === 'male') {
            bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
        } else {
            bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
        }
        
        const heightInMeters = height / 100;
        const calculatedBmi = weight / (heightInMeters * heightInMeters);
        setBmi(calculatedBmi.toFixed(1));

        const activityMultipliers = {
            1: 1.2,    
            2: 1.375,  
            3: 1.55,   
            4: 1.725,  
            5: 1.9,    
        };
        const activityMultiplier = activityMultipliers[activity] || 1.2;

        const maintainWeight = bmr * activityMultiplier;
        
        setHealthMetrics({
            maintainWeight: maintainWeight.toFixed(0),
            mildWeightLoss: (maintainWeight - 250).toFixed(0),
            weightLoss: (maintainWeight - 500).toFixed(0),
            extremeWeightLoss: (maintainWeight - 750).toFixed(0),
        });

        setUserProfile(prevProfile => ({
            ...prevProfile,
            weeklyGoal: `Your BMR is ${bmr.toFixed(2)} kcal/day, and your TDEE is ${maintainWeight.toFixed(2)} kcal/day. This is a guideline for daily calories to maintain your current weight based on your inputs.`
        }));
    };

    // Render content based on active page
    const renderPageContent = () => {
        switch (activePage) {
            case 'home':
                if (selectedRecipe) {
                    return (
                        <div className="animate-fade-in-up">
                            <button
                                onClick={goBackToList}
                                className={`px-4 py-2 mb-4 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button}`}
                                aria-label="Back to recipe list"
                            >
                                ← Back to Recipes
                            </button>
                            <h2 className={`text-3xl font-bold text-center ${themeClasses.subheading}`}>
                                {selectedRecipe.title}
                            </h2>
                            <div className="flex flex-col items-center my-4">
                                <div className="relative w-full max-w-md">
                                    {imageLoading[selectedRecipe.id] && (
                                        <div className="absolute inset-0 flex items-center justify-center">
                                            <svg className="animate-spin h-8 w-8 text-gray-500" viewBox="0 0 24 24">
                                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" />
                                            </svg>
                                        </div>
                                    )}
                                    <img
                                        src={selectedRecipe.image}
                                        alt={selectedRecipe.title}
                                        className={`w-full h-auto object-cover rounded-xl shadow-lg ${imageLoading[selectedRecipe.id] ? 'opacity-0' : 'opacity-100'}`}
                                        onLoad={() => setImageLoading(prev => ({ ...prev, [selectedRecipe.id]: false }))}
                                        onError={(e) => {
                                            e.target.src = `https://placehold.co/400x300/e2e8f0/888?text=${encodeURIComponent(selectedRecipe.title)}`;
                                            setImageLoading(prev => ({ ...prev, [selectedRecipe.id]: false }));
                                        }}
                                    />
                                </div>
                            </div>
                            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>
                                    Nutrition (per serving):
                                </h3>
                                <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                                    <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.calories}</span>
                                        <span className={`block ${themeClasses.nutrientsSubtext}`}>Calories</span>
                                    </div>
                                    <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.protein}</span>
                                        <span className={`block ${themeClasses.nutrientsSubtext}`}>Protein</span>
                                    </div>
                                    <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.carbohydrates}</span>
                                        <span className={`block ${themeClasses.nutrientsSubtext}`}>Carbohydrates</span>
                                    </div>
                                    <div className={`p-4 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                        <span className="block text-xl font-bold">{selectedRecipe.nutrients.fat}</span>
                                        <span className={`block ${themeClasses.nutrientsSubtext}`}>Fat</span>
                                    </div>
                                </div>
                            </div>
                            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Ingredients:</h3>
                                <ul className="list-disc list-inside space-y-2">
                                    {selectedRecipe.ingredients.map((ingredient, index) => (
                                        <li key={index} className={`pl-2 ${themeClasses.listText}`}>
                                            {ingredient}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                            <div className={`p-6 rounded-2xl my-6 ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Instructions:</h3>
                                <div
                                    className={`leading-relaxed ${themeClasses.listText}`}
                                    dangerouslySetInnerHTML={{ __html: selectedRecipe.instructions || 'Instructions not available.' }}
                                />
                            </div>
                        </div>
                    );
                }
                return (
                    <div className="flex flex-col items-center">
                        {/* Hero Text Section */}
                        <div className="text-center mb-10 p-4">
                            <h1 className="text-4xl sm:text-5xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500 mb-2">
                                Scan. Plan. Eat.
                            </h1>
                            <p className={`text-lg sm:text-xl font-medium ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                                Snap a photo of your fridge, pantry, or grocery list to unlock healthy, personalized meal plans instantly.
                            </p>
                        </div>
                        
                        <div className={`w-full max-w-lg p-6 rounded-2xl border-4 border-dashed border-purple-500/50 ${themeClasses.infoBox} flex flex-col items-center space-y-6 transition-all duration-300 hover:border-purple-500 hover:shadow-2xl hover:shadow-purple-500/20`}>
                            
                            <h2 className={`text-xl font-bold ${themeClasses.subheading}`}>Upload Ingredients Image</h2>
                            
                            <div className="w-full flex flex-col sm:flex-row items-center justify-between space-y-4 sm:space-y-0 sm:space-x-4">
                                {/* Custom styled file input */}
                                <label className={`cursor-pointer flex items-center justify-center w-full sm:w-2/3 px-4 py-3 border-2 border-indigo-400/50 rounded-full text-sm font-semibold transition-all duration-200 ${isDarkMode ? 'text-gray-200 hover:bg-gray-700/50' : 'text-gray-700 hover:bg-gray-100'} ${image ? 'text-green-400 border-green-400' : ''}`}>
                                    {image ? `✅ ${image.name.substring(0, 20)}...` : 'Choose Image or Take a Photo'}
                                    <input
                                        type="file"
                                        onChange={handleImageChange}
                                        accept="image/*"
                                        capture="environment"
                                        className="hidden" // Hides the default input
                                    />
                                </label>

                                <button
                                    onClick={processImage}
                                    disabled={!image || status.includes('...')}
                                    className={`w-full sm:w-1/3 px-6 py-3 rounded-full text-white font-bold transition-all transform hover:scale-105 active:scale-95 duration-200 disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-xl shadow-purple-500/20`}
                                >
                                    {status.includes('...') ? (
                                        <span className="flex items-center justify-center">
                                            <svg className="animate-spin h-5 w-5 mr-2 text-white" viewBox="0 0 24 24">
                                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z"></path>
                                            </svg>
                                            Processing
                                        </span>
                                    ) : 'Find Recipes'}
                                </button>
                            </div>
                            
                            {/* Status and Error Messages */}
                            {errorMessage && <p className="mt-4 text-sm font-semibold text-center text-red-400 animate-pulse">{errorMessage}</p>}
                            {!errorMessage && (
                                <p className={`text-sm font-semibold text-center ${themeClasses.listText}`}>
                                    <strong className={`${themeClasses.subheading}`}>Status:</strong> {status}
                                </p>
                            )}

                        </div>
                        
                        {/* Image Preview and Extracted Text (Simplified display) */}
                        <div className="mt-8 w-full max-w-4xl grid grid-cols-1 md:grid-cols-2 gap-8">
                            {image && (
                                <div className={`p-4 rounded-xl shadow-lg border ${themeClasses.infoBox}`}>
                                    <h3 className={`text-lg font-semibold mb-3 ${themeClasses.subheading}`}>Image Preview:</h3>
                                    <img
                                        src={URL.createObjectURL(image)}
                                        alt="Uploaded preview"
                                        className="w-full max-h-60 object-contain rounded-lg border-2 border-gray-600/50"
                                    />
                                </div>
                            )}
                            {text && (
                                <div className={`p-4 rounded-xl shadow-lg border ${themeClasses.infoBox}`}>
                                    <h3 className={`text-lg font-semibold mb-3 ${themeClasses.subheading}`}>Extracted Text:</h3>
                                    <div className={`h-60 overflow-y-auto p-2 rounded-lg ${isDarkMode ? 'bg-gray-800' : 'bg-white'}`}>
                                        <pre className={`whitespace-pre-wrap text-sm ${themeClasses.listText}`}>
                                            {text || 'No text extracted.'}
                                        </pre>
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Suggested Recipes (Enhanced Cards) */}
                        {recipes.length > 0 && (
                            <div className="recipes-container mt-12 w-full max-w-5xl">
                                <h3 className={`text-3xl font-bold text-center mb-6 ${themeClasses.subheading}`}>
                                    Your Meal Suggestions 🍽️
                                </h3>
                                <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                                    {recipes.map((recipe) => (
                                        <div
                                            key={recipe.id}
                                            className={`group p-4 rounded-2xl shadow-xl border-t-4 border-purple-500/70 cursor-pointer transform hover:scale-[1.02] transition-transform duration-300 ${themeClasses.card} ${themeClasses.cardHover}`}
                                            onClick={() => fetchRecipeDetails(recipe.id, recipe.title)}
                                            role="button"
                                            tabIndex={0}
                                            onKeyDown={(e) => e.key === 'Enter' && fetchRecipeDetails(recipe.id, recipe.title)}
                                            aria-label={`View details for ${recipe.title}`}
                                        >
                                            <div className="relative overflow-hidden rounded-xl">
                                                {imageLoading[recipe.id] && (
                                                    <div className="absolute inset-0 flex items-center justify-center bg-gray-700/80 rounded-xl z-20">
                                                        <svg className="animate-spin h-8 w-8 text-white" viewBox="0 0 24 24">
                                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z"></path>
                                                        </svg>
                                                    </div>
                                                )}
                                                <img
                                                    src={recipe.image}
                                                    alt={recipe.title}
                                                    className={`w-full h-40 object-cover mb-3 transform group-hover:scale-110 transition-transform duration-500 ${imageLoading[recipe.id] ? 'opacity-0' : 'opacity-100'}`}
                                                    onLoad={() => setImageLoading(prev => ({ ...prev, [recipe.id]: false }))}
                                                    onError={(e) => {
                                                        e.target.src = FOOD_IMAGE_URLS[0]; // Fallback to a generic image
                                                        setImageLoading(prev => ({ ...prev, [recipe.id]: false }));
                                                    }}
                                                />
                                            </div>
                                            <h4
                                                className={`text-xl font-bold text-center ${
                                                    isDarkMode ? 'text-purple-300' : 'text-indigo-700'
                                                } mt-2`}
                                            >
                                                {recipe.title}
                                            </h4>
                                            <p className={`text-sm text-center mt-1 ${themeClasses.listText}`}>
                                                Tap to view details & nutrition
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                        {status === 'Complete! Found recipes.' && recipes.length === 0 && (
                            <p className={`mt-8 text-center ${themeClasses.listText} p-4 rounded-lg border border-red-400/50`}>
                                ⚠️ No recipes found. Try a different image or more common ingredients!
                            </p>
                        )}
                    </div>
                );
            case 'daily-goal':
                return <div className="text-center">Daily Goal content goes here.</div>;
            case 'profile':
                return (
                    <div className="flex flex-col items-center p-6 space-y-8">
                        {/* Profile Info Section */}
                        <h2 className={`text-3xl font-bold ${themeClasses.subheading}`}>My Profile</h2>
                        <div className={`flex items-center space-x-6 p-6 rounded-2xl shadow-lg w-full ${themeClasses.infoBox}`}>
                            <img
                                src="https://placehold.co/100x100/A0B5FF/000000?text=P"
                                alt="Profile"
                                className="w-24 h-24 rounded-full border-4 border-purple-400"
                            />
                            <div className="flex flex-col space-y-1">
                                <h3 className="text-2xl font-semibold">{userProfile.name}</h3>
                                <p className={`text-sm ${themeClasses.listText}`}>{userProfile.email}</p>
                                <p className={`text-sm ${themeClasses.listText}`}>User ID: {userProfile.userId}</p>
                            </div>
                        </div>

                        {/* Health Profile Form Section */}
                        <div className={`w-full mx-auto max-w-lg rounded-2xl shadow-xl overflow-hidden p-8 space-y-6 ${themeClasses.infoBox}`}>
                            <div className="text-center">
                                <h2 className={`text-xl font-semibold text-gray-800 dark:text-white ${themeClasses.text}`}>Modify your health values</h2>
                            </div>

                            {/* Age input section */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="age" className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Age</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="age" value={age} onChange={(e) => setAge(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setAge(prevAge => prevAge > 0 ? prevAge - 1 : 0)}>-</button>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setAge(prevAge => prevAge + 1)}>+</button>
                                </div>
                            </div>

                            {/* Height input section */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="height" className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Height(cm)</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="height" value={height} onChange={(e) => setHeight(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setHeight(prevHeight => prevHeight > 0 ? prevHeight - 1 : 0)}>-</button>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setHeight(prevHeight => prevHeight + 1)}>+</button>
                                </div>
                            </div>

                            {/* Weight input section */}
                            <div className="flex items-center justify-between">
                                <label htmlFor="weight" className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Weight(kg)</label>
                                <div className="flex items-center space-x-2">
                                    <input type="number" id="weight" value={weight} onChange={(e) => setWeight(parseInt(e.target.value) || 0)} className="w-20 text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm"/>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setWeight(prevWeight => prevWeight > 0 ? prevWeight - 1 : 0)}>-</button>
                                    <button className="bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200" onClick={() => setWeight(prevWeight => prevWeight + 1)}>+</button>
                                </div>
                            </div>

                            {/* Gender radio buttons section */}
                            <div className="space-y-4 pt-4">
                                <h3 className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Gender</h3>
                                <div className="flex items-center space-x-6">
                                    <label className="flex items-center space-x-2 cursor-pointer">
                                        <input type="radio" name="gender" value="male" checked={gender === 'male'} onChange={() => setGender('male')} className="form-radio text-blue-600" />
                                        <span className={`text-gray-700 dark:text-gray-300 ${themeClasses.text}`}>Male</span>
                                    </label>
                                    <label className="flex items-center space-x-2 cursor-pointer">
                                        <input type="radio" name="gender" value="female" checked={gender === 'female'} onChange={() => setGender('female')} className="form-radio text-blue-600"/>
                                        <span className={`text-gray-700 dark:text-gray-300 ${themeClasses.text}`}>Female</span>
                                    </label>
                                </div>
                            </div>

                            {/* Activity slider section */}
                            <div className="pt-4">
                                <h3 className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Activity</h3>
                                <div className="relative mt-2">
                                    <input type="range" id="activity" min="1" max="5" value={activity} onChange={(e) => setActivity(parseInt(e.target.value))} className="w-full"/>
                                    <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-2">
                                        <span>Little/no exercise</span>
                                        <span>Extra active (very active & physical job)</span>
                                    </div>
                                    <p className={`text-center mt-2 text-sm ${themeClasses.text}`}>Current: {getActivityLabel(activity)}</p>
                                </div>
                            </div>

                            {/* Weight loss plan dropdown section */}
                            <div className="pt-4">
                                <label htmlFor="weight-plan" className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Choose your weight loss plan:</label>
                                <select id="weight-plan" className={`w-full mt-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 dark:text-white focus:ring-blue-500 focus:border-blue-500 p-2 text-sm ${themeClasses.text}`}>
                                    <option>Maintain weight</option>
                                    <option>Mild weight loss</option>
                                    <option>Regular weight loss</option>
                                    <option>Aggressive weight loss</option>
                                </select>
                            </div>

                            {/* Meals per day input section */}
                            <div className="pt-4">
                                <label htmlFor="meals" className={`block text-gray-700 dark:text-gray-300 font-medium ${themeClasses.text}`}>Meals per day</label>
                                <div className="relative mt-2">
                                    <input type="range" id="meals" min="1" max="6" value={mealsPerDay} onChange={(e) => setMealsPerDay(parseInt(e.target.value))} className="w-full"/>
                                    <p className={`text-center mt-2 text-sm ${themeClasses.text}`}>Current: {mealsPerDay} meals</p>
                                </div>
                            </div>

                            {/* Generate button section */}
                            <div className="flex justify-center pt-6">
                                <button onClick={calculateHealthMetrics} className={`bg-blue-600 text-white font-semibold py-3 px-8 rounded-full shadow-md hover:bg-blue-700 transition-colors duration-300`}>
                                    Generate
                                </button>
                            </div>

                            {/* Results display area */}
                            {healthMetrics && (
                                <div className="pt-6 text-center text-gray-800 dark:text-white space-y-6">
                                    {healthMetrics.error ? (
                                        <p className="text-red-500 font-medium">{healthMetrics.error}</p>
                                    ) : (
                                        <>
                                            {/* BMI Section */}
                                            <div className="bg-gray-200 dark:bg-gray-600 rounded-lg p-4 shadow-inner">
                                                <h3 className="text-lg font-bold">BMI CALCULATOR</h3>
                                                <p className="text-2xl font-bold mt-2">{bmi} <span className="text-sm font-normal">kg/m²</span></p>
                                                <p className={`text-red-500 font-bold mt-1`}>{bmi >= 30 ? 'Obesity' : bmi >= 25 ? 'Overweight' : bmi >= 18.5 ? 'Normal' : 'Underweight'}</p>
                                                <p className="text-xs text-gray-500 dark:text-gray-300 mt-1">Healthy BMI range: $18.5 kg/m^2$ - $25 kg/m^2$</p>
                                            </div>

                                            {/* Calorie Calculator Section */}
                                            <div className="bg-gray-200 dark:bg-gray-600 rounded-lg p-4 shadow-inner">
                                                <h3 className="text-lg font-bold">CALORIES CALCULATOR</h3>
                                                <p className="text-sm text-gray-600 dark:text-gray-300 mt-2">
                                                    The results show a number of daily calorie estimates that can be used as a guideline for how many calories to consume each day to maintain, lose, or gain weight at a chosen rate.
                                                </p>
                                                <div className="flex justify-around mt-4 text-center">
                                                    <div>
                                                        <p className="font-semibold text-sm">Maintain weight</p>
                                                        <p className="text-3xl font-bold mt-1">{healthMetrics.maintainWeight} <span className="text-sm font-normal">Calorie</span></p>
                                                        <p className="text-green-500 text-sm mt-1">↓ 0 kg/week</p>
                                                    </div>
                                                    <div>
                                                        <p className="font-semibold text-sm">Mild weight loss</p>
                                                        <p className="text-3xl font-bold mt-1">{healthMetrics.mildWeightLoss} <span className="text-sm font-normal">Calorie</span></p>
                                                        <p className="text-green-500 text-sm mt-1">↓ $0.25$ kg/week</p>
                                                    </div>
                                                    <div>
                                                        <p className="font-semibold text-sm">Weight loss</p>
                                                        <p className="text-3xl font-bold mt-1">{healthMetrics.weightLoss} <span className="text-sm font-normal">Calorie</span></p>
                                                        <p className="text-green-500 text-sm mt-1">↓ $0.5$ kg/week</p>
                                                    </div>
                                                    <div>
                                                        <p className="font-semibold text-sm">Extreme weight loss</p>
                                                        <p className="text-3xl font-bold mt-1">{healthMetrics.extremeWeightLoss} <span className="text-sm font-normal">Calorie</span></p>
                                                        <p className="text-green-500 text-sm mt-1">↓ 1 kg/week</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </>
                                    )}
                                </div>
                            )}

                            {/* Chart Section */}
                            {healthMetrics && (
                                <div className={`w-full p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                    <NutritionalChart data={nutritionalData} colors={chartColors} isDarkMode={isDarkMode} />
                                </div>
                            )}

                            {/* Existing user profile details (kept for context) */}
                            <div className={`w-full p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>My Diet Plan</h3>
                                <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                                    {userProfile.dietPlan}
                                </p>
                            </div>
                            <div className={`w-full p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>My Weekly Goal</h3>
                                <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                                    {userProfile.weeklyGoal}
                                </p>
                            </div>
                            <div className="flex space-x-4 mt-4">
                                <button
                                    className={`px-6 py-2 rounded-full font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg`}
                                    onClick={() => console.log("Simulating sign out...")}
                                >
                                    Sign Out
                                </button>
                            </div>
                        </div>
                    </div>
                );
            case 'weekend-summary':
                return <div className="text-center">Weekend Summary content goes here.</div>;
            default:
                return <div className="text-center">Page not found.</div>;
        }
    };

    return (
        // The main container for the content is now centered and spaced
        <div className={`min-h-screen ${themeClasses.text} font-sans relative`}>
            
            {/* INCLUDE GLOBAL STYLES HERE */}
            <GlobalStyles />
            
            {/* 1. Background Overlay - uses the isDarkMode state for dynamic color */}
            <div className={`absolute inset-0 z-0 ${themeClasses.bg} transition-colors duration-500`}></div>

            {/* 2. Main Content Wrapper - centered and above the overlay */}
            <div className="relative w-full max-w-7xl mx-auto flex flex-col items-center z-10 p-4 sm:p-8">

                <header className="w-full flex justify-between items-center mb-8 px-4 sm:px-0">
                    <div className="flex items-center">
                        <svg
                            className="w-8 h-8 mr-2 text-purple-400" 
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5"
                            />
                        </svg>
                        <span className="text-2xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">PlanMyPlate</span>
                    </div>
                    <nav className="hidden sm:flex space-x-6">
                        {['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => {
                            const formattedPage = page.toLowerCase().replace(' ', '-');
                            return (
                                <button
                                    key={page}
                                    onClick={() => handleNavClick(page)}
                                    className={`px-2 py-1 transition-all duration-300 font-semibold ${themeClasses.navText} ${
                                        activePage === formattedPage ? themeClasses.navActive : ''
                                    }`}
                                    aria-current={activePage === formattedPage ? 'page' : undefined}
                                    aria-label={`Maps to ${page}`}
                                >
                                    {page}
                                </button>
                            );
                        })}
                    </nav>
                    <div className="flex space-x-2">
                        <button 
                            className={`p-2 rounded-full transition-colors duration-200 ${themeClasses.buttonPrimary} text-white`} 
                            aria-label="Search"
                        >
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"/></svg>
                        </button>
                        {/* Profile is visible on mobile too */}
                        <button 
                            onClick={() => handleNavClick('Profile')}
                            className={`p-2 rounded-full transition-colors duration-200 ${themeClasses.buttonPrimary} text-white`} 
                            aria-label="Profile"
                        >
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"/></svg>
                        </button>
                    </div>
                </header>
                
                <main
                    className={`w-full p-6 rounded-3xl shadow-2xl border ${themeClasses.container} transition-colors duration-500`}
                >
                    {renderPageContent()}
                </main>

            </div> {/* End of Main Content Wrapper */}

            <button
                onClick={toggleDarkMode}
                className={`fixed bottom-4 right-4 p-2 rounded-full ${themeClasses.button} shadow-lg transition-colors duration-200 z-50`}
                aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
            >
                {isDarkMode ? '🌞 Light Mode' : '🌙 Dark Mode'}
            </button>
        </div>
    );
};

export default App;
