import React, { useState, useRef, useCallback } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';

const RecipeScanner = () => {
    const [imageFile, setImageFile] = useState(null);
    const [previewUrl, setPreviewUrl] = useState('');
    const [cameraMode, setCameraMode] = useState(false);
    const webcamRef = useRef(null);
    const [text, setText] = useState('');
    const [recipes, setRecipes] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [selectedRecipeDetails, setSelectedRecipeDetails] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [isFullRecipeLoading, setIsFullRecipeLoading] = useState(false);
  
    const base64ToFile = (base64, filename) => {
      const arr = base64.split(',');
      const mime = arr[0].match(/:(.*?);/)[1];
      const bstr = atob(arr[1]);
      let n = bstr.length;
      const u8arr = new Uint8Array(n);
      while (n--) { u8arr[n] = bstr.charCodeAt(n); }
      return new File([u8arr], filename, { type: mime });
    };
    
    const capture = useCallback(() => {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        const file = base64ToFile(imageSrc, 'capture.jpg');
        setImageFile(file);
        setPreviewUrl(imageSrc);
        setCameraMode(false);
      }
    }, [webcamRef]);
  
    const handleFileChange = (e) => {
      const file = e.target.files[0];
      if (file) {
        setImageFile(file);
        setPreviewUrl(URL.createObjectURL(file));
      }
    };
  
    const handleUpload = async () => {
      if (!imageFile) {
        alert('Please take a photo or upload an image first.');
        return;
      }
      setIsLoading(true);
      setText('');
      setRecipes([]);
  
      const formData = new FormData();
      formData.append('image', imageFile);
  
      try {
        // Step 1: Send image to your backend for OCR
        const ocrResponse = await axios.post('http://localhost:5000/extract', formData);
        const extractedText = ocrResponse.data.text;
        setText(extractedText);
        
        // Step 2: Send extracted text to your backend to get recipes from Spoonacular
        const apiResponse = await axios.get(`http://localhost:5000/api/recipes`, {
          params: { ingredients: extractedText }
        });
        setRecipes(apiResponse.data);
  
      } catch (error) {
        console.error('Error processing image:', error);
        alert(error.response?.data?.message || 'Failed to get recipes. Please ensure your backend server is running.');
      } finally {
        setIsLoading(false);
      }
    };
    
    const fetchFullRecipeDetails = async (recipeName) => {
      setIsFullRecipeLoading(true);
      setShowModal(true);
      try {
        // Step 3: Get full recipe details from your backend (which gets it from Spoonacular)
        const response = await axios.get(`http://localhost:5000/api/full-recipe-details`, {
          params: { name: recipeName }
        });
        setSelectedRecipeDetails(response.data);
      } catch (error) {
        console.error("Error fetching full recipe details:", error);
        alert(error.response?.data?.message || 'Failed to fetch details.');
        setShowModal(false);
      } finally {
        setIsFullRecipeLoading(false);
      }
    };
    
    const closeModal = () => {
      setShowModal(false);
      setSelectedRecipeDetails(null);
    };
    
    const groupRecipesByMealType = (recipesList) => {
      const grouped = { breakfast: [], lunch: [], dinner: [], dessert: [], other: [] };
      recipesList.forEach(recipe => {
        const meal = recipe.meal ? recipe.meal.toLowerCase() : 'unknown';
        if (meal.includes('breakfast')) grouped.breakfast.push(recipe);
        else if (meal.includes('lunch') || meal.includes('main course')) grouped.lunch.push(recipe);
        else if (meal.includes('dinner')) grouped.dinner.push(recipe);
        else if (meal.includes('dessert')) grouped.dessert.push(recipe);
        else grouped.other.push(recipe);
      });
      return grouped;
    };
    const categorizedRecipes = groupRecipesByMealType(recipes);
  
    const renderRecipeList = (categoryName, recipesInCategory) => {
      if (!recipesInCategory || recipesInCategory.length === 0) return null;
      return (
        <div key={categoryName} className="mb-8 p-4 bg-gray-50 rounded-lg shadow-md w-full md:w-[calc(50%-1rem)] lg:w-[calc(33.33%-1rem)]">
          <h4 className="text-xl font-semibold text-green-600 border-b-2 border-green-400 pb-2 mb-4">{categoryName.charAt(0).toUpperCase() + categoryName.slice(1)} Recipes</h4>
          <ul className="list-none p-0">{recipesInCategory.map((recipe) => (<li key={recipe.id} onClick={() => fetchFullRecipeDetails(recipe.name)} className="mb-3 bg-white p-4 rounded-lg shadow-sm border-l-4 border-green-500 flex items-center gap-4 cursor-pointer hover:shadow-md"><img src={recipe.image} alt={recipe.name} className="w-20 h-20 rounded-lg object-cover flex-shrink-0" onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/80x80/cccccc/000000?text=No+Img"; }} /><div className="flex-grow flex flex-col"><div className="flex justify-between items-start w-full"><strong className="text-green-700 text-lg mr-2">{recipe.name}</strong>{recipe.calories && recipe.calories !== 'N/A' && (<span className="text-gray-600 text-sm font-medium whitespace-nowrap">{recipe.calories}</span>)}</div></div></li>))}</ul>
        </div>
      );
    };
    
    return (
      <div className="container mx-auto p-6 bg-white shadow-xl rounded-lg mt-8">
        <h2 className="text-3xl font-bold text-center text-green-800 mb-6">Recipe Scanner ðŸ“¸</h2>
        <div className="p-4 bg-green-50 rounded-lg shadow-inner text-center">
          {cameraMode ? (
            <div className="flex flex-col items-center gap-4">
              <Webcam audio={false} ref={webcamRef} screenshotFormat="image/jpeg" className="w-full max-w-lg rounded-lg border-2 border-gray-300" />
              <div className="flex gap-4 mt-4">
                <button onClick={capture} className="py-2 px-6 bg-blue-600 text-white rounded-full font-semibold">Take Photo</button>
                <button onClick={() => setCameraMode(false)} className="py-2 px-6 bg-gray-500 text-white rounded-full font-semibold">Cancel</button>
              </div>
            </div>
          ) : (
            <div className="flex flex-col items-center gap-4">
              <div className="flex gap-4">
                <button onClick={() => setCameraMode(true)} className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold hover:bg-gray-700">Open Camera</button>
                <label className="py-2 px-4 bg-gray-600 text-white rounded-full font-semibold cursor-pointer hover:bg-gray-700"><span>Upload File</span><input type="file" accept="image/*" onChange={handleFileChange} className="hidden" /></label>
              </div>
              {previewUrl && (<div className="mt-4"><h4 className="font-semibold text-lg mb-2">Preview:</h4><img src={previewUrl} alt="Selected preview" className="max-w-xs mx-auto rounded-lg shadow-md"/></div>)}
            </div>
          )}
        </div>
        <div className="text-center my-6"><button onClick={handleUpload} disabled={isLoading || !imageFile} className={`py-2 px-8 rounded-full text-white font-semibold transition duration-300 ${isLoading || !imageFile ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'}`}>{isLoading ? 'Analyzing...' : 'Get Recipes'}</button></div>
        {text && (<div className="mb-8"><h3 className="text-2xl font-semibold text-gray-700 mb-3">Detected Text:</h3><pre className="bg-gray-100 p-4 rounded-lg whitespace-pre-wrap">{text}</pre></div>)}
        {recipes.length > 0 && (<div className="mt-8"><h3 className="text-2xl font-semibold text-gray-700 mb-6 text-center">Suggested Recipes âœ¨</h3><div className="flex flex-wrap justify-center gap-4">{Object.entries(categorizedRecipes).map(([category, list]) => renderRecipeList(category, list))}</div></div>)}
        
        {showModal && (
          <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
            <div className="bg-white p-6 md:p-8 rounded-xl shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto relative">
              <button onClick={closeModal} className="absolute top-4 right-4 text-3xl text-gray-400 hover:text-gray-700">&times;</button>
              {isFullRecipeLoading ? (<p className="text-center py-20">Loading details...</p>) : selectedRecipeDetails ? (
                <>
                  <h3 className="text-3xl md:text-4xl font-bold text-green-800 mb-4">{selectedRecipeDetails.title}</h3>
                  <img src={selectedRecipeDetails.image} alt={selectedRecipeDetails.title} className="w-full h-48 md:h-64 rounded-lg mb-6 object-cover" onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/600x400/cccccc/000000?text=No+Image"; }} />
                  <div className="flex justify-center text-center gap-8 p-4 bg-green-50 rounded-lg mb-6">
                    <div><p className="text-sm text-gray-600">Servings</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.servings || 'N/A'}</p></div>
                    <div><p className="text-sm text-gray-600">Ready in</p><p className="font-bold text-lg text-green-800">{selectedRecipeDetails.readyInMinutes ? `${selectedRecipeDetails.readyInMinutes} min` : 'N/A'}</p></div>
                  </div>
                  {selectedRecipeDetails.nutrition?.nutrients && (
                    <div className="my-6">
                      <h4 className="text-2xl font-semibold text-gray-800 mb-3">Nutritional Information</h4>
                      <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                        {selectedRecipeDetails.nutrition.nutrients.filter(n => ['Calories', 'Carbohydrates', 'Protein', 'Fat'].includes(n.name)).map((nutrient, i) => (
                          <div key={i} className="bg-gray-100 p-3 rounded-lg">
                            <p className="text-sm text-gray-600">{nutrient.name}</p>
                            <p className="font-bold text-lg text-green-800">{nutrient.amount.toFixed(0)}{nutrient.unit}</p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                  <div className="mt-8 border-t pt-6 grid md:grid-cols-2 md:gap-x-8">
                    <div className="mb-6 md:mb-0">
                      <h4 className="text-2xl font-semibold text-gray-800 mb-3">Ingredients:</h4>
                      <ul className="list-disc pl-5 text-gray-700 space-y-2">
                        {selectedRecipeDetails.extendedIngredients?.map((ing, i) => <li key={i}>{ing.original}</li>)}
                      </ul>
                    </div>
                    <div>
                      <h4 className="text-2xl font-semibold text-gray-800 mb-3">Instructions:</h4>
                      {selectedRecipeDetails.instructions ? (
                        <div className="prose prose-sm md:prose-base max-w-none text-gray-700" dangerouslySetInnerHTML={{ __html: selectedRecipeDetails.instructions }} />
                      ) : (
                        <p className="text-gray-600">No instructions available.</p>
                      )}
                    </div>
                  </div>
                </>
              ) : <p className="text-center py-20">Details could not be loaded.</p>}
            </div>
          </div>
        )}
      </div>
    );
};
