import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from 'recharts';

// Component to render the nutritional donut chart
const NutritionalChart = ({ data, colors, isDarkMode }) => {
    // A simple function to determine the text color based on dark mode
    const getTextColor = () => isDarkMode ? 'white' : 'black';

    // Format the data for the chart, adding a name for the tooltip
    const formattedData = Object.keys(data).map(key => ({
        name: key,
        value: data[key],
    }));

    // Render the chart only if data is available
    if (!formattedData || formattedData.length === 0) {
        return <p className={`text-center ${getTextColor()}`}>No nutritional data available.</p>;
    }

    // A custom label for the center of the chart
    const renderCustomizedLabel = ({ cx, cy }) => {
        const totalCalories = data['Calories'] || 'N/A';
        return (
            <text x={cx} y={cy} dy={5} textAnchor="middle" fill={getTextColor()} className="text-sm">
                <tspan x={cx} dy="-0.6em" className="text-xl font-bold">{totalCalories}</tspan>
                <tspan x={cx} dy="1.5em" className="text-sm font-light">CALORIES</tspan>
            </text>
        );
    };

    return (
        <div className="flex flex-col items-center">
            <h3 className={`text-xl font-semibold mb-4 ${isDarkMode ? 'text-purple-300' : 'text-indigo-500'} transition-colors duration-500`}>
                Nutritional Breakdown:
            </h3>
            <div className="flex justify-center items-center w-full h-80">
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={formattedData}
                            cx="50%"
                            cy="50%"
                            innerRadius={70} // Increased radius for a better donut
                            outerRadius={110} // Increased radius for a better donut
                            fill="#8884d8"
                            paddingAngle={2} // Reduced padding for a tighter look
                            dataKey="value"
                            labelLine={false}
                            label={renderCustomizedLabel} // Use the custom label for the center
                            cornerRadius={5} // Added corner radius for a softer look
                        >
                            {formattedData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                            ))}
                        </Pie>
                        <Tooltip
                             contentStyle={{
                                 backgroundColor: isDarkMode ? '#1f2937' : 'white',
                                 borderColor: isDarkMode ? '#4b5563' : '#d1d5db',
                                 borderRadius: '8px',
                                 fontSize: '14px',
                                 color: isDarkMode ? 'white' : 'black',
                                 padding: '10px'
                             }}
                             formatter={(value, name) => [`${value} ${name.includes('Content') ? 'mg' : 'g'}`, name.replace('Content', '')]}
                        />
                    </PieChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

// Main App component for the diet recommender application.
const App = () => {
    // State variables
    const [image, setImage] = useState(null);
    const [text, setText] = useState('');
    const [status, setStatus] = useState('Idle');
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipe, setSelectedRecipe] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(true);
    const [activePage, setActivePage] = useState('home');
    const [imageLoading, setImageLoading] = useState({});

    // New state variables for the health profile form
    const [age, setAge] = useState(30); // Changed default age to a more typical adult value
    const [height, setHeight] = useState(175); // Changed default height to a more typical adult value (cm)
    const [weight, setWeight] = useState(70); // Changed default weight to a more typical adult value (kg)
    const [gender, setGender] = useState('male');
    const [activity, setActivity] = useState(3); // Moderately active
    const [mealsPerDay, setMealsPerDay] = useState(3);
    const [healthMetrics, setHealthMetrics] = useState(null); // Stores BMR and TDEE
    const [bmi, setBmi] = useState(null); // New state for BMI

    // Existing user profile state
    const [userProfile, setUserProfile] = useState({
        name: 'Paula',
        email: 'paula@zylker.com',
        userId: '630083685',
        dietPlan: 'Your custom diet plan will appear here after a recommendation is generated.',
        weeklyGoal: 'N/A'
    });

    // Fix for ReferenceError: process is not defined
    const API_URL = 'http://localhost:5000';

    // Hardcoded nutritional data for the chart, based on the user's image.
    // In a real application, this would be calculated or fetched from an API.
    const nutritionalData = {
        'Calories': 1238,
        'FatContent': 20,
        'SaturatedFatContent': 5,
        'CholesterolContent': 100,
        'SodiumContent': 500,
        'CarbohydrateContent': 150,
        'FiberContent': 25,
        'SugarContent': 50,
        'ProteinContent': 80,
    };
    const chartColors = [
        '#667eea', // Blue-Violet
        '#4FD1C5', // Teal
        '#F6AD55', // Orange
        '#E53E3E', // Red
        '#63B3ED', // Sky Blue
        '#81E6D9', // Light Teal
        '#D53F8C', // Pink
        '#ECC94B', // Yellow
        '#9F7AEA'  // Purple
    ];

    // Helper function to get the activity level label
    const getActivityLabel = (level) => {
      switch (level) {
        case 1:
          return 'Little/no exercise (Sedentary)';
        case 2:
          return 'Lightly active (1-3 days/week)';
        case 3:
          return 'Moderately active (3-5 days/week)';
        case 4:
          return 'Very active (6-7 days/week)';
        case 5:
          return 'Extra active (Very active & physical job)';
        default:
          return 'Unknown';
      }
    };

    // Helper function for BMI categorization
    const getBMICategory = (bmiValue) => {
        if (bmiValue < 18.5) return 'Underweight';
        if (bmiValue >= 18.5 && bmiValue < 25) return 'Normal';
        if (bmiValue >= 25 && bmiValue < 30) return 'Overweight';
        return 'Obesity';
    };

    // Sync activePage with URL hash
    useEffect(() => {
        const hash = window.location.hash.replace('#', '').toLowerCase() || 'home';
        setActivePage(hash.replace('-', ' ')); // Normalize to match navigation labels
    }, []);

    // Handle navigation click and update URL hash
    const handleNavClick = (page) => {
        const formattedPage = page.toLowerCase().replace(' ', '-');
        window.location.hash = formattedPage;
        setActivePage(page);
    };

    // Handles the file input change event
    const handleImageChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setImage(event.target.files[0]);
            setText('');
            setRecipes([]);
            setSelectedRecipe(null);
            setErrorMessage('');
            setStatus(`Image selected: ${event.target.files[0].name}`);
            setImageLoading({});
        }
    };

    // Main function to process the image and find recipes
    const processImage = async () => {
        setErrorMessage('');
        if (!image) {
            setErrorMessage('Please select an image first.');
            return;
        }

        setStatus('Uploading and processing...');
        setText('');
        setRecipes([]);
        setSelectedRecipe(null);
        setImageLoading({});

        try {
            // Step 1: Upload image for OCR
            const formData = new FormData();
            formData.append('image', image);
            console.log("Sending OCR request to:", `${API_URL}/ocr`);

            const ocrResponse = await fetch(`${API_URL}/ocr`, {
                method: 'POST',
                body: formData,
            });

            if (!ocrResponse.ok) {
                const errorText = await ocrResponse.text();
                console.error("OCR Response Error:", ocrResponse.status, errorText);
                throw new Error(`OCR failed with status: ${ocrResponse.status}. Server response: ${errorText}. Please ensure the backend is running and Tesseract is configured.`);
            }
            const ocrData = await ocrResponse.json();
            console.log("OCR Response:", ocrData);

            if (ocrData.error) {
                throw new Error(ocrData.error);
            }
            const extractedText = ocrData.text;
            setText(extractedText || 'No text extracted.');

            // Step 2: Format text into a list of ingredients
            const ingredients = extractedText
                .toLowerCase()
                .split(/\s*[\r\n-]\s*/)
                .map(item => item.replace(/^\d+(\.\d+)?\s*(cups?|tbsp|tsp|oz|g|kg|lb)?\s*/i, '')) // Remove quantities
                .map(item => item.replace(/\b(and|or|with|to|taste)\b/g, '')) // Remove common words
                .filter(item => item.trim() !== '' && item.length > 2);

            if (ingredients.length === 0) {
                setStatus('No valid ingredients found in the image.');
                return;
            }
            console.log("Extracted ingredients for /get_recipes:", ingredients);

            // Step 3: Send ingredients to get a list of recipes
            setStatus('Searching local database for recipes...');
            const recipesResponse = await fetch(`${API_URL}/get_recipes`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ ingredients }),
            });

            if (!recipesResponse.ok) {
                const errorText = await recipesResponse.text();
                console.error("Recipes Response Error:", recipesResponse.status, errorText);
                throw new Error(`Recipe lookup failed with status: ${recipesResponse.status}. Server response: ${errorText || 'No additional details'}`);
            }
            const recipesData = await recipesResponse.json();
            console.log("Recipes Response:", recipesData);
            
            // The backend now consistently returns a 'recipes' key
            setRecipes(recipesData.recipes || []);
            setImageLoading(recipesData.recipes?.reduce((acc, recipe) => ({
                ...acc,
                [recipe.id]: true
            }), {}) || {});
            setStatus('Complete! Recipes found.');

        } catch (error) {
            console.error('API Error:', error);
            setErrorMessage(error.message.includes('Failed to fetch')
                ? `Cannot connect to the backend server. Please ensure it is running at ${API_URL}.`
                : `Error: ${error.message}`);
            setStatus('An error occurred.');
            setRecipes([]);
            setImageLoading({});
        }
    };

    // Fetch detailed information for a specific recipe
    const fetchRecipeDetails = async (id, title) => {
        setStatus('Fetching recipe details...');
        setErrorMessage('');
        setImageLoading(prev => ({ ...prev, [id]: true }));
        try {
            const response = await fetch(`${API_URL}/get_recipe_details/${id}`);
            if (!response.ok) {
                throw new Error(`Failed to fetch recipe details: ${response.status}`);
            }
            const details = await response.json();
            setSelectedRecipe(details);
            setStatus(`Viewing: ${details.title}`);
        } catch (error) {
            console.error('Error fetching details:', error);
            setErrorMessage('Failed to load details. Please try again.');
            setStatus('Failed to load details.');
        } finally {
            setImageLoading(prev => ({ ...prev, [id]: false }));
        }
    };
    
    // Go back to the recipe list from the detailed view
    const goBackToList = () => {
        setSelectedRecipe(null);
        setStatus('Complete! Recipes found.');
        setImageLoading({});
    };

    // Toggle dark/light mode
    const toggleDarkMode = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    const themeClasses = isDarkMode
        ? {
            bg: 'bg-gray-900', // H:240 S:11% I:11%
            text: 'text-white', // H:0 S:0% I:100%
            container: 'bg-gray-800 border-gray-700 shadow-2xl', // H:240 S:11% I:16%
            card: 'bg-gray-700', // H:240 S:11% I:21%
            cardHover: 'hover:bg-gray-600', // H:240 S:11% I:25%
            button: 'bg-gray-700 text-gray-200 hover:bg-gray-600', // H:240 S:11% I:21%
            buttonPrimary: 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700', // from H:208 S:60% I:60% to H:260 S:50% I:60%
            subheading: 'text-purple-300', // H:260 S:32% I:76%
            infoBox: 'bg-gray-700 border-gray-700', // H:240 S:11% I:21%
            input: 'file:text-blue-700 file:bg-blue-50 hover:file:bg-blue-100', // from H:208 S:60% I:60% to H:208 S:60% I:60%
            nutrients: 'bg-gray-600 text-gray-100', // H:240 S:11% I:25%
            nutrientsSubtext: 'text-gray-400', // H:240 S:11% I:43%
            listText: 'text-gray-300', // H:240 S:11% I:60%
            navText: 'text-gray-300 hover:text-white', // H:240 S:11% I:60%
            navActive: 'border-b-2 border-purple-400 font-bold', // H:260 S:32% I:76%
            bgFilter: 'bg-black/70 backdrop-blur-sm', // Dark overlay for background image
          }
        : {
            bg: 'bg-gray-100', // H:0 S:0% I:90%
            text: 'text-gray-900', // H:0 S:0% I:10%
            container: 'bg-white border-gray-300 shadow-xl', // H:0 S:0% I:100%
            card: 'bg-gray-200', // H:0 S:0% I:80%
            cardHover: 'hover:bg-gray-300', // H:0 S:0% I:70%
            button: 'bg-gray-300 text-gray-800 hover:bg-gray-400', // H:0 S:0% I:70%
            buttonPrimary: 'bg-gradient-to-r from-indigo-500 to-teal-600 hover:from-indigo-600 hover:to-teal-700', // from H:236 S:60% I:60% to H:174 S:62% I:62%
            subheading: 'text-indigo-600', // H:236 S:60% I:60%
            infoBox: 'bg-gray-200 border-gray-300', // H:0 S:0% I:80%
            input: 'file:text-indigo-700 file:bg-indigo-50 hover:file:bg-indigo-100', // H:236 S:60% I:60%
            nutrients: 'bg-gray-300 text-gray-800', // H:0 S:0% I:70%
            nutrientsSubtext: 'text-gray-600', // H:0 S:0% I:40%
            listText: 'text-gray-700', // H:0 S:0% I:30%
            navText: 'text-gray-600 hover:text-gray-900', // H:0 S:0% I:40%
            navActive: 'border-b-2 border-indigo-600 font-bold', // H:236 S:60% I:60%
            bgFilter: 'bg-white/70 backdrop-blur-sm', // Light overlay for background image
          };

    // Function to calculate BMR and TDEE based on form inputs
    const calculateHealthMetrics = () => {
        // Validate inputs
        if (isNaN(age) || isNaN(height) || isNaN(weight) || height <= 0 || weight <= 0) {
            setHealthMetrics({ error: 'Please enter valid numbers for age, height, and weight.' });
            setBmi(null);
            return;
        }

        // Calculate BMR using the Mifflin-St Jeor equation
        let bmr;
        if (gender === 'male') {
            bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
        } else {
            bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
        }
        
        // Calculate BMI
        const heightInMeters = height / 100;
        const calculatedBmi = weight / (heightInMeters * heightInMeters);
        setBmi(calculatedBmi.toFixed(1));

        // Define activity multipliers
        const activityMultipliers = {
            1: 1.2,    // Sedentary
            2: 1.375,  // Lightly active
            3: 1.55,   // Moderately active
            4: 1.725,  // Very active
            5: 1.9,    // Extra active
        };
        const activityMultiplier = activityMultipliers[activity] || 1.2;

        // Calculate TDEE (Total Daily Energy Expenditure)
        const maintainWeight = bmr * activityMultiplier;

        // Update the state with the calculated metrics (using a more standard -250, -500, -1000 deficit)
        setHealthMetrics({
            maintainWeight: maintainWeight.toFixed(0),
            mildWeightLoss: (maintainWeight - 250).toFixed(0), // Approx -0.25 kg/week
            weightLoss: (maintainWeight - 500).toFixed(0), // Approx -0.5 kg/week
            extremeWeightLoss: (maintainWeight - 1000).toFixed(0), // Approx -1.0 kg/week (aggressive)
        });

        // Update userProfile with the new metrics for display
        setUserProfile(prevProfile => ({
            ...prevProfile,
            weeklyGoal: `Your Basal Metabolic Rate (BMR) is ${bmr.toFixed(0)} kcal/day, and your Total Daily Energy Expenditure (TDEE) is ${maintainWeight.toFixed(0)} kcal/day. Use the calorie goals below as a starting guideline for your plan.`
        }));
    };

    // Render content based on active page
    const renderPageContent = () => {
        switch (activePage) {
            case 'Home':
                if (selectedRecipe) {
                    return (
                        <div className="animate-fade-in-up p-4">
                            <button
                                onClick={goBackToList}
                                className={`px-4 py-2 mb-6 text-sm font-semibold rounded-full shadow-md transition-colors duration-200 ${themeClasses.button} flex items-center`}
                                aria-label="Back to recipe list"
                            >
                                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path></svg>
                                Back to Recipes
                            </button>
                            <h2 className={`text-4xl font-extrabold text-center mb-6 ${themeClasses.subheading} transition-colors duration-500`}>
                                {selectedRecipe.title}
                            </h2>
                            <div className="grid md:grid-cols-2 gap-8">
                                {/* Recipe Image/Media Column */}
                                <div className="flex flex-col items-center">
                                    <div className="relative w-full max-w-lg">
                                        {imageLoading[selectedRecipe.id] && (
                                            <div className="absolute inset-0 flex items-center justify-center bg-gray-800/50 rounded-xl">
                                                <svg className="animate-spin h-10 w-10 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg>
                                            </div>
                                        )}
                                        <img
                                            src={selectedRecipe.image}
                                            alt={selectedRecipe.title}
                                            className={`w-full h-80 object-cover rounded-2xl shadow-xl transition-opacity duration-500 ${imageLoading[selectedRecipe.id] ? 'opacity-50' : 'opacity-100'}`}
                                            onLoad={() => setImageLoading(prev => ({ ...prev, [selectedRecipe.id]: false }))}
                                            onError={(e) => {
                                                e.target.src = `https://placehold.co/400x300/4c4c4c/ffffff?text=Image+Not+Found`;
                                                setImageLoading(prev => ({ ...prev, [selectedRecipe.id]: false }));
                                            }}
                                        />
                                    </div>
                                    <div className={`p-4 rounded-xl shadow-lg mt-6 w-full max-w-lg ${themeClasses.infoBox}`}>
                                        <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Nutrition (per serving):</h3>
                                        <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-center">
                                            {/* Nutrition boxes */}
                                            {Object.entries(selectedRecipe.nutrients).map(([key, value]) => (
                                                <div key={key} className={`p-3 rounded-xl shadow-inner ${themeClasses.nutrients}`}>
                                                    <span className="block text-xl font-bold">{value}</span>
                                                    <span className={`block text-xs ${themeClasses.nutrientsSubtext}`}>{key.charAt(0).toUpperCase() + key.slice(1)}</span>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                                
                                {/* Instructions Column */}
                                <div className="space-y-6">
                                    <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                        <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Ingredients:</h3>
                                        <ul className="list-disc list-inside space-y-2">
                                            {selectedRecipe.ingredients.map((ingredient, index) => (
                                                <li key={index} className={`pl-2 ${themeClasses.listText}`}>
                                                    {ingredient}
                                                </li>
                                            ))}
                                        </ul>
                                    </div>
                                    <div className={`p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                        <h3 className={`text-2xl font-bold mb-4 ${themeClasses.subheading}`}>Instructions:</h3>
                                        <div
                                            className={`leading-relaxed space-y-4 ${themeClasses.listText}`}
                                            dangerouslySetInnerHTML={{ __html: selectedRecipe.instructions || 'Instructions not available.' }}
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    );
                }
                return (
                    <div className="flex flex-col items-center">
                        <div className="w-full max-w-2xl text-center p-6 space-y-6 rounded-2xl shadow-lg" style={{ backgroundColor: isDarkMode ? '#2d3748' : '#e2e8f0' }}>
                            <h2 className={`text-2xl font-bold ${themeClasses.subheading}`}>Find Recipes from your Ingredients! 🍲</h2>
                            <div className="flex flex-col sm:flex-row items-center justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                                <input
                                    type="file"
                                    onChange={handleImageChange}
                                    accept="image/*"
                                    capture="environment"
                                    className={`block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold ${themeClasses.input} transition-colors duration-500`}
                                />
                                <button
                                    onClick={processImage}
                                    disabled={!image || status.includes('Processing...') || status.includes('Error.')}
                                    className={`w-full sm:w-auto px-8 py-3 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 disabled:opacity-50 disabled:cursor-not-allowed ${themeClasses.buttonPrimary} shadow-lg`}
                                >
                                    {status.includes('...') ? (
                                        <span className="flex items-center">
                                            <svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24">
                                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" />
                                            </svg>
                                            Processing...
                                        </span>
                                    ) : 'Find Recipes'}
                                </button>
                            </div>
                        </div>

                        {errorMessage && <p className="mt-4 text-sm font-semibold text-center text-red-400">{errorMessage}</p>}
                        {!errorMessage && (
                            <p className={`mt-4 text-sm font-semibold text-center ${themeClasses.listText}`}>
                                <strong>Status:</strong> {status}
                            </p>
                        )}
                        
                        {text && (
                            <div className={`mt-6 p-4 rounded-xl shadow-inner w-full max-w-2xl ${themeClasses.infoBox} transition-colors duration-500`}>
                                <h3 className={`text-lg font-semibold mb-2 ${themeClasses.subheading}`}>Extracted Ingredients (Raw):</h3>
                                <pre className={`whitespace-pre-wrap text-sm p-2 rounded ${themeClasses.listText} bg-gray-600/50 dark:bg-gray-800/50`}>
                                    {text || 'No text extracted.'}
                                </pre>
                            </div>
                        )}

                        {recipes.length > 0 && (
                            <div className="recipes-container mt-12 w-full max-w-6xl">
                                <h3 className={`text-3xl font-extrabold text-center mb-6 ${themeClasses.subheading}`}>
                                    Suggested Recipes Based on Ingredients 🍽️
                                </h3>
                                <div className="recipe-grid grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                                    {recipes.map((recipe) => (
                                        <div
                                            key={recipe.id}
                                            className={`p-4 rounded-3xl shadow-xl cursor-pointer transform hover:scale-[1.02] transition-all duration-300 ${themeClasses.card} ${themeClasses.cardHover}`}
                                            onClick={() => fetchRecipeDetails(recipe.id, recipe.title)}
                                            role="button"
                                            tabIndex={0}
                                            onKeyDown={(e) => e.key === 'Enter' && fetchRecipeDetails(recipe.id, recipe.title)}
                                            aria-label={`View details for ${recipe.title}`}
                                        >
                                            <div className="relative">
                                                {imageLoading[recipe.id] && (
                                                    <div className="absolute inset-0 flex items-center justify-center bg-gray-800/50 rounded-xl">
                                                        <svg className="animate-spin h-8 w-8 text-purple-400" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8h8a8 8 0 01-16 0z" /></svg>
                                                    </div>
                                                )}
                                                <img
                                                    src={recipe.image}
                                                    alt={recipe.title}
                                                    className={`w-full h-48 object-cover rounded-2xl mb-3 transition-opacity duration-500 ${imageLoading[recipe.id] ? 'opacity-50' : 'opacity-100'}`}
                                                    onLoad={() => setImageLoading(prev => ({ ...prev, [recipe.id]: false }))}
                                                    onError={(e) => {
                                                        e.target.src = `https://placehold.co/400x300/4c4c4c/ffffff?text=Image+Not+Found`;
                                                        setImageLoading(prev => ({ ...prev, [recipe.id]: false }));
                                                    }}
                                                />
                                            </div>
                                            <h4
                                                className={`text-xl font-extrabold text-center mt-2 ${
                                                    isDarkMode ? 'text-gray-100' : 'text-gray-800'
                                                }`}
                                            >
                                                {recipe.title}
                                            </h4>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                        {status.includes('Complete!') && recipes.length === 0 && (
                            <p className={`mt-4 text-center text-lg font-medium ${themeClasses.listText}`}>
                                🧐 No recipes found. Try a different image or include more common ingredients!
                            </p>
                        )}
                    </div>
                );
            case 'Daily Goal':
                // Removed placeholder and added Nutritional Chart to this page for better use case
                return (
                    <div className={`p-6 rounded-2xl shadow-lg w-full max-w-4xl mx-auto ${themeClasses.infoBox} transition-colors duration-500`}>
                        <NutritionalChart data={nutritionalData} colors={chartColors} isDarkMode={isDarkMode} />
                        <h3 className={`text-xl font-bold mt-8 text-center ${themeClasses.subheading}`}>
                            Nutrient Targets (Based on placeholder data):
                        </h3>
                        <p className={`text-center mt-2 ${themeClasses.listText}`}>
                            This chart shows the hardcoded breakdown of a sample meal. On a real day, this would track your progress towards your Daily Goals set in the Profile section.
                        </p>
                    </div>
                );
            case 'Profile':
                return (
                    <div className="flex flex-col items-center p-6 space-y-8 max-w-4xl mx-auto">
                        {/* Profile Info Section */}
                        <h2 className={`text-3xl font-bold ${themeClasses.subheading} transition-colors duration-500`}>My Health Profile</h2>
                        <div className={`flex flex-col sm:flex-row items-center space-y-4 sm:space-y-0 sm:space-x-6 p-6 rounded-2xl shadow-lg w-full ${themeClasses.infoBox} transition-colors duration-500`}>
                            <img
                                src="https://placehold.co/100x100/9F7AEA/ffffff?text=P" // Updated placeholder for a better aesthetic
                                alt="Profile"
                                className="w-24 h-24 rounded-full border-4 border-purple-400"
                            />
                            <div className="flex flex-col space-y-1 text-center sm:text-left">
                                <h3 className="text-2xl font-semibold">{userProfile.name}</h3>
                                <p className={`text-sm ${themeClasses.listText}`}>{userProfile.email}</p>
                                <p className={`text-sm ${themeClasses.listText}`}>User ID: {userProfile.userId}</p>
                            </div>
                        </div>

                        {/* Health Profile Form Section */}
                        <div className={`w-full mx-auto rounded-3xl shadow-2xl overflow-hidden p-8 space-y-8 ${themeClasses.infoBox} transition-colors duration-500`}>
                            <div className="text-center">
                                <h2 className={`text-2xl font-extrabold ${themeClasses.subheading}`}>Calorie Goal Calculator 🚀</h2>
                            </div>

                            {/* Form Grid */}
                            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {/* Age input section */}
                                <div className="flex flex-col space-y-2">
                                    <label htmlFor="age" className={`block font-medium ${themeClasses.text}`}>Age (years)</label>
                                    <div className="flex items-center space-x-2">
                                        <input type="number" id="age" value={age} onChange={(e) => setAge(parseInt(e.target.value) || 0)} min="1" className="w-full text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-800 dark:text-white focus:ring-purple-500 focus:border-purple-500 p-3 text-lg transition-colors duration-500"/>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setAge(prevAge => prevAge > 0 ? prevAge - 1 : 0)}>-</button>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setAge(prevAge => prevAge + 1)}>+</button>
                                    </div>
                                </div>

                                {/* Height input section */}
                                <div className="flex flex-col space-y-2">
                                    <label htmlFor="height" className={`block font-medium ${themeClasses.text}`}>Height (cm)</label>
                                    <div className="flex items-center space-x-2">
                                        <input type="number" id="height" value={height} onChange={(e) => setHeight(parseInt(e.target.value) || 0)} min="1" className="w-full text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-800 dark:text-white focus:ring-purple-500 focus:border-purple-500 p-3 text-lg transition-colors duration-500"/>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setHeight(prevHeight => prevHeight > 0 ? prevHeight - 1 : 0)}>-</button>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setHeight(prevHeight => prevHeight + 1)}>+</button>
                                    </div>
                                </div>

                                {/* Weight input section */}
                                <div className="flex flex-col space-y-2">
                                    <label htmlFor="weight" className={`block font-medium ${themeClasses.text}`}>Weight (kg)</label>
                                    <div className="flex items-center space-x-2">
                                        <input type="number" id="weight" value={weight} onChange={(e) => setWeight(parseInt(e.target.value) || 0)} min="1" className="w-full text-center rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-800 dark:text-white focus:ring-purple-500 focus:border-purple-500 p-3 text-lg transition-colors duration-500"/>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setWeight(prevWeight => prevWeight > 0 ? prevWeight - 1 : 0)}>-</button>
                                        <button className={`p-2 rounded-full hover:shadow-lg transition-colors duration-200 ${themeClasses.buttonPrimary}`} onClick={() => setWeight(prevWeight => prevWeight + 1)}>+</button>
                                    </div>
                                </div>

                                {/* Gender radio buttons section */}
                                <div className="flex flex-col space-y-2">
                                    <h3 className={`block font-medium ${themeClasses.text}`}>Gender</h3>
                                    <div className="flex items-center space-x-6">
                                        <label className="flex items-center space-x-2 cursor-pointer">
                                            <input type="radio" name="gender" value="male" checked={gender === 'male'} onChange={() => setGender('male')} className="form-radio h-4 w-4 text-purple-600 focus:ring-purple-500" />
                                            <span className={`${themeClasses.text}`}>Male</span>
                                        </label>
                                        <label className="flex items-center space-x-2 cursor-pointer">
                                            <input type="radio" name="gender" value="female" checked={gender === 'female'} onChange={() => setGender('female')} className="form-radio h-4 w-4 text-purple-600 focus:ring-purple-500"/>
                                            <span className={`${themeClasses.text}`}>Female</span>
                                        </label>
                                    </div>
                                </div>

                                {/* Meals per day input section */}
                                <div className="flex flex-col space-y-2">
                                    <label htmlFor="meals" className={`block font-medium ${themeClasses.text}`}>Meals per day</label>
                                    <div className="relative">
                                        <input type="range" id="meals" min="1" max="6" value={mealsPerDay} onChange={(e) => setMealsPerDay(parseInt(e.target.value))} className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer dark:bg-gray-700 transition-colors duration-500"/>
                                        <p className={`text-center mt-2 text-lg font-bold ${themeClasses.text}`}>{mealsPerDay} meals</p>
                                    </div>
                                </div>

                                {/* Weight loss plan dropdown section (Conceptual - not fully implemented in calc yet) */}
                                <div className="flex flex-col space-y-2">
                                    <label htmlFor="weight-plan" className={`block font-medium ${themeClasses.text}`}>Choose your goal:</label>
                                    <select id="weight-plan" className={`w-full rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-800 dark:text-white focus:ring-purple-500 focus:border-purple-500 p-3 text-lg ${themeClasses.text} transition-colors duration-500`}>
                                        <option>Maintain weight</option>
                                        <option>Mild weight loss</option>
                                        <option>Regular weight loss</option>
                                        <option>Aggressive weight loss</option>
                                    </select>
                                </div>
                            </div>
                            
                            {/* Activity slider section - Full width */}
                            <div className="pt-4 md:col-span-2 lg:col-span-3">
                                <h3 className={`block font-medium mb-3 ${themeClasses.text}`}>Activity Level</h3>
                                <div className="relative">
                                    <input type="range" id="activity" min="1" max="5" value={activity} onChange={(e) => setActivity(parseInt(e.target.value))} className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer dark:bg-gray-700 transition-colors duration-500"/>
                                    <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-2">
                                        <span>Sedentary (1)</span>
                                        <span>Extra Active (5)</span>
                                    </div>
                                    <p className={`text-center mt-4 text-lg font-bold ${themeClasses.text}`}>Current: {getActivityLabel(activity)}</p>
                                </div>
                            </div>

                            {/* Generate button section */}
                            <div className="flex justify-center pt-6">
                                <button onClick={calculateHealthMetrics} className={`w-full sm:w-auto px-10 py-3 rounded-full text-white font-bold transition-transform transform hover:scale-105 active:scale-95 duration-200 ${themeClasses.buttonPrimary} shadow-lg text-lg`}>
                                    Calculate My Goals
                                </button>
                            </div>

                            {/* Results display area */}
                            {healthMetrics && (
                                <div className="pt-6 text-center text-gray-800 dark:text-white space-y-8 transition-colors duration-500">
                                    {healthMetrics.error ? (
                                        <p className="text-red-400 font-medium text-xl">{healthMetrics.error}</p>
                                    ) : (
                                        <>
                                            {/* BMI Section */}
                                            <div className="bg-gray-200 dark:bg-gray-800 rounded-xl p-6 shadow-lg transition-colors duration-500">
                                                <h3 className={`text-xl font-extrabold mb-2 ${themeClasses.subheading}`}>BMI CALCULATOR (Body Mass Index)</h3>
                                                <p className="text-5xl font-extrabold mt-2">{bmi} <span className="text-lg font-normal">kg/m²</span></p>
                                                <p className={`text-2xl font-bold mt-2 ${bmi < 18.5 ? 'text-yellow-500' : bmi >= 30 ? 'text-red-500' : bmi >= 25 ? 'text-orange-500' : 'text-green-500'}`}>{getBMICategory(parseFloat(bmi))}</p>
                                                <p className="text-sm text-gray-500 dark:text-gray-300 mt-2">Healthy BMI range: 18.5 kg/m² - 24.9 kg/m²</p>
                                            </div>

                                            {/* Calorie Calculator Section */}
                                            <div className="bg-gray-200 dark:bg-gray-800 rounded-xl p-6 shadow-lg transition-colors duration-500">
                                                <h3 className={`text-xl font-extrabold mb-4 ${themeClasses.subheading}`}>DAILY CALORIE GOALS 📊</h3>
                                                <p className="text-sm text-gray-600 dark:text-gray-300 mb-6">
                                                    Your daily caloric needs (TDEE) are **{healthMetrics.maintainWeight} kcal**. Here are your goal estimates:
                                                </p>
                                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
                                                    {[
                                                        { label: 'MAINTAIN', calories: healthMetrics.maintainWeight, rate: '0 kg/week', color: 'text-green-500' },
                                                        { label: 'MILD LOSS', calories: healthMetrics.mildWeightLoss, rate: '↓ 0.25 kg/week', color: 'text-green-400' },
                                                        { label: 'REGULAR LOSS', calories: healthMetrics.weightLoss, rate: '↓ 0.5 kg/week', color: 'text-yellow-400' },
                                                        { label: 'AGGRESSIVE LOSS', calories: healthMetrics.extremeWeightLoss, rate: '↓ 1.0 kg/week', color: 'text-red-400' },
                                                    ].map((goal) => (
                                                        <div key={goal.label} className="p-3 rounded-lg bg-white dark:bg-gray-700 shadow-md">
                                                            <p className="font-semibold text-sm text-gray-700 dark:text-gray-300">{goal.label}</p>
                                                            <p className="text-3xl font-extrabold mt-1">{goal.calories}</p>
                                                            <p className="text-xs font-medium mt-1 text-gray-500 dark:text-gray-400">CALORIES</p>
                                                            <p className={`text-xs mt-1 ${goal.color}`}>{goal.rate}</p>
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>

                                            {/* Weekly Goal Text Box */}
                                            <div className={`w-full p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                                                <h3 className={`text-xl font-bold mb-4 ${themeClasses.subheading}`}>Analysis Summary</h3>
                                                <p className={`text-sm leading-relaxed ${themeClasses.listText}`}>
                                                    {userProfile.weeklyGoal}
                                                </p>
                                            </div>
                                        </>
                                    )}
                                </div>
                            )}
                        </div>
                    </div>
                );
            case 'Weekend Summary':
                return (
                    <div className="text-center p-6">
                        <h2 className={`text-3xl font-bold ${themeClasses.subheading} transition-colors duration-500`}>Weekend Summary</h2>
                        <p className={`mt-4 ${themeClasses.listText}`}>This section will provide a summary of your nutritional intake and progress for the week, along with suggestions for the next week's meal planning.</p>
                        <div className={`mt-8 p-6 rounded-2xl shadow-lg ${themeClasses.infoBox}`}>
                            <p className={`text-xl font-semibold ${themeClasses.subheading}`}>Weekly Goal Status: N/A</p>
                            <p className={`mt-2 ${themeClasses.listText}`}>Your weekly summary is ready to be generated!</p>
                        </div>
                    </div>
                );
            default:
                return <div className="text-center">Page not found.</div>;
        }
    };

    return (
        // Main container with background image and transition
        <div
            className={`min-h-screen font-sans flex flex-col items-center p-4 sm:p-8 transition-all duration-500 ease-in-out`}
            style={{
                backgroundImage: 'url("https://images.unsplash.com/photo-1543353071-10c84333b207?q=80&w=2670&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D")',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundAttachment: 'fixed'
            }}
        >
            <div className={`absolute inset-0 z-0 ${themeClasses.bgFilter} transition-colors duration-500`}></div> {/* Dark/Light Overlay */}
            
            <header className={`w-full max-w-7xl flex justify-between items-center mb-10 z-10 transition-colors duration-500 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                <div className="flex items-center">
                    <svg
                        className="w-8 h-8 mr-2 text-purple-400"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"
                            d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V7m-18 0l9 5 9-5"
                        />
                    </svg>
                    <span className="text-2xl font-extrabold tracking-wider">PlanMyPlate</span>
                </div>
                
                {/* Navigation - Centered on desktop */}
                <nav className="hidden md:flex space-x-6 text-lg">
                    {['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => {
                        return (
                            <button
                                key={page}
                                onClick={() => handleNavClick(page)}
                                className={`px-2 py-1 transition-all duration-300 ${themeClasses.navText} ${
                                    activePage === page ? themeClasses.navActive : ''
                                } hover:scale-105`}
                                aria-current={activePage === page ? 'page' : undefined}
                                aria-label={`Maps to ${page}`}
                            >
                                {page}
                            </button>
                        );
                    })}
                </nav>
                
                {/* Utility Buttons */}
                <div className="flex space-x-4">
                    <button 
                        onClick={toggleDarkMode}
                        className={`p-3 rounded-full font-bold transition-all duration-500 hover:scale-110 ${themeClasses.buttonPrimary} shadow-md text-white`}
                        aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
                    >
                        {isDarkMode ? '🌞' : '🌙'}
                    </button>
                    <button className={`${themeClasses.button} p-3 rounded-full hidden sm:block transition-colors duration-500`} aria-label="Search">
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg>
                    </button>
                    <button className={`${themeClasses.button} p-3 rounded-full hidden sm:block transition-colors duration-500`} aria-label="Menu">
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" /></svg>
                    </button>
                </div>
            </header>

            <main
                className={`w-full max-w-7xl min-h-[70vh] p-8 rounded-3xl z-10 border-t-4 border-b-4 border-purple-500 ${themeClasses.container} transition-all duration-500 ease-in-out`}
            >
                {renderPageContent()}
            </main>
            
            {/* Floating Navigation for Mobile (Optional, but good practice for great UX) */}
            <nav className="fixed bottom-0 left-0 w-full md:hidden z-20 bg-gray-800/95 backdrop-blur-md shadow-2xl rounded-t-xl p-2">
                <div className="flex justify-around">
                    {['Home', 'Daily Goal', 'Profile', 'Weekend Summary'].map((page) => {
                        return (
                            <button
                                key={`mobile-${page}`}
                                onClick={() => handleNavClick(page)}
                                className={`flex flex-col items-center p-2 text-xs transition-all duration-300 ${activePage === page ? 'text-purple-400 font-bold' : 'text-gray-400 hover:text-white'}`}
                            >
                                {page === 'Home' && <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12l9-9 9 9M5 10v10a1 1 0 001 1h3m10-10v10a1 1 0 001 1h3M14 20h-4" /></svg>}
                                {page === 'Daily Goal' && <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19V6l12-3v13M12 19V6l12-3v13M12 19l-7 2M12 6l-7 2M21 3l-7 2m7-2L9 3" /></svg>}
                                {page === 'Profile' && <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg>}
                                {page === 'Weekend Summary' && <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg>}
                                <span>{page}</span>
                            </button>
                        );
                    })}
                </div>
            </nav>
        </div>
    );
};

export default App;
