import React, { useState, useRef } from 'react';
import axios from 'axios';
import Webcam from 'react-webcam';

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const webcamRef = useRef(null);
  const [useCamera, setUseCamera] = useState(false);

  const videoConstraints = {
    width: 480,
    height: 360,
    facingMode: "user"
  };

  const capture = () => {
    const screenshot = webcamRef.current.getScreenshot();
    fetch(screenshot)
      .then(res => res.blob())
      .then(blob => setImage(new File([blob], "webcam-image.jpg", { type: "image/jpeg" })));
  };

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
    setUseCamera(false); // switch off camera if user uploads
  };

  const handleUpload = async () => {
    if (!image) {
      alert("Please select or capture an image.");
      return;
    }

    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await axios.post('http://localhost:5000/extract', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setText(response.data.text);
    } catch (error) {
      console.error('Error:', error);
      alert("Failed to extract text.");
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>OCR Image Upload with Camera</h2>

      <div style={{ marginBottom: '1rem' }}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button onClick={() => setUseCamera(!useCamera)} style={{ marginLeft: '1rem' }}>
          {useCamera ? 'Hide Camera' : 'Use Camera'}
        </button>
      </div>

      {useCamera && (
        <div>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={480}
            videoConstraints={videoConstraints}
          />
          <div>
            <button onClick={capture} style={{ marginTop: '1rem' }}>Capture Image</button>
          </div>
        </div>
      )}

      <button onClick={handleUpload} style={{ marginTop: '1rem' }}>Upload and Extract Text</button>

      <pre style={{ marginTop: '2rem', background: '#f0f0f0', padding: '1rem' }}>{text}</pre>
    </div>
  );
}

export default App;
